import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Auth.css';

const ForgotPassword = () => {
  const navigate = useNavigate();
  const { sendPasswordResetCode, resetPassword } = useAuth();
  
  // Estados
  const [step, setStep] = useState(1); // 1 = solicitar c√≥digo, 2 = resetear contrase√±a
  const [email, setEmail] = useState('');
  const [code, setCode] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // ‚úÖ PASO 1: Solicitar c√≥digo
  const handleRequestCode = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (!email.trim()) {
      setError('Por favor ingresa tu email');
      return;
    }

    setLoading(true);

    try {
      await sendPasswordResetCode(email);
      setSuccess('C√≥digo enviado a tu email. Revisa tu bandeja de entrada.');
      
      // ‚úÖ PASAR AUTOM√ÅTICAMENTE AL PASO 2 despu√©s de 2 segundos
      setTimeout(() => {
        setStep(2);
        setSuccess('');
      }, 2000);
      
    } catch (err) {
      setError(err.response?.data?.message || 'Error al enviar c√≥digo');
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ PASO 2: Resetear contrase√±a
  const handleResetPassword = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validaciones
    if (!code.trim()) {
      setError('Por favor ingresa el c√≥digo de verificaci√≥n');
      return;
    }

    if (!newPassword) {
      setError('Por favor ingresa una nueva contrase√±a');
      return;
    }

    if (newPassword.length < 6) {
      setError('La contrase√±a debe tener al menos 6 caracteres');
      return;
    }

    if (newPassword !== confirmPassword) {
      setError('Las contrase√±as no coinciden');
      return;
    }

    setLoading(true);

    try {
      await resetPassword(email, code, newPassword);
      setSuccess('¬°Contrase√±a actualizada correctamente!');
      
      // Redirigir al login despu√©s de 2 segundos
      setTimeout(() => {
        navigate('/login');
      }, 2000);
      
    } catch (err) {
      setError(err.response?.data?.message || 'Error al resetear contrase√±a');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h2>üîí Recuperar Contrase√±a</h2>
          <p>
            {step === 1 
              ? 'Ingresa tu email para recibir un c√≥digo de verificaci√≥n' 
              : 'Ingresa el c√≥digo que recibiste y tu nueva contrase√±a'}
          </p>
        </div>

        {/* INDICADOR DE PASOS */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          gap: '10px', 
          marginBottom: '20px' 
        }}>
          <div style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            background: step >= 1 ? '#667eea' : '#e0e0e0',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>
            1
          </div>
          <div style={{
            width: '40px',
            height: '2px',
            background: '#e0e0e0',
            alignSelf: 'center'
          }} />
          <div style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            background: step >= 2 ? '#667eea' : '#e0e0e0',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>
            2
          </div>
        </div>

        {error && (
          <div className="alert alert-error">
            ‚ö†Ô∏è {error}
          </div>
        )}

        {success && (
          <div className="alert alert-success">
            ‚úÖ {success}
          </div>
        )}

        {/* ‚úÖ PASO 1: SOLICITAR C√ìDIGO */}
        {step === 1 && (
          <form onSubmit={handleRequestCode} className="auth-form">
            <div className="form-group">
              <label>üìß Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="tu@email.com"
                disabled={loading}
                required
              />
            </div>

            <button type="submit" className="auth-button" disabled={loading}>
              {loading ? 'Enviando...' : 'Enviar C√≥digo'}
            </button>

            <div className="auth-footer">
              <button 
                type="button"
                onClick={() => navigate('/login')}
                className="link-button"
              >
                ‚Üê Volver al login
              </button>
            </div>
          </form>
        )}

        {/* ‚úÖ PASO 2: RESETEAR CONTRASE√ëA */}
        {step === 2 && (
          <form onSubmit={handleResetPassword} className="auth-form">
            <div className="form-group">
              <label>üìß Email</label>
              <input
                type="email"
                value={email}
                disabled
                style={{ background: '#f5f5f5', cursor: 'not-allowed' }}
              />
            </div>

            <div className="form-group">
              <label>üî¢ C√≥digo de Verificaci√≥n</label>
              <input
                type="text"
                value={code}
                onChange={(e) => setCode(e.target.value)}
                placeholder="123456"
                maxLength="6"
                disabled={loading}
                required
                style={{ 
                  fontSize: '20px', 
                  letterSpacing: '5px', 
                  textAlign: 'center' 
                }}
              />
            </div>

            <div className="form-group">
              <label>üîí Nueva Contrase√±a</label>
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                placeholder="M√≠nimo 6 caracteres"
                disabled={loading}
                required
              />
            </div>

            <div className="form-group">
              <label>üîí Confirmar Contrase√±a</label>
              <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                placeholder="Repite tu contrase√±a"
                disabled={loading}
                required
              />
            </div>

            <button type="submit" className="auth-button" disabled={loading}>
              {loading ? 'Actualizando...' : 'Actualizar Contrase√±a'}
            </button>

            <div className="auth-footer">
              <button 
                type="button"
                onClick={() => setStep(1)}
                className="link-button"
                disabled={loading}
              >
                ‚Üê Volver a solicitar c√≥digo
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default ForgotPassword;
