import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import ChatWidget from './components/ChatWidget';
import AdminRoute from './components/AdminRoute';
import AdminLayout from './components/admin/AdminLayout';

// Páginas públicas
import Home from './pages/Home';
import Products from './pages/Products';
import ProductDetail from './pages/ProductDetail';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout';
import Login from './pages/Login';
import Register from './pages/Register';
import Profile from './pages/Profile';

// Páginas de administración
import AdminDashboard from './pages/admin/AdminDashboard';
import AdminProducts from './pages/admin/AdminProducts';
import AdminCategories from './pages/admin/AdminCategories';
import AdminOrders from './pages/admin/AdminOrders';
import AdminUsers from './pages/admin/AdminUsers';
import AdminChat from './components/admin/AdminChat';
import ProfileTab from './components/profile/ProfileTab';
import OrdersTab from './components/profile/OrdersTab';
import NotificationsTab from './components/profile/NotificationsTab';
import SecurityTab from './components/profile/SecurityTab';

import './styles/App.css';
import CustomerChat from './components/CustomerChat';

function App() {
  return (
    <div className="App">
      <Routes>
        {/* Rutas públicas */}
        <Route path="/*" element={
          <>
            <Header />
            <main className="main-content">
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/productos" element={<Products />} />
                <Route path="/productos/:slug" element={<ProductDetail />} />
                <Route path="/categoria/:categorySlug" element={<Products />} />
                <Route path="/carrito" element={<Cart />} />
                <Route path="/checkout" element={<Checkout />} />
                <Route path="/login" element={<Login />} />
                <Route path="/registro" element={<Register />} />
                <Route path="/perfil/*" element={<Profile />} />
                <Route path="/profile" element={<ProfileTab />} />
                <Route path="/orders" element={<OrdersTab />} />
                <Route path="/notifications" element={<NotificationsTab />} />
                <Route path="/security" element={<SecurityTab />} />

                {/* ❌ QUITA AdminChat de aquí, no pertenece a la tienda pública */}
              </Routes>
            </main>
            <Footer />
            {/* El ChatWidget o CustomerChat va aquí para que el cliente lo use */}
            <ChatWidget /> 
          </>
        } />

        {/* Rutas de administración */}
        <Route path="/admin/*" element={
          <AdminRoute>
            <AdminLayout />
          </AdminRoute>
        }>
          <Route index element={<AdminDashboard />} />
          <Route path="productos" element={<AdminProducts />} />
          <Route path="categorias" element={<AdminCategories />} />
          <Route path="pedidos" element={<AdminOrders />} />
          <Route path="usuarios" element={<AdminUsers />} />

          {/* ✅ AQUÍ ES DONDE DEBE IR EL ADMIN CHAT */}
          {/* Fíjate que la ruta es 'chat' y el componente es 'AdminChat' */}
          <Route path="chat" element={<AdminChat />} /> 
        </Route>
      </Routes>
    </div>
  );
}

export default App;
