import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaEdit } from 'react-icons/fa';
import '../../styles/Profile.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const ProfileTab = ({ user, login }) => {
  const [editing, setEditing] = useState(false);
  
  // Añadimos email al estado inicial
  const [formData, setFormData] = useState({
    name: user?.name || '',
    email: user?.email || '', // <--- Campo añadido
    phone: user?.phone || '',
    street: user?.address?.street || '',
    city: user?.address?.city || '',
    postalCode: user?.address?.postalCode || '',
    country: user?.address?.country || 'España'
  });

  // Efecto para actualizar el formulario si los datos del usuario cambian externamente
  useEffect(() => {
    if (user) {
      setFormData({
        name: user.name || '',
        email: user.email || '',
        phone: user.phone || '',
        street: user.address?.street || '',
        city: user.address?.city || '',
        postalCode: user.address?.postalCode || '',
        country: user.address?.country || 'España'
      });
    }
  }, [user]);

  const handleSave = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`${API_URL}/auth/profile`, {
        name: formData.name, 
        phone: formData.phone,
        address: { 
          street: formData.street, 
          city: formData.city, 
          postalCode: formData.postalCode, 
          country: formData.country 
        }
      }, { headers: { Authorization: `Bearer ${token}` } });

      login({ ...user, ...response.data }, token);
      setEditing(false);
      alert('Perfil actualizado con éxito');
    } catch (err) { 
      alert('Error al guardar los cambios'); 
    }
  };

  return (
    <div className="profile-tab">
      <div className="tab-header">
        <h2>Mis Datos</h2>
        {!editing ? (
          <button className="btn-edit" onClick={() => setEditing(true)}>
            <FaEdit /> Editar Perfil
          </button>
        ) : (
          <div className="edit-actions">
            <button className="btn-save" onClick={handleSave}>Guardar</button>
            <button className="btn-cancel" onClick={() => setEditing(false)}>Cancelar</button>
          </div>
        )}
      </div>

      <div className="profile-form">
        <div className="form-grid">
          {/* Campo Email - Generalmente deshabilitado siempre */}
          <div className="form-group">
            <label>Email (No editable)</label>
            <input 
              name="email" 
              value={formData.email} 
              disabled 
              placeholder="Correo electrónico" 
              className="input-disabled"
            />
          </div>

          <div className="form-group">
            <label>Nombre</label>
            <input 
              name="name" 
              value={formData.name} 
              onChange={e => setFormData({...formData, name: e.target.value})} 
              disabled={!editing} 
              placeholder="Tu nombre completo" 
            />
          </div>

          <div className="form-group">
            <label>Teléfono</label>
            <input 
              name="phone" 
              value={formData.phone} 
              onChange={e => setFormData({...formData, phone: e.target.value})} 
              disabled={!editing} 
              placeholder="Tu número de teléfono" 
            />
          </div>
        </div>

        {/* Ejemplo de cómo podrías añadir la sección de dirección si la necesitas */}
        {editing && (
          <div className="address-section">
            <h3>Dirección</h3>
            <input 
              placeholder="Calle" 
              value={formData.street} 
              onChange={e => setFormData({...formData, street: e.target.value})} 
            />
            {/* Otros campos de dirección... */}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfileTab;
