import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // --- 1. PERSISTENCIA DE SESIÓN ---
  // Al cargar la app, revisamos si hay un usuario guardado en el navegador
  useEffect(() => {
    const checkAuth = () => {
      try {
        const savedUser = localStorage.getItem('user');
        const token = localStorage.getItem('token');

        if (savedUser && token) {
          setUser(JSON.parse(savedUser));
        }
      } catch (err) {
        console.error("Error cargando sesión:", err);
        localStorage.removeItem('user');
        localStorage.removeItem('token');
      } finally {
        setLoading(false);
      }
    };
    checkAuth();
  }, []);

  // --- 2. FUNCIONES DE AUTENTICACIÓN ---

  // Login normal
  const login = async (email, password) => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        return { success: true, user: data.user };
      } else {
        return { success: false, message: data.message || 'Credenciales inválidas' };
      }
    } catch (error) {
      return { success: false, message: 'Error de conexión con el servidor' };
    }
  };

  // Logout
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  // Helper para verificar si está logueado
  const isAuthenticated = useCallback(() => {
    return !!user;
  }, [user]);


  // --- 3. FLUJO DE RECUPERACIÓN (OLVIDÉ CONTRASEÑA) ---

  // Paso 1: Enviar el email al backend para que genere y mande el código
  const sendPasswordResetCode = async (email) => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/forgot-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      const data = await response.json();
      return { success: response.ok, message: data.message };
    } catch (error) {
      console.error("Error enviando código:", error);
      return { success: false, message: 'Error de red al solicitar el código' };
    }
  };

  // Paso 2: Enviar Email + Código + Nueva Contraseña al backend
  const resetPassword = async (email, code, newPassword) => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/reset-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, code, newPassword }),
      });
      const data = await response.json();
      return { success: response.ok, message: data.message };
    } catch (error) {
      console.error("Error reseteando contraseña:", error);
      return { success: false, message: 'Error de red al cambiar la contraseña' };
    }
  };

  // --- 4. EXPOSICIÓN DEL CONTEXTO ---
  const value = {
    user,
    loading,
    login,
    logout,
    isAuthenticated,
    sendPasswordResetCode,
    resetPassword
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

// Hook personalizado para usar el contexto fácilmente
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe usarse dentro de un AuthProvider');
  }
  return context;
};
