import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FaUser, FaUserShield, FaTimes, FaPaperPlane, FaComment } from 'react-icons/fa';
import '../styles/CustomerChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const CustomerChat = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [chat, setChat] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const messagesEndRef = useRef(null);

useEffect(() => {
  const token = localStorage.getItem('token');
  if (!token) return;

  loadChat();

  const interval = setInterval(() => {
    if (isOpen) loadChat();
    else checkUnreadMessages();
  }, 3000);

  return () => clearInterval(interval);
}, [isOpen]);

  useEffect(() => {
    if (isOpen && chat) {
      scrollToBottom();
      markAsRead();
    }
  }, [chat, isOpen]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadChat = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await axios.get(`${API_URL}/chat/my-chat`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setChat(response.data);

      const unread = response.data.messages.filter(
        m => m.sender === 'admin' && !m.read
      ).length;
      setUnreadCount(unread);

    } catch (error) {
      console.error('Error al cargar chat:', error);
    }
  };

  const checkUnreadMessages = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await axios.get(`${API_URL}/chat/my-chat`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const unread = response.data.messages.filter(
        m => m.sender === 'admin' && !m.read
      ).length;
      setUnreadCount(unread);

    } catch (error) {
      console.error('Error al verificar mensajes:', error);
    }
  };

  const markAsRead = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      await axios.put(
        `${API_URL}/chat/mark-read`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setUnreadCount(0);
    } catch (error) {
      console.error('Error al marcar como le√≠do:', error);
    }
  };

  const sendMessage = async () => {
    if (!message.trim()) return;

    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const response = await axios.post(
        `${API_URL}/chat/send-message`,
        { text: message },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setChat(response.data);
      setMessage('');
      scrollToBottom();

    } catch (error) {
      alert('Error al enviar mensaje');
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ FUNCI√ìN PARA FINALIZAR CHAT DESDE CLIENTE
  const closeChat = async () => {
    if (!window.confirm('¬øDeseas finalizar esta conversaci√≥n? No podr√°s enviar m√°s mensajes.')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      
      // Llamar a la ruta de cerrar chat desde cliente
      await axios.put(
        `${API_URL}/chat/close-my-chat`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert('‚úÖ Conversaci√≥n finalizada. ¬°Gracias por contactarnos!');
      setIsOpen(false);
      setChat(null);
      
    } catch (error) {
      console.error('Error al finalizar chat:', error);
      alert('Error al finalizar la conversaci√≥n');
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('es-ES', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const toggleChat = () => {
    setIsOpen(!isOpen);
    if (!isOpen) {
      markAsRead();
    }
  };

  return (
    <>
      {/* Bot√≥n flotante */}
      <button 
        className={`chat-toggle-btn ${isOpen ? 'active' : ''}`}
        onClick={toggleChat}
        title="Chat de soporte"
      >
        {isOpen ? (
          <FaTimes />
        ) : (
          <>
            <FaComment />
            {unreadCount > 0 && (
              <span className="unread-badge">{unreadCount}</span>
            )}
          </>
        )}
      </button>

      {/* Ventana de chat */}
      {isOpen && (
        <div className="customer-chat-window">
          {/* Header */}
          <div className="chat-window-header">
            <div className="header-info">
              <div className="support-avatar">
                <FaUserShield />
              </div>
              <div>
                <h3>Soporte al Cliente</h3>
                <span className="status-indicator">‚óè En l√≠nea</span>
              </div>
            </div>
            <div className="header-actions">
              {/* ‚úÖ BOT√ìN FINALIZAR CHAT */}
              {chat && chat.messages.length > 1 && (
                <button 
                  className="btn-close-conversation" 
                  onClick={closeChat}
                  title="Finalizar conversaci√≥n"
                >
                  Finalizar
                </button>
              )}
              <button className="btn-close" onClick={() => setIsOpen(false)}>
                <FaTimes />
              </button>
            </div>
          </div>

          {/* Mensajes */}
          <div className="chat-window-messages">
            {chat?.messages.map((msg, index) => {
              const isAdmin = msg.sender === 'admin';
              const isUser = msg.sender === 'user';

              return (
                <div 
                  key={index} 
                  className={`chat-message-wrapper ${isUser ? 'user-msg' : 'admin-msg'}`}
                >
                  <div className="msg-avatar">
                    {isAdmin ? (
                      <div className="avatar support">
                        <FaUserShield />
                      </div>
                    ) : (
                      <div className="avatar user">
                        <FaUser />
                      </div>
                    )}
                  </div>

                  <div className="msg-content">
                    <div className="msg-sender">
                      {isAdmin ? 'üõ°Ô∏è Soporte' : 'üë§ T√∫'}
                    </div>
                    <div className={`msg-bubble ${isUser ? 'user' : 'admin'}`}>
                      <p>{msg.text}</p>
                      <span className="msg-time">{formatTime(msg.timestamp)}</span>
                    </div>
                  </div>
                </div>
              );
            })}
            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <div className="chat-window-input">
            <input
              type="text"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
              placeholder="Escribe tu mensaje..."
              disabled={loading}
            />
            <button 
              onClick={sendMessage}
              disabled={loading || !message.trim()}
              className="btn-send"
            >
              <FaPaperPlane />
            </button>
          </div>

          {/* Footer info */}
          <div className="chat-window-footer">
            <small>üí° Respondemos en pocos minutos</small>
          </div>
        </div>
      )}

      {/* Tooltip de ayuda */}
      {!isOpen && unreadCount === 0 && (
        <div className="chat-tooltip">
          ¬øNecesitas ayuda? üí¨
        </div>
      )}
    </>
  );
};

export default CustomerChat;
