import React, { useState, useEffect, useRef, useCallback } from 'react';
import { FaEye, FaEdit, FaPhone, FaFilePdf, FaDownload, FaSync } from 'react-icons/fa';
import axios from 'axios';
import '../../styles/AdminOrders.css';
import { toast } from 'react-toastify';

// 1. Configuraci√≥n de API centralizada
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const api = axios.create({ baseURL: API_URL });

// Interceptor para manejar el token autom√°ticamente en cada petici√≥n
api.interceptors.request.use(config => {
  const token = sessionStorage.getItem('token');
  if (token) config.headers.Authorization = `Bearer ${token}`;
  return config;
});

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [statusUpdate, setStatusUpdate] = useState({ orderStatus: '', paymentStatus: '' });
  const [error, setError] = useState(null);

  const notifiedOrders = useRef(new Set());
  const isFirstLoad = useRef(true);

  // --- L√≥gica de Notificaciones ---
  const handleNewOrderNotification = useCallback((newOrders) => {
    const audio = new Audio('https://assets.mixkit.co/active_storage/sfx/2869/2869-preview.mp3');
    audio.play().catch(() => console.log('El navegador bloque√≥ el audio hasta que haya interacci√≥n'));

    newOrders.forEach(order => {
      toast.success(
        `üîî ¬°NUEVO PEDIDO! #${order._id.slice(-6).toUpperCase()}\n` +
        `Cliente: ${order.user?.name || 'Desconocido'}`,
        { position: "top-right", autoClose: 8000 }
      );
    });

    if (navigator.vibrate) navigator.vibrate([200, 100, 200]);

    if (Notification.permission === "granted") {
      new Notification("üõí Nuevo pedido recibido", {
        body: `${newOrders.length} pedido(s) nuevo(s).`,
        icon: "/logo192.png"
      });
    }
  }, []);

  // --- Carga de Pedidos ---
  const loadOrders = async (initial = false) => {
    try {
      if (initial) setLoading(true);
      setRefreshing(true);

      const response = await api.get('/orders');
      const ordersData = Array.isArray(response.data) ? response.data : [];

      // Detecci√≥n de nuevos pedidos (solo si no es la primera carga)
      if (!initial && !isFirstLoad.current) {
        const newOrders = ordersData.filter(o => 
          !notifiedOrders.current.has(o._id) && o.orderStatus === 'pendiente'
        );
        if (newOrders.length > 0) {
          handleNewOrderNotification(newOrders);
          newOrders.forEach(o => notifiedOrders.current.add(o._id));
        }
      }

      // En la primera carga, registramos los IDs existentes para no notificarlos
      if (initial) {
        ordersData.forEach(o => notifiedOrders.current.add(o._id));
        isFirstLoad.current = false;
      }

      setOrders(ordersData);
      setError(null);
    } catch (err) {
      console.error('‚ùå Error:', err);
      if (initial) setError(err.response?.data?.message || 'Error al cargar los pedidos');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  useEffect(() => {
    loadOrders(true);
    if (Notification.permission === "default") Notification.requestPermission();

    const interval = setInterval(() => loadOrders(false), 30000);
    return () => clearInterval(interval);
  }, []);

  // --- Gesti√≥n de Etiquetas (PDF) ---
  const fetchLabelBlob = async (trackingNumber) => {
    const response = await api.get(`/tracking/${trackingNumber}/label`, {
      responseType: 'blob'
    });
    return new Blob([response.data], { type: 'application/pdf' });
  };

  const downloadLabel = async (order) => {
    const trackingNumber = order.tracking?.trackingNumber;
    if (!trackingNumber) return toast.error('Este pedido no tiene tracking asignado');

    const loadingToast = toast.loading('Descargando etiqueta...');
    try {
      const blob = await fetchLabelBlob(trackingNumber);
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `etiqueta-${trackingNumber}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      toast.update(loadingToast, { render: '‚úÖ Descargado', type: 'success', isLoading: false, autoClose: 2000 });
    } catch (err) {
      toast.update(loadingToast, { render: '‚ùå Error al descargar', type: 'error', isLoading: false, autoClose: 3000 });
    }
  };

  const viewLabel = async (order) => {
    const trackingNumber = order.tracking?.trackingNumber;
    if (!trackingNumber) return toast.error('No hay tracking asignado');

    try {
      const blob = await fetchLabelBlob(trackingNumber);
      const url = window.URL.createObjectURL(blob);
      window.open(url, '_blank');
      // No revocamos el URL inmediatamente para que la pesta√±a pueda cargarlo
    } catch (err) {
      toast.error('Error al abrir la vista previa');
    }
  };

  // --- Actualizaci√≥n de Estados ---
  const updateOrderStatus = async () => {
    try {
      await api.put(`/orders/${selectedOrder._id}/status`, statusUpdate);
      toast.success('‚úÖ Estado actualizado y cliente notificado');
      setShowDetailModal(false);
      loadOrders(false);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error al actualizar');
    }
  };

  const handleCreateTracking = async (orderId) => {
    const loadingToast = toast.loading("‚è≥ Generando etiqueta...");
    try {
      await api.post(`/tracking/create`, { orderId });
      toast.update(loadingToast, { render: "‚úÖ Etiqueta generada", type: "success", isLoading: false, autoClose: 3000 });
      loadOrders(false);
    } catch (error) {
      toast.update(loadingToast, { render: "‚ùå Error al generar", type: "error", isLoading: false, autoClose: 3000 });
    }
  };

  // --- Helpers Visuales ---
  const formatPrice = (price) => new Intl.NumberFormat('es-ES', { style: 'currency', currency: 'EUR' }).format(price);

  const getStatusBadge = (status) => {
    const statusMap = {
      pendiente: 'badge-warning', procesando: 'badge-info', enviado: 'badge-primary',
      entregado: 'badge-success', cancelado: 'badge-danger', pagado: 'badge-success'
    };
    return statusMap[status] || 'badge-secondary';
  };

  if (loading) return <div className="admin-orders"><div className="loading-container"><div className="spinner-large"></div><p>Cargando pedidos...</p></div></div>;

  return (
    <div className="admin-orders">
      <div className="page-header">
        <h2>Gesti√≥n de Pedidos {refreshing && <small className="refresh-label">üîÑ Actualizando...</small>}</h2>
        <div className="orders-stats">
          <span className="stat-item">Total: <strong>{orders.length}</strong></span>
          <span className="stat-item">Pendientes: <strong>{orders.filter(o => o.orderStatus === 'pendiente').length}</strong></span>
        </div>
        <button className="btn btn-secondary" onClick={() => loadOrders(true)}>üîÑ Actualizar</button>
      </div>

      <div className="table-responsive">
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID Pedido</th>
              <th>Cliente</th>
              <th>Total</th>
              <th>Estado Pago</th>
              <th>Estado Pedido</th>
              <th>Etiqueta</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id} className={order.orderStatus === 'pendiente' ? 'row-urgent' : ''}>
                <td><strong>#{order._id.slice(-8).toUpperCase()}</strong></td>
                <td>
                  <div className="customer-cell">
                    <strong>{order.user?.name || 'Usuario'}</strong>
                    <small><FaPhone /> {order.phone || 'S/T'}</small>
                  </div>
                </td>
                <td><strong>{formatPrice(order.totalAmount)}</strong></td>
                <td><span className={`badge ${getStatusBadge(order.paymentStatus)}`}>{order.paymentStatus}</span></td>
                <td><span className={`badge ${getStatusBadge(order.orderStatus)}`}>{order.orderStatus}</span></td>
                <td>
                  {!order.tracking ? (
                    <button className="btn-table btn-generate" onClick={() => handleCreateTracking(order._id)}>
                      <FaFilePdf /> Generar Etiqueta
                    </button>
                  ) : (
                    <div style={{ display: 'flex', gap: '5px' }}>
                      <button className="btn-table btn-pdf" onClick={() => downloadLabel(order)} title="Descargar PDF">
                        <FaDownload />
                      </button>
                      <button className="btn-table btn-view" onClick={() => viewLabel(order)} title="Ver" style={{ background: '#3498db' }}>
                        <FaEye />
                      </button>
                    </div>
                  )}
                </td>
                <td>
                  <button className="btn-icon btn-view" onClick={() => {
                    setSelectedOrder(order);
                    setStatusUpdate({ orderStatus: order.orderStatus, paymentStatus: order.paymentStatus });
                    setShowDetailModal(true);
                  }}>
                    <FaEdit />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showDetailModal && selectedOrder && (
        <div className="modal-overlay" onClick={() => setShowDetailModal(false)}>
          <div className="modal-content large" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Pedido #{selectedOrder._id.slice(-8).toUpperCase()}</h3>
              <button className="modal-close" onClick={() => setShowDetailModal(false)}>&times;</button>
            </div>

            <div className="order-detail-container">
              <div className="detail-grid-top">
                <div className="detail-card">
                  <h4>üìç Direcci√≥n de Env√≠o</h4>
                  <div className="address-box">
                    <p><strong>{selectedOrder.user?.name}</strong></p>
                    <p>{selectedOrder.shippingAddress?.street}</p>
                    <p>{selectedOrder.shippingAddress?.city}, {selectedOrder.shippingAddress?.postalCode}</p>
                    <p>üìû {selectedOrder.phone}</p>
                  </div>
                </div>

                <div className="detail-card">
                  <h4>‚öôÔ∏è Estado del Pedido</h4>
                  <select 
                    className="form-select"
                    value={statusUpdate.orderStatus} 
                    onChange={(e) => setStatusUpdate({...statusUpdate, orderStatus: e.target.value})}
                  >
                    <option value="pendiente">Pendiente</option>
                    <option value="procesando">Procesando</option>
                    <option value="enviado">Enviado</option>
                    <option value="entregado">Entregado</option>
                    <option value="cancelado">Cancelado</option>
                  </select>
                  <button className="btn-update-status" onClick={updateOrderStatus} style={{marginTop: '15px'}}>
                    Guardar y Notificar
                  </button>
                </div>
              </div>

              <div className="detail-card full-width">
                <h4>üì¶ Productos ({selectedOrder.items?.length})</h4>
                <div className="order-items-list">
                  {selectedOrder.items?.map((item, idx) => (
                    <div key={idx} className="item-row">
                      <span>{item.quantity}x {item.product?.name}</span>
                      <span>{formatPrice(item.price * item.quantity)}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminOrders;
