import React, { useState, useEffect, useRef } from 'react';
import { FaComments, FaTimes, FaPaperPlane } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import '../styles/ChatWidget.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const ChatWidget = () => {
  const { isAuthenticated } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (isOpen && isAuthenticated()) {
      loadChat();
      const interval = setInterval(loadChat, 5000); // Actualizar cada 5 segundos
      return () => clearInterval(interval);
    }
  }, [isOpen, isAuthenticated]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };
useEffect(() => {
  const token = localStorage.getItem("token");
  socket.emit("auth", token);

  socket.on("chat_update_" + userId, chat => setChat(chat));
}, []);

  const loadChat = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await axios.get(`${API_URL}/chat/my-chat`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessages(response.data.messages);

      // Contar mensajes no le铆dos del admin
      const unread = response.data.messages.filter(
        m => m.sender === 'admin' && !m.read
      ).length;
      setUnreadCount(unread);

      // Marcar mensajes como le铆dos
      if (isOpen && unread > 0) {
        await axios.put(`${API_URL}/chat/mark-read`, {}, {
          headers: { Authorization: `Bearer ${token}` }
        });
      }
    } catch (error) {
      console.error('Error al cargar chat:', error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || loading) return;

    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      await axios.post(
        `${API_URL}/chat/send-message`,
        { text: inputMessage },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setInputMessage('');
      await loadChat();
    } catch (error) {
      console.error('Error al enviar mensaje:', error);
      alert('Error al enviar mensaje');
    } finally {
      setLoading(false);
    }
  };

  const toggleChat = () => {
    if (!isAuthenticated()) {
      alert('Debes iniciar sesi贸n para usar el chat');
      return;
    }
    setIsOpen(!isOpen);
  };

  const formatTime = (date) => {
    return new Date(date).toLocaleTimeString('es-ES', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="chat-widget">
      {/* Bot贸n flotante */}
      <button 
        className="chat-button" 
        onClick={toggleChat}
        aria-label="Chat de soporte"
      >
        {isOpen ? <FaTimes /> : <FaComments />}
        {!isOpen && unreadCount > 0 && (
          <span className="chat-badge">{unreadCount}</span>
        )}
      </button>

      {/* Ventana de chat */}
      {isOpen && (
        <div className="chat-window">
          <div className="chat-header">
            <h3> Atenci贸n al Cliente</h3>
            <button onClick={toggleChat} aria-label="Cerrar chat">
              <FaTimes />
            </button>
          </div>

          <div className="chat-messages">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`message ${message.sender === 'user' ? 'user-message' : 'admin-message'}`}
              >
                <div className="message-content">
                  <p>{message.text}</p>
                  <span className="message-time">{formatTime(message.timestamp)}</span>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          <form onSubmit={sendMessage} className="chat-input-form">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              placeholder="Escribe tu mensaje..."
              disabled={loading}
            />
            <button type="submit" disabled={loading || !inputMessage.trim()}>
              <FaPaperPlane />
            </button>
          </form>

          <div className="chat-footer">
            <small>Responderemos lo antes posible</small>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatWidget;
