import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUser, FaBox, FaBell, FaLock, FaEdit, FaSave, FaTimes } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import '../styles/Profile.css';
import TrackingView from '../components/TrackingView';
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Profile = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated, logout, login } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
    } else {
      setLoading(false);
    }
  }, [isAuthenticated, navigate]);

  if (loading) {
    return <div className="loading">Cargando perfil...</div>;
  }

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-header">
          <div className="user-avatar">
            {user?.avatar ? (
              <img src={user.avatar} alt={user.name} />
            ) : (
              <div className="avatar-placeholder">
                {user?.name?.charAt(0).toUpperCase()}
              </div>
            )}
          </div>
          <div className="user-info">
            <h1>{user?.name}</h1>
            <p>{user?.email}</p>
          </div>
        </div>

        <div className="profile-tabs">
          <button 
            className={activeTab === 'profile' ? 'active' : ''}
            onClick={() => setActiveTab('profile')}
          >
            <FaUser /> Mi Perfil
          </button>
          <button 
            className={activeTab === 'orders' ? 'active' : ''}
            onClick={() => setActiveTab('orders')}
          >
            <FaBox /> Mis Pedidos
          </button>
          <button 
            className={activeTab === 'notifications' ? 'active' : ''}
            onClick={() => setActiveTab('notifications')}
          >
            <FaBell /> Notificaciones
          </button>
          <button 
            className={activeTab === 'security' ? 'active' : ''}
            onClick={() => setActiveTab('security')}
          >
            <FaLock /> Seguridad
          </button>
        </div>

        <div className="profile-content">
          {activeTab === 'profile' && <ProfileTab user={user} login={login} />}
          {activeTab === 'orders' && <OrdersTab />}
          {activeTab === 'notifications' && <NotificationsTab />}
          {activeTab === 'security' && <SecurityTab />}
        </div>
      </div>
    </div>
  );
};

// TAB: Mi Perfil
const ProfileTab = ({ user, login }) => {
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name || '',
    phone: user?.phone || '',
    street: user?.address?.street || '',
    city: user?.address?.city || '',
    postalCode: user?.address?.postalCode || '',
    country: user?.address?.country || 'Espa√±a'
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const response = await axios.put(
        `${API_URL}/auth/profile`,
        {
          name: formData.name,
          phone: formData.phone,
          address: {
            street: formData.street,
            city: formData.city,
            postalCode: formData.postalCode,
            country: formData.country
          }
        },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      // Actualizar usuario en el contexto
      login({ ...user, ...response.data }, token);
      setEditing(false);
      alert('Perfil actualizado correctamente');
    } catch (error) {
      alert('Error al actualizar perfil');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-tab">
      <div className="tab-header">
        <h2>Informaci√≥n Personal</h2>
        {!editing ? (
          <button className="btn-edit" onClick={() => setEditing(true)}>
            <FaEdit /> Editar
          </button>
        ) : (
          <div className="edit-actions">
            <button className="btn-save" onClick={handleSave} disabled={loading}>
              <FaSave /> Guardar
            </button>
            <button className="btn-cancel" onClick={() => setEditing(false)}>
              <FaTimes /> Cancelar
            </button>
          </div>
        )}
      </div>

      <div className="profile-form">
        <div className="form-group">
          <label>Nombre completo</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            disabled={!editing}
          />
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={user?.email}
            disabled
          />
          {user?.emailVerified && (
            <span className="verified-badge">‚úì Verificado</span>
          )}
        </div>

        <div className="form-group">
          <label>Tel√©fono</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            disabled={!editing}
          />
        </div>

        <h3>Direcci√≥n</h3>

        <div className="form-group">
          <label>Calle</label>
          <input
            type="text"
            name="street"
            value={formData.street}
            onChange={handleChange}
            disabled={!editing}
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Ciudad</label>
            <input
              type="text"
              name="city"
              value={formData.city}
              onChange={handleChange}
              disabled={!editing}
            />
          </div>

          <div className="form-group">
            <label>C√≥digo Postal</label>
            <input
              type="text"
              name="postalCode"
              value={formData.postalCode}
              onChange={handleChange}
              disabled={!editing}
            />
          </div>
        </div>

        <div className="form-group">
          <label>Pa√≠s</label>
          <input
            type="text"
            name="country"
            value={formData.country}
            onChange={handleChange}
            disabled={!editing}
          />
        </div>
      </div>
    </div>
  );
};

// TAB: Mis Pedidos
const OrdersTab = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/orders/my-orders`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(response.data);
    } catch (error) {
      console.error('Error al cargar pedidos:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-ES', {
      style: 'currency',
      currency: 'EUR'
    }).format(price);
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const getStatusBadge = (status) => {
    const statusColors = {
      pendiente: 'warning',
      procesando: 'info',
      enviado: 'primary',
      entregado: 'success',
      cancelado: 'danger'
    };
    return statusColors[status] || 'secondary';
  };

  if (loading) {
    return <div className="loading">Cargando pedidos...</div>;
  }

  if (orders.length === 0) {
    return (
      <div className="empty-state">
        <FaBox size={64} />
        <h3>No tienes pedidos todav√≠a</h3>
        <p>Cuando hagas un pedido, aparecer√° aqu√≠</p>
      </div>
    );
  }

  return (
    <div className="orders-tab">
      <h2>Mis Pedidos ({orders.length})</h2>

      <div className="orders-list">
        {orders.map(order => (
          <div key={order._id} className="order-card">
            <div className="order-header">
              <div>
                <h3>Pedido #{order._id.slice(-8)}</h3>
                <p className="order-date">{formatDate(order.createdAt)}</p>
              </div>
              <div className="order-status">
                <span className={`status-badge ${getStatusBadge(order.orderStatus)}`}>
                  {order.orderStatus}
                </span>
                <span className={`status-badge ${getStatusBadge(order.paymentStatus)}`}>
                  {order.paymentStatus}
                </span>
                <div className="order-tracking-section">
    <TrackingView orderId={order._id} />
  </div>
              </div>
            </div>

            <div className="order-items">
              {order.items.map((item, index) => (
                <div key={index} className="order-item">
                  <img 
                    src={item.product?.images?.[0] || 'https://via.placeholder.com/60'} 
                    alt={item.product?.name} 
                  />
                  <div>
                    <p>{item.product?.name}</p>
                    <small>Cantidad: {item.quantity}</small>
                  </div>
                  <p>{formatPrice(item.price * item.quantity)}</p>
                </div>
              ))}
            </div>

            <div className="order-footer">
              <div className="order-total">
                <strong>Total:</strong> {formatPrice(order.totalAmount)}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// TAB: Notificaciones
const NotificationsTab = () => {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadNotifications();
  }, []);

  const loadNotifications = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/notifications`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotifications(response.data.notifications);
      setUnreadCount(response.data.unreadCount);
    } catch (error) {
      console.error('Error al cargar notificaciones:', error);
    } finally {
      setLoading(false);
    }
  };

  const markAsRead = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/notifications/${id}/read`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      loadNotifications();
    } catch (error) {
      console.error('Error al marcar como le√≠da:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/notifications/mark-all-read`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      loadNotifications();
    } catch (error) {
      console.error('Error:', error);
    }
  };

  if (loading) {
    return <div className="loading">Cargando notificaciones...</div>;
  }

  return (
    <div className="notifications-tab">
      <div className="tab-header">
        <h2>Notificaciones {unreadCount > 0 && `(${unreadCount} nuevas)`}</h2>
        {unreadCount > 0 && (
          <button className="btn-mark-all" onClick={markAllAsRead}>
            Marcar todas como le√≠das
          </button>
        )}
      </div>

      {notifications.length === 0 ? (
        <div className="empty-state">
          <FaBell size={64} />
          <h3>No tienes notificaciones</h3>
        </div>
      ) : (
        <div className="notifications-list">
          {notifications.map(notif => (
            <div 
              key={notif._id} 
              className={`notification-item ${!notif.read ? 'unread' : ''}`}
              onClick={() => !notif.read && markAsRead(notif._id)}
            >
              <div className="notification-icon">
                {notif.icon === 'check-circle' && '‚úì'}
                {notif.icon === 'truck' && 'üöö'}
                {notif.icon === 'bell' && 'üîî'}
              </div>
              <div className="notification-content">
                <h4>{notif.title}</h4>
                <p>{notif.message}</p>
                <small>{new Date(notif.createdAt).toLocaleDateString('es-ES')}</small>
              </div>
              {!notif.read && <span className="unread-dot"></span>}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// TAB: Seguridad
const SecurityTab = () => {
  const [formData, setFormData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    if (formData.newPassword !== formData.confirmPassword) {
      setError('Las contrase√±as no coinciden');
      return;
    }

    if (formData.newPassword.length < 6) {
      setError('La contrase√±a debe tener al menos 6 caracteres');
      return;
    }

    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      await axios.post(
        `${API_URL}/auth/change-password`,
        {
          currentPassword: formData.currentPassword,
          newPassword: formData.newPassword
        },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      setMessage('‚úì Contrase√±a actualizada correctamente');
      setFormData({ currentPassword: '', newPassword: '', confirmPassword: '' });
    } catch (err) {
      setError(err.response?.data?.message || 'Error al cambiar contrase√±a');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="security-tab">
      <h2>Cambiar Contrase√±a</h2>

      {message && <div className="success-message">{message}</div>}
      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Contrase√±a actual</label>
          <input
            type="password"
            name="currentPassword"
            value={formData.currentPassword}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Nueva contrase√±a</label>
          <input
            type="password"
            name="newPassword"
            value={formData.newPassword}
            onChange={handleChange}
            required
            minLength="6"
          />
        </div>

        <div className="form-group">
          <label>Confirmar nueva contrase√±a</label>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="btn-save" disabled={loading}>
          {loading ? 'Guardando...' : 'Cambiar Contrase√±a'}
        </button>
      </form>
    </div>
  );
};

export default Profile;
