import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaPrint, FaTruck, FaSync } from 'react-icons/fa';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  // 1. Cargar pedidos cada 30 segundos para automatizar
  useEffect(() => {
    loadOrders();
    const interval = setInterval(loadOrders, 30000); 
    return () => clearInterval(interval);
  }, []);

  // 2. MONITOR DE IMPRESIN
  useEffect(() => {
    const pendingToPrint = orders.find(order => order.needsPrinting === true);

    if (pendingToPrint && pendingToPrint.tracking) {
      handleAutoPrint(pendingToPrint);
    }
  }, [orders]);

  const loadOrders = async () => {
    const token = localStorage.getItem('token');
    const res = await axios.get(`${API_URL}/orders`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setOrders(res.data);
  };

  const handleAutoPrint = (order) => {
    // Sonido de alerta (opcional)
    const audio = new Audio('https://assets.mixkit.co/active_storage/sfx/2358/2358-preview.mp3');
    audio.play();

    if (window.confirm(` 隆NUEVO PEDIDO! #${order._id.slice(-6)}\n驴Imprimir etiqueta de preparaci贸n ahora?`)) {
      const trackingId = order.tracking._id || order.tracking;
      const token = localStorage.getItem('token');

      // Abrir PDF
      window.open(`${API_URL}/tracking/label/${trackingId}?token=${token}`, '_blank');

      // Avisar al backend que ya se imprimi贸 para que no vuelva a saltar
      markAsPrinted(order._id);
    }
  };

  const markAsPrinted = async (orderId) => {
    const token = localStorage.getItem('token');
    await axios.put(`${API_URL}/orders/${orderId}/printed`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    });
    loadOrders(); // Refrescar lista
  };

  return (
    <div className="admin-orders">
      <div className="header">
        <h2>Panel de Control de Almac茅n</h2>
        <button onClick={loadOrders} className="btn-refresh"><FaSync /> Actualizar</button>
      </div>

      <div className="orders-grid">
        {orders.map(order => (
          <div key={order._id} className={`order-card ${order.needsPrinting ? 'pulse-border' : ''}`}>
            <div className="order-info">
              <h4>Pedido #{order._id.slice(-6)}</h4>
              <p>{order.user?.name}</p>
              <span className={`status ${order.orderStatus}`}>{order.orderStatus}</span>
            </div>

            <div className="order-actions">
              {order.tracking ? (
                <button 
                  className="btn-print" 
                  onClick={() => window.open(`${API_URL}/tracking/label/${order.tracking._id || order.tracking}?token=${localStorage.getItem('token')}`, '_blank')}
                >
                  <FaPrint /> Reimprimir Etiqueta
                </button>
              ) : (
                <p>Esperando pago...</p>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminOrders;
