import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';
import { FaPaperPlane, FaTimes, FaComments, FaCheck } from 'react-icons/fa';
import '../../styles/AdminChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const SOCKET_URL = API_URL.replace('/api', '');

let socket = null;

const AdminChat = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    initializeSocket();
    loadChats();

    return () => {
      // No desconectar socket al desmontar
    };
  }, []);

  const initializeSocket = () => {
    if (socket) return;

    socket = io(SOCKET_URL, {
      transports: ['websocket', 'polling'],
      reconnection: true
    });

    socket.on('connect', () => {
      console.log('‚úÖ Admin socket conectado');
      setConnected(true);
      
      const token = localStorage.getItem('token');
      if (token) {
        socket.emit('authenticate', token);
      }
    });

    socket.on('disconnect', () => {
      console.log('‚ùå Socket desconectado');
      setConnected(false);
    });

    socket.on('authenticated', (data) => {
      console.log('‚úÖ Admin autenticado:', data);
    });

    // Nuevo mensaje de usuario
    socket.on('new_user_message', ({ chat }) => {
      console.log('üì® Nuevo mensaje de usuario');
      
      setChats(prevChats => {
        const exists = prevChats.find(c => c._id === chat._id);
        if (exists) {
          return prevChats.map(c => c._id === chat._id ? chat : c);
        }
        return [chat, ...prevChats];
      });

      if (selectedChat && selectedChat._id === chat._id) {
        setSelectedChat(chat);
      }
    });

    // Chat actualizado
    socket.on('chat_updated', (chat) => {
      console.log('üîÑ Chat actualizado');
      setChats(prevChats => prevChats.map(c => c._id === chat._id ? chat : c));
      
      if (selectedChat && selectedChat._id === chat._id) {
        setSelectedChat(chat);
      }
    });

    // Mensaje enviado confirmado
    socket.on('message_sent', ({ success, chat }) => {
      if (success) {
        console.log('‚úÖ Mensaje enviado');
        setLoading(false);
        setMessage('');
        
        if (selectedChat && selectedChat._id === chat._id) {
          setSelectedChat(chat);
        }
      }
    });

    socket.on('message_error', (data) => {
      console.error('‚ùå Error:', data.message);
      alert('Error al enviar mensaje: ' + data.message);
      setLoading(false);
    });
  };

  const loadChats = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        console.error('No hay token');
        return;
      }

      const response = await axios.get(`${API_URL}/chat/admin/chats?status=open`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      console.log('Chats cargados:', response.data.length);
      setChats(response.data);
    } catch (error) {
      console.error('Error al cargar chats:', error);
      if (error.response?.status === 403) {
        alert('No tienes permisos de administrador');
      }
    }
  };

  const sendMessage = () => {
    if (!message.trim() || !selectedChat || loading || !socket) {
      console.log('No se puede enviar:', { message: message.trim(), selectedChat, loading, socket: !!socket });
      return;
    }

    console.log('Enviando mensaje:', message);
    setLoading(true);
    
    socket.emit('admin_send_message', {
      chatId: selectedChat._id,
      text: message.trim()
    });
  };

  const closeChat = async () => {
    if (!selectedChat) return;

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `${API_URL}/chat/admin/close-chat/${selectedChat._id}`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert('Chat cerrado correctamente');
      setSelectedChat(null);
      loadChats();
    } catch (error) {
      console.error('Error al cerrar chat:', error);
      alert('Error al cerrar chat');
    }
  };

  const formatTime = (date) => {
    return new Date(date).toLocaleTimeString('es-ES', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="admin-chat-container">
      <div className="chat-header-main">
        <div className="header-left">
          <h1>üí¨ Chat de Atenci√≥n al Cliente</h1>
          <p className="header-subtitle">
            {connected ? 'üü¢ Conectado' : 'üî¥ Desconectado'}
          </p>
        </div>
        <div className="header-stats">
          <div className="stat-badge">
            <span className="stat-number">{chats.length}</span>
            <span className="stat-label">Chats Abiertos</span>
          </div>
        </div>
      </div>

      <div className="chat-layout">
        {/* Lista de chats */}
        <div className="chats-list">
          <div className="chats-list-header">
            <h3>Conversaciones</h3>
            <span className="badge-count">{chats.length}</span>
          </div>

          <div className="chats-list-items">
            {chats.length === 0 ? (
              <div className="no-chats">
                <div className="no-chats-icon"><FaComments size={48} /></div>
                <p>No hay chats abiertos</p>
                <small>Los nuevos mensajes aparecer√°n aqu√≠ autom√°ticamente</small>
              </div>
            ) : (
              chats.map(chat => {
                const lastMessage = chat.messages[chat.messages.length - 1];
                const unread = chat.messages.filter(m => m.sender === 'user' && !m.read).length;

                return (
                  <div
                    key={chat._id}
                    className={`chat-item ${selectedChat?._id === chat._id ? 'active' : ''} ${unread > 0 ? 'has-unread' : ''}`}
                    onClick={() => setSelectedChat(chat)}
                  >
                    <div className="chat-item-avatar">
                      üë§
                      {unread > 0 && <span className="unread-badge">{unread}</span>}
                    </div>
                    <div className="chat-item-content">
                      <div className="chat-item-header">
                        <span className="chat-item-name">{chat.user?.name || 'Usuario'}</span>
                        <span className="chat-item-time">
                          {formatTime(lastMessage.timestamp)}
                        </span>
                      </div>
                      <div className="chat-item-preview">
                        <span className={`message-sender ${lastMessage.sender === 'admin' ? 'you' : ''}`}>
                          {lastMessage.sender === 'admin' ? 'T√∫: ' : ''}
                        </span>
                        {lastMessage.text.substring(0, 40)}...
                      </div>
                    </div>
                  </div>
                );
              })
            )}
          </div>
        </div>

        {/* Conversaci√≥n */}
        <div className="chat-conversation">
          {selectedChat ? (
            <>
              <div className="conversation-header">
                <div className="conversation-user-info">
                  <div className="user-avatar-large">üë§</div>
                  <div className="user-details">
                    <h3>{selectedChat.user?.name || 'Usuario'}</h3>
                    <p className="user-email">{selectedChat.user?.email || ''}</p>
                    <p className="chat-status active">‚óè Chat activo</p>
                  </div>
                </div>
                <div className="conversation-actions">
                  <button className="btn-close-chat" onClick={closeChat}>
                    <FaCheck /> Finalizar Chat
                  </button>
                </div>
              </div>

              <div className="messages-container">
                <div className="messages-wrapper">
                  {selectedChat.messages.map((msg, index) => (
                    <div
                      key={index}
                      className={`message-bubble-wrapper ${msg.sender === 'admin' ? 'admin-message' : ''}`}
                    >
                      <div className="message-avatar">
                        <div className={`avatar ${msg.sender === 'admin' ? 'admin-avatar' : 'user-avatar'}`}>
                          {msg.sender === 'admin' ? 'üë®‚Äçüíº' : 'üë§'}
                        </div>
                      </div>
                      <div className="message-content-wrapper">
                        <div className="message-sender-label">
                          <span className={`sender-name ${msg.sender}`}>
                            {msg.sender === 'admin' ? 'T√∫' : selectedChat.user?.name}
                          </span>
                        </div>
                        <div className={`message-bubble ${msg.sender}`}>
                          <p className="message-text">{msg.text}</p>
                          <div className="message-footer">
                            <span className="message-time">{formatTime(msg.timestamp)}</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div className="message-input-container">
                <div className="input-wrapper">
                  <input
                    type="text"
                    className="message-input"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        sendMessage();
                      }
                    }}
                    placeholder="Escribe tu respuesta..."
                    disabled={loading || !connected}
                  />
                  <button
                    className={`btn-send ${message.trim() ? 'active' : ''}`}
                    onClick={sendMessage}
                    disabled={loading || !message.trim() || !connected}
                  >
                    <FaPaperPlane /> Enviar
                  </button>
                </div>
                <div className="input-help">
                  <small>{connected ? 'Presiona Enter para enviar' : 'Reconectando...'}</small>
                </div>
              </div>
            </>
          ) : (
            <div className="no-chat-selected">
              <div className="no-chat-icon"><FaComments size={64} /></div>
              <h3>Selecciona una conversaci√≥n</h3>
              <p>Elige un chat de la lista para ver y responder mensajes</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminChat;
