import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaPlus, FaEdit, FaTrash, FaChevronDown, FaChevronRight } from 'react-icons/fa';
import { toast } from 'react-toastify';
import '../../styles/AdminCategories.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const AdminCategories = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [showSubModal, setShowSubModal] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [expandedCategories, setExpandedCategories] = useState(new Set());

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    image: '',
    icon: ''
  });

  const [subFormData, setSubFormData] = useState({
    name: '',
    description: ''
  });

  useEffect(() => {
    loadCategories();
  }, []);

  const loadCategories = async () => {
    try {
      const token = sessionStorage.getItem('token') || localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/categories`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCategories(response.data);
    } catch (error) {
      console.error('Error al cargar categor칤as:', error);
      toast.error('Error al cargar categor칤as');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = sessionStorage.getItem('token') || localStorage.getItem('token');

      if (selectedCategory) {
        // Actualizar
        await axios.put(
          `${API_URL}/categories/${selectedCategory._id}`,
          formData,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        toast.success('Categor칤a actualizada');
      } else {
        // Crear
        await axios.post(
          `${API_URL}/categories`,
          formData,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        toast.success('Categor칤a creada');
      }

      loadCategories();
      closeModal();
    } catch (error) {
      console.error('Error:', error);
      toast.error(error.response?.data?.error || 'Error al guardar');
    }
  };

  const handleSubSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = sessionStorage.getItem('token') || localStorage.getItem('token');

      await axios.post(
        `${API_URL}/categories/${selectedCategory._id}/subcategories`,
        subFormData,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      toast.success('Subcategor칤a agregada');
      loadCategories();
      closeSubModal();
    } catch (error) {
      console.error('Error:', error);
      toast.error(error.response?.data?.error || 'Error al agregar subcategor칤a');
    }
  };

  const deleteCategory = async (id) => {
    if (!window.confirm('쮼st치s seguro de eliminar esta categor칤a?')) return;

    try {
      const token = sessionStorage.getItem('token') || localStorage.getItem('token');
      await axios.delete(`${API_URL}/categories/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      toast.success('Categor칤a eliminada');
      loadCategories();
    } catch (error) {
      toast.error(error.response?.data?.error || 'Error al eliminar');
    }
  };

  const deleteSubcategory = async (categoryId, subId) => {
    if (!window.confirm('쮼st치s seguro de eliminar esta subcategor칤a?')) return;

    try {
      const token = sessionStorage.getItem('token') || localStorage.getItem('token');
      await axios.delete(
        `${API_URL}/categories/${categoryId}/subcategories/${subId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success('Subcategor칤a eliminada');
      loadCategories();
    } catch (error) {
      toast.error(error.response?.data?.error || 'Error al eliminar');
    }
  };

  const openModal = (category = null) => {
    setSelectedCategory(category);
    setFormData(category ? {
      name: category.name,
      description: category.description || '',
      image: category.image || '',
      icon: category.icon || ''
    } : {
      name: '',
      description: '',
      image: '',
      icon: ''
    });
    setShowModal(true);
  };

  const openSubModal = (category) => {
    setSelectedCategory(category);
    setSubFormData({ name: '', description: '' });
    setShowSubModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedCategory(null);
  };

  const closeSubModal = () => {
    setShowSubModal(false);
    setSelectedCategory(null);
  };

  const toggleExpand = (categoryId) => {
    const newExpanded = new Set(expandedCategories);
    if (newExpanded.has(categoryId)) {
      newExpanded.delete(categoryId);
    } else {
      newExpanded.add(categoryId);
    }
    setExpandedCategories(newExpanded);
  };

  if (loading) return <div>Cargando...</div>;

  return (
    <div className="admin-categories">
      <div className="page-header">
        <h2>Gesti칩n de Categor칤as</h2>
        <button className="btn btn-primary" onClick={() => openModal()}>
          <FaPlus /> Nueva Categor칤a
        </button>
      </div>

      <div className="categories-list">
        {categories.map(category => (
          <div key={category._id} className="category-card">
            <div className="category-header">
              <div className="category-info">
                <button 
                  className="expand-btn"
                  onClick={() => toggleExpand(category._id)}
                >
                  {expandedCategories.has(category._id) ? <FaChevronDown /> : <FaChevronRight />}
                </button>
                <span className="category-icon">{category.icon || '游닍'}</span>
                <div>
                  <h3>{category.name}</h3>
                  <small>{category.subcategories?.length || 0} subcategor칤as</small>
                </div>
              </div>

              <div className="category-actions">
                <button 
                  className="btn-icon btn-success"
                  onClick={() => openSubModal(category)}
                  title="Agregar subcategor칤a"
                >
                  <FaPlus />
                </button>
                <button 
                  className="btn-icon btn-primary"
                  onClick={() => openModal(category)}
                  title="Editar"
                >
                  <FaEdit />
                </button>
                <button 
                  className="btn-icon btn-danger"
                  onClick={() => deleteCategory(category._id)}
                  title="Eliminar"
                >
                  <FaTrash />
                </button>
              </div>
            </div>

            {/* Subcategor칤as */}
            {expandedCategories.has(category._id) && (
              <div className="subcategories-list">
                {category.subcategories && category.subcategories.length > 0 ? (
                  category.subcategories.map(sub => (
                    <div key={sub._id} className="subcategory-item">
                      <span>{sub.name}</span>
                      <button 
                        className="btn-icon btn-danger btn-sm"
                        onClick={() => deleteSubcategory(category._id, sub._id)}
                      >
                        <FaTrash />
                      </button>
                    </div>
                  ))
                ) : (
                  <p className="no-subcategories">Sin subcategor칤as</p>
                )}
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Modal Categor칤a */}
      {showModal && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <h3>{selectedCategory ? 'Editar Categor칤a' : 'Nueva Categor칤a'}</h3>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Nombre *</label>
                <input
                  type="text"
                  value={formData.name}
                  onChange={e => setFormData({...formData, name: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Descripci칩n</label>
                <textarea
                  value={formData.description}
                  onChange={e => setFormData({...formData, description: e.target.value})}
                  rows="3"
                />
              </div>
              <div className="form-group">
                <label>Icono (emoji)</label>
                <input
                  type="text"
                  value={formData.icon}
                  onChange={e => setFormData({...formData, icon: e.target.value})}
                  placeholder="游닍"
                />
              </div>
              <div className="modal-actions">
                <button type="button" className="btn btn-secondary" onClick={closeModal}>
                  Cancelar
                </button>
                <button type="submit" className="btn btn-primary">
                  Guardar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Modal Subcategor칤a */}
      {showSubModal && (
        <div className="modal-overlay" onClick={closeSubModal}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <h3>Nueva Subcategor칤a en {selectedCategory?.name}</h3>
            <form onSubmit={handleSubSubmit}>
              <div className="form-group">
                <label>Nombre *</label>
                <input
                  type="text"
                  value={subFormData.name}
                  onChange={e => setSubFormData({...subFormData, name: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Descripci칩n</label>
                <textarea
                  value={subFormData.description}
                  onChange={e => setSubFormData({...subFormData, description: e.target.value})}
                  rows="2"
                />
              </div>
              <div className="modal-actions">
                <button type="button" className="btn btn-secondary" onClick={closeSubModal}>
                  Cancelar
                </button>
                <button type="submit" className="btn btn-primary">
                  Agregar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminCategories;
