import React, { useState, useEffect, useRef } from 'react';
import { FaEye, FaEdit, FaPhone, FaTruck, FaFilePdf, FaSync, FaPrint, FaCheckCircle } from 'react-icons/fa';
import axios from 'axios';
import '../../styles/AdminOrders.css';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [statusUpdate, setStatusUpdate] = useState({ orderStatus: '', paymentStatus: '' });
  const [error, setError] = useState(null);

  const notifiedOrders = useRef(new Set());
  const isFirstLoad = useRef(true);

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  // 1. CARGA INICIAL E INTERVALO
  useEffect(() => {
    loadOrders(true);
    const interval = setInterval(() => loadOrders(false), 30000);
    return () => clearInterval(interval);
  }, []);

  // 2. MONITOR DE IMPRESIÃ“N
  useEffect(() => {
    const pendingToPrint = orders.filter(o => 
      o.needsPrinting === true && 
      o.orderStatus === 'procesando' &&
      !notifiedOrders.current.has(o._id)
    );

    if (pendingToPrint.length > 0) {
      pendingToPrint.forEach(order => {
        notifiedOrders.current.add(order._id);
        if (!isFirstLoad.current) {
          handleAutoPrintPrompt(order);
        }
      });
      isFirstLoad.current = false;
    }
  }, [orders]);

  const loadOrders = async (initial = false) => {
    try {
      if (initial) setLoading(true);
      setRefreshing(true);
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/orders`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      const ordersData = Array.isArray(response.data) ? response.data : [];
      setOrders(ordersData);

      if (initial && ordersData.length > 0) {
        ordersData.forEach(o => notifiedOrders.current.add(o._id));
        isFirstLoad.current = false;
      }
    } catch (error) {
      console.error('âŒ Error:', error);
      setError("Error al conectar con el servidor");
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  // --- FUNCIONES DEL MODAL (AquÃ­ estaba el error) ---
  const openDetailModal = (order) => {
    setSelectedOrder(order);
    setStatusUpdate({ 
      orderStatus: order.orderStatus, 
      paymentStatus: order.paymentStatus 
    });
    setShowDetailModal(true);
  };

  const closeDetailModal = () => {
    setShowDetailModal(false);
    setSelectedOrder(null);
  };

  const handleStatusChange = (e) => {
    setStatusUpdate({ ...statusUpdate, [e.target.name]: e.target.value });
  };

  const updateOrderStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/orders/${selectedOrder._id}/status`, statusUpdate, {
        headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' }
      });
      alert('Estado actualizado');
      closeDetailModal();
      loadOrders(false);
    } catch (error) {
      alert('Error al actualizar');
    }
  };

  // --- FUNCIONES DE LOGÃSTICA ---
  const handleAutoPrintPrompt = (order) => {
    const audio = new Audio('https://assets.mixkit.co/active_storage/sfx/2358/2358-preview.mp3');
    audio.play().catch(() => {});

    setTimeout(() => {
      if (window.confirm(`ðŸ“¦ Â¡NUEVO PEDIDO! #${order._id.slice(-6).toUpperCase()}\nÂ¿Imprimir etiqueta?`)) {
        const tId = order.tracking?._id || order.tracking;
        if (tId) {
          downloadLabel(tId);
          markAsPrinted(order._id);
        }
      }
    }, 1000);
  };

  const downloadLabel = (trackingId) => {
    const token = localStorage.getItem('token');
    window.open(`${API_URL}/tracking/label/${trackingId}?token=${token}`, '_blank');
  };

  const markAsPrinted = async (orderId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/orders/${orderId}/printed`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(prev => prev.map(o => o._id === orderId ? { ...o, needsPrinting: false } : o));
    } catch (err) {
      console.error("Error al marcar como impreso", err);
    }
  };

  const handleCreateTracking = async (orderId) => {
    const carrier = prompt("Introduce el transportista:", "SimShip Express");
    if (!carrier) return;
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_URL}/tracking/create`, { orderId, carrier }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      loadOrders(false);
    } catch (err) {
      alert("Error al generar tracking");
    }
  };

  if (loading) return <div className="admin-orders"><div className="spinner-large"></div></div>;

  return (
    <div className="admin-orders">
      <div className="page-header">
        <h2>GestiÃ³n de Pedidos {refreshing && <small>ðŸ”„</small>}</h2>
        <button className="btn btn-secondary" onClick={() => loadOrders(true)}>Refrescar</button>
      </div>

      <div className="table-responsive">
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Cliente</th>
              <th>Total</th>
              <th>Estado</th>
              <th>LogÃ­stica</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id} className={order.needsPrinting ? 'row-urgent' : ''}>
                <td>#{order._id.slice(-8)}</td>
                <td>{order.user?.name || 'Cliente'}</td>
                <td>{order.totalAmount}â‚¬</td>
                <td><span className={`badge badge-${order.orderStatus}`}>{order.orderStatus}</span></td>
                <td>
                  {!order.tracking ? (
                    <button className="btn-table" onClick={() => handleCreateTracking(order._id)}><FaTruck /> Enviar</button>
                  ) : (
                    <button className="btn-table" onClick={() => downloadLabel(order.tracking._id || order.tracking)}><FaPrint /> Etiqueta</button>
                  )}
                </td>
                <td>
                  <button className="btn-icon" onClick={() => openDetailModal(order)}><FaEye /></button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* MODAL DE DETALLE */}
      {showDetailModal && selectedOrder && (
        <div className="modal-overlay" onClick={closeDetailModal}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Pedido #{selectedOrder._id.slice(-8)}</h3>
              <button onClick={closeDetailModal}>&times;</button>
            </div>
            <div className="modal-body">
              <p><strong>Cliente:</strong> {selectedOrder.user?.name}</p>
              <div className="status-section">
                <select name="orderStatus" value={statusUpdate.orderStatus} onChange={handleStatusChange}>
                  <option value="pendiente">Pendiente</option>
                  <option value="procesando">Procesando</option>
                  <option value="enviado">Enviado</option>
                  <option value="entregado">Entregado</option>
                </select>
                <button className="btn btn-primary" onClick={updateOrderStatus}><FaEdit /> Actualizar</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminOrders;
