import React, { useState, useEffect } from 'react';
import { FaEye, FaEdit, FaPhone, FaTruck, FaFilePdf, FaSync, FaPrint, FaCheckCircle } from 'react-icons/fa';
import axios from 'axios';
import '../../styles/AdminOrders.css';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [statusUpdate, setStatusUpdate] = useState({
    orderStatus: '',
    paymentStatus: ''
  });
  const [error, setError] = useState(null);

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  // 1. Carga inicial y refresco autom치tico cada 30 segundos
  useEffect(() => {
    loadOrders();
    const interval = setInterval(loadOrders, 30000);
    return () => clearInterval(interval);
  }, []);

  // 2. Monitor de impresi칩n autom치tica para nuevos pedidos
  useEffect(() => {
    const newOrder = orders.find(o => o.needsPrinting === true && o.orderStatus === 'procesando');
    if (newOrder) {
      triggerAutoPrintPrompt(newOrder);
    }
  }, [orders]);

  const loadOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;
      const response = await axios.get(`${API_URL}/orders`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setOrders(Array.isArray(response.data) ? response.data : []);
      setLoading(false);
    } catch (err) {
      console.error('Error:', err);
      setLoading(false);
    }
  };

  const triggerAutoPrintPrompt = (order) => {
    const audio = new Audio('https://assets.mixkit.co/active_storage/sfx/2358/2358-preview.mp3');
    audio.play().catch(() => {});

    if (window.confirm(`游닍 NUEVO PEDIDO #${order._id.slice(-6).toUpperCase()}\n쮸brir etiqueta ahora?`)) {
      const tId = order.tracking?._id || order.tracking;
      if (tId) {
        downloadLabel(tId);
        markAsPrinted(order._id);
      }
    }
  };

  const downloadLabel = (trackingId) => {
    const token = localStorage.getItem('token');
    window.open(`${API_URL}/tracking/label/${trackingId}?token=${token}`, '_blank');
  };

  const markAsPrinted = async (orderId) => {
    const token = localStorage.getItem('token');
    await axios.put(`${API_URL}/orders/${orderId}/printed`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    });
    loadOrders();
  };

  const handleCreateTracking = async (orderId) => {
    const carrier = prompt("Transportista:", "SimShip Express");
    if (!carrier) return;
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_URL}/tracking/create`, { orderId, carrier }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      loadOrders();
    } catch (err) { alert("Error al crear tracking"); }
  };

  const updateOrderStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/orders/${selectedOrder._id}/status`, statusUpdate, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Estado actualizado');
      setShowDetailModal(false);
      loadOrders();
    } catch (err) { alert('Error al actualizar'); }
  };

  const openDetailModal = (order) => {
    setSelectedOrder(order);
    setStatusUpdate({ orderStatus: order.orderStatus, paymentStatus: order.paymentStatus });
    setShowDetailModal(true);
  };

  if (loading) return <div className="admin-orders"><div className="spinner"></div></div>;

  return (
    <div className="admin-orders">
      <div className="page-header">
        <h2>Gesti칩n de Pedidos y Almac칠n</h2>
        <button className="btn-refresh" onClick={loadOrders}><FaSync /> Actualizar</button>
      </div>

      <table className="admin-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Cliente</th>
            <th>Estado</th>
            <th>Log칤stica</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order._id} className={order.needsPrinting ? 'row-new' : ''}>
              <td>#{order._id.slice(-6).toUpperCase()}</td>
              <td>{order.user?.name}</td>
              <td><span className={`badge badge-${order.orderStatus}`}>{order.orderStatus}</span></td>
              <td>
                {order.tracking ? (
                  <button className="btn-table btn-pdf" onClick={() => downloadLabel(order.tracking._id || order.tracking)}>
                    <FaPrint /> Etiqueta
                  </button>
                ) : (
                  <button className="btn-table btn-prep" onClick={() => handleCreateTracking(order._id)}>
                    <FaTruck /> Preparar
                  </button>
                )}
              </td>
              <td>
                <button className="btn-icon" onClick={() => openDetailModal(order)}><FaEye /></button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showDetailModal && selectedOrder && (
        <div className="modal-overlay" onClick={() => setShowDetailModal(false)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Pedido #{selectedOrder._id.slice(-8)}</h3>
              <span className="close" onClick={() => setShowDetailModal(false)}>&times;</span>
            </div>

            <div className="modal-body">
              {/* SECCI칍N CORREGIDA: AQU칈 APARECER츼 TU BOT칍N */}
              <div className="detail-section label-status">
                <h4><FaPrint /> Estado de Impresi칩n</h4>
                <div className="status-container">
                  {selectedOrder.needsPrinting ? (
                    <p className="text-warning">丘멆잺 Etiqueta pendiente de impresi칩n.</p>
                  ) : (
                    <p className="text-success"><FaCheckCircle /> Etiqueta ya generada/impresa.</p>
                  )}

                  {/* El bot칩n siempre visible si hay tracking */}
                  {selectedOrder.tracking && (
                    <button 
                      className="btn-download-label" 
                      onClick={() => downloadLabel(selectedOrder.tracking._id || selectedOrder.tracking)}
                    >
                      <FaFilePdf /> DESCARGAR ETIQUETA PARA IMPRIMIR
                    </button>
                  )}
                </div>
              </div>

              <div className="detail-section">
                <h4>丘뙖잺 Cambiar Estado</h4>
                <div className="flex-row">
                  <select 
                    value={statusUpdate.orderStatus} 
                    onChange={e => setStatusUpdate({...statusUpdate, orderStatus: e.target.value})}
                  >
                    <option value="pendiente">Pendiente</option>
                    <option value="procesando">Procesando (Almac칠n)</option>
                    <option value="enviado">Enviado</option>
                  </select>
                  <button className="btn-save" onClick={updateOrderStatus}>Guardar</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminOrders;
