import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUser, FaBox, FaBell, FaLock } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import '../styles/Profile.css';

// Importamos los nuevos componentes
import ProfileTab from '../components/profile/ProfileTab';
import OrdersTab from '../components/profile/OrdersTab';
import NotificationsTab from '../components/profile/NotificationsTab';
import SecurityTab from '../components/profile/SecurityTab';

const Profile = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated, logout, login } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');

  useEffect(() => {
    if (!isAuthenticated()) navigate('/login');
  }, [isAuthenticated, navigate]);

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-header">
          <div className="user-info">
            <h1>{user?.name}</h1>
            <p>{user?.email}</p>
          </div>
        </div>

        <div className="profile-tabs">
          <button className={activeTab === 'profile' ? 'active' : ''} onClick={() => setActiveTab('profile')}><FaUser /> Perfil</button>
          <button className={activeTab === 'orders' ? 'active' : ''} onClick={() => setActiveTab('orders')}><FaBox /> Pedidos</button>
          <button className={activeTab === 'notifications' ? 'active' : ''} onClick={() => setActiveTab('notifications')}><FaBell /> Avisos</button>
          <button className={activeTab === 'security' ? 'active' : ''} onClick={() => setActiveTab('security')}><FaLock /> Seguridad</button>
        </div>

        <div className="profile-content">
          {activeTab === 'profile' && <ProfileTab user={user} login={login} />}
          {activeTab === 'orders' && <OrdersTab />}
          {activeTab === 'notifications' && <NotificationsTab />}
          {activeTab === 'security' && <SecurityTab />}
        </div>

        <button className="btn-logout-main" onClick={logout}>Cerrar Sesi√≥n</button>
      </div>
    </div>
  );
};

export default Profile;
