import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';
import '../../styles/AdminChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const SOCKET_URL = API_URL.replace('/api', '');

let socket = null;

const AdminChat = () => {
  const [allChats, setAllChats] = useState([]); // ‚úÖ TODOS los chats
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState('');
  const [view, setView] = useState('open'); // 'open' o 'closed'
  const [connected, setConnected] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);

  const getToken = () => {
    return localStorage.getItem('token') || sessionStorage.getItem('token');
  };

  useEffect(() => {
    scrollToBottom();
  }, [selectedChat?.messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    const token = getToken();
    if (!token) {
      setError('No hay sesi√≥n activa');
      return;
    }

    initializeSocket(token);
    loadAllChats();

    // Recargar cada 10 segundos como backup
    const interval = setInterval(loadAllChats, 10000);

    return () => {
      clearInterval(interval);
    };
  }, []); // Solo al montar

  const initializeSocket = (token) => {
    if (socket) return;

    console.log('üîå [ADMIN] Conectando socket...');
    
    socket = io(SOCKET_URL, { 
      transports: ['websocket', 'polling'],
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000
    });

    socket.on('connect', () => {
      console.log('‚úÖ [ADMIN] Socket conectado');
      setConnected(true);
      socket.emit('authenticate', token);
    });

    socket.on('disconnect', () => {
      console.log('‚ùå [ADMIN] Socket desconectado');
      setConnected(false);
    });

    socket.on('authenticated', (data) => {
      console.log('‚úÖ [ADMIN] Autenticado:', data);
    });

    // ‚úÖ NUEVO MENSAJE DE USUARIO
    socket.on('new_user_message', (data) => {
      console.log('üì® [ADMIN] Nuevo mensaje de usuario:', data);
      const chat = data.chat || data;
      if (chat && chat._id) {
        updateOrAddChat(chat);
      }
    });

    // ‚úÖ CONFIRMACI√ìN MENSAJE ADMIN
    socket.on('message_sent', (data) => {
      console.log('‚úÖ [ADMIN] Mensaje enviado:', data);
      if (data && data.success && data.chat) {
        updateOrAddChat(data.chat);
      }
    });

    // ‚úÖ CHAT ACTUALIZADO
    socket.on('chat_updated', (data) => {
      console.log('üîÑ [ADMIN] Chat actualizado:', data);
      const chat = data.chat || data;
      if (chat && chat._id) {
        updateOrAddChat(chat);
      }
    });

    // ‚úÖ CHAT CERRADO
    socket.on('chat_closed', (data) => {
      console.log('üîí [ADMIN] Chat cerrado:', data);
      const chat = data.chat || data;
      if (chat && chat._id) {
        updateOrAddChat(chat);
        
        // Si es el chat seleccionado, actualizar pero no deseleccionar
        setSelectedChat(prev => {
          if (prev?._id === chat._id) {
            return chat; // Actualizar con nuevo estado
          }
          return prev;
        });
      }
    });

    socket.on('message_error', (data) => {
      console.error('‚ùå [ADMIN] Error:', data);
      alert('Error al enviar mensaje: ' + (data?.message || 'Error desconocido'));
    });
  };

  // ‚úÖ ACTUALIZAR O AGREGAR CHAT
  const updateOrAddChat = (updatedChat) => {
    if (!updatedChat || !updatedChat._id) {
      console.error('‚ùå Chat inv√°lido');
      return;
    }

    // Asegurar que messages sea un array
    if (!Array.isArray(updatedChat.messages)) {
      updatedChat.messages = [];
    }

    console.log('üîÑ Actualizando chat:', updatedChat._id, 'Status:', updatedChat.status);
    
    setAllChats(prev => {
      const currentChats = Array.isArray(prev) ? prev : [];
      const existingIndex = currentChats.findIndex(c => c._id === updatedChat._id);
      
      if (existingIndex !== -1) {
        // Actualizar chat existente
        const updated = [...currentChats];
        updated[existingIndex] = updatedChat;
        return updated;
      } else {
        // Agregar nuevo chat
        return [updatedChat, ...currentChats];
      }
    });

    // Actualizar chat seleccionado si es el mismo
    setSelectedChat(prev => {
      if (prev?._id === updatedChat._id) {
        return updatedChat;
      }
      return prev;
    });
  };

  // ‚úÖ CARGAR TODOS LOS CHATS
  const loadAllChats = async () => {
    try {
      const token = getToken();
      if (!token) {
        setError('No hay sesi√≥n activa');
        setLoading(false);
        return;
      }

      console.log('üì• [ADMIN] Cargando todos los chats...');

      // Cargar TODOS los chats sin filtro
      const response = await axios.get(`${API_URL}/chat/admin/chats`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      const chatData = Array.isArray(response.data) ? response.data : [];
      
      console.log('‚úÖ [ADMIN] Total chats cargados:', chatData.length);
      console.log('  - Abiertos:', chatData.filter(c => c.status === 'open').length);
      console.log('  - Cerrados:', chatData.filter(c => c.status === 'closed').length);
      
      setAllChats(chatData);
      setError(null);
    } catch (err) {
      console.error('‚ùå [ADMIN] Error al cargar chats:', err);
      setError('Error al conectar con el servidor');
      setAllChats([]);
      
      if (err.response?.status === 401) {
        alert('Sesi√≥n expirada. Por favor, inicia sesi√≥n nuevamente.');
        window.location.href = '/login';
      }
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ FILTRAR CHATS SEG√öN LA VISTA
  const filteredChats = Array.isArray(allChats) 
    ? allChats.filter(chat => chat.status === view)
    : [];

  console.log('üìä Chats filtrados:', {
    vista: view,
    total: allChats.length,
    filtrados: filteredChats.length
  });

  const sendMessage = (e) => {
    e.preventDefault();
    
    if (!message.trim() || !selectedChat) return;

    if (!connected) {
      alert('No hay conexi√≥n. Reconectando...');
      return;
    }

    console.log('üì§ [ADMIN] Enviando mensaje a chat:', selectedChat._id);

    socket.emit('admin_send_message', {
      chatId: selectedChat._id,
      text: message
    });

    setMessage('');
  };

  const closeChat = async (chatId) => {
    if (!window.confirm('¬øFinalizar esta conversaci√≥n?')) return;
    
    try {
      const token = getToken();
      if (!token) {
        alert('Sesi√≥n expirada');
        window.location.href = '/login';
        return;
      }

      console.log('üîí [ADMIN] Cerrando chat:', chatId);

      await axios.put(
        `${API_URL}/chat/admin/close-chat/${chatId}`, 
        {}, 
        { headers: { 'Authorization': `Bearer ${token}` } }
      );

      console.log('‚úÖ [ADMIN] Chat cerrado correctamente');
      
      // ‚úÖ Recargar para asegurar sincronizaci√≥n
      await loadAllChats();
      
      // Deseleccionar chat
      setSelectedChat(null);
      
      alert('‚úÖ Conversaci√≥n finalizada');
      
    } catch (err) {
      console.error('‚ùå [ADMIN] Error al cerrar chat:', err);
      alert('Error al cerrar la conversaci√≥n');
      
      if (err.response?.status === 401) {
        window.location.href = '/login';
      }
    }
  };

  const selectedChatMessages = Array.isArray(selectedChat?.messages) ? selectedChat.messages : [];

  return (
    <div className="admin-chat-container" style={{ padding: '20px' }}>
      {/* HEADER */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1>üí¨ Panel de Soporte</h1>
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '10px',
          padding: '8px 15px',
          background: connected ? '#d4edda' : '#f8d7da',
          borderRadius: '20px',
          fontSize: '14px'
        }}>
          <span style={{ 
            width: '10px', 
            height: '10px', 
            borderRadius: '50%', 
            background: connected ? '#28a745' : '#dc3545',
            display: 'inline-block'
          }}></span>
          {connected ? 'Conectado' : 'Desconectado'}
        </div>
      </div>

      {/* FILTROS */}
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <button 
          onClick={() => {
            setView('open');
            setSelectedChat(null);
          }}
          style={{
            padding: '10px 20px', 
            borderRadius: '20px', 
            border: 'none',
            background: view === 'open' ? '#667eea' : '#ddd',
            color: view === 'open' ? 'white' : '#666',
            cursor: 'pointer', 
            fontWeight: 'bold',
            transition: 'all 0.3s'
          }}
        >
          üî• Chats Activos ({filteredChats.length})
        </button>
        <button 
          onClick={() => {
            setView('closed');
            setSelectedChat(null);
          }}
          style={{
            padding: '10px 20px', 
            borderRadius: '20px', 
            border: 'none',
            background: view === 'closed' ? '#2c3e50' : '#ddd',
            color: view === 'closed' ? 'white' : '#666',
            cursor: 'pointer', 
            fontWeight: 'bold',
            transition: 'all 0.3s'
          }}
        >
          üìÅ Historial ({allChats.filter(c => c.status === 'closed').length})
        </button>
      </div>

      {error && (
        <div style={{ 
          background: '#f8d7da', 
          color: '#721c24', 
          padding: '15px', 
          borderRadius: '8px', 
          marginBottom: '20px',
          border: '1px solid #f5c6cb'
        }}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      {/* LAYOUT PRINCIPAL */}
      <div style={{ display: 'grid', gridTemplateColumns: '350px 1fr', gap: '20px' }}>
        {/* LISTA DE CHATS */}
        <div style={{ 
          background: 'white', 
          borderRadius: '8px', 
          border: '1px solid #ddd', 
          height: '70vh', 
          overflowY: 'auto',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
        }}>
          <div style={{ 
            padding: '15px', 
            borderBottom: '1px solid #eee', 
            background: '#fcfcfc',
            position: 'sticky',
            top: 0,
            zIndex: 10
          }}>
            <strong>
              {view === 'open' ? 'üî• Conversaciones en curso' : 'üìÅ Chats finalizados'} ({filteredChats.length})
            </strong>
          </div>
          
          {loading ? (
            <div style={{ padding: '20px', textAlign: 'center', color: '#999' }}>
              Cargando...
            </div>
          ) : filteredChats.length === 0 ? (
            <div style={{ padding: '40px 20px', textAlign: 'center', color: '#999' }}>
              <div style={{ fontSize: '48px', marginBottom: '10px' }}>
                {view === 'open' ? 'üí¨' : 'üì≠'}
              </div>
              <p>
                {view === 'open' 
                  ? 'No hay chats activos' 
                  : 'No hay chats cerrados'}
              </p>
            </div>
          ) : (
            filteredChats.map(chat => {
              const lastMessage = Array.isArray(chat.messages) && chat.messages.length > 0 
                ? chat.messages[chat.messages.length - 1] 
                : null;

              return (
                <div 
                  key={chat._id}
                  onClick={() => setSelectedChat(chat)}
                  style={{
                    padding: '15px', 
                    borderBottom: '1px solid #f5f5f5', 
                    cursor: 'pointer',
                    background: selectedChat?._id === chat._id ? '#f0f4ff' : 'transparent',
                    borderLeft: selectedChat?._id === chat._id ? '4px solid #667eea' : 'none',
                    transition: 'all 0.2s'
                  }}
                >
                  <div style={{ fontWeight: 'bold', color: '#333', marginBottom: '5px' }}>
                    {chat.user?.name || 'Cliente'}
                    {chat.status === 'closed' && (
                      <span style={{ 
                        marginLeft: '8px', 
                        padding: '2px 8px', 
                        background: '#28a745', 
                        color: 'white', 
                        fontSize: '10px', 
                        borderRadius: '10px' 
                      }}>
                        CERRADO
                      </span>
                    )}
                  </div>
                  <div style={{ 
                    fontSize: '12px', 
                    color: '#666', 
                    whiteSpace: 'nowrap', 
                    overflow: 'hidden', 
                    textOverflow: 'ellipsis' 
                  }}>
                    {lastMessage?.text || 'Sin mensajes'}
                  </div>
                  {lastMessage?.timestamp && (
                    <div style={{ fontSize: '11px', color: '#999', marginTop: '5px' }}>
                      {new Date(lastMessage.timestamp).toLocaleString('es-ES', {
                        day: '2-digit',
                        month: '2-digit',
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </div>
                  )}
                </div>
              );
            })
          )}
        </div>

        {/* CONVERSACI√ìN */}
        <div style={{ 
          background: 'white', 
          borderRadius: '8px', 
          border: '1px solid #ddd', 
          display: 'flex', 
          flexDirection: 'column', 
          height: '70vh',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
        }}>
          {selectedChat ? (
            <>
              {/* Header del chat */}
              <div style={{ 
                padding: '15px', 
                borderBottom: '1px solid #eee', 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                background: '#fcfcfc'
              }}>
                <div>
                  <h3 style={{ margin: 0, color: '#333' }}>
                    {selectedChat.user?.name || 'Cliente'}
                    {selectedChat.status === 'closed' && (
                      <span style={{ 
                        marginLeft: '10px', 
                        fontSize: '14px', 
                        color: '#28a745' 
                      }}>
                        ‚úì Cerrado
                      </span>
                    )}
                  </h3>
                  <small style={{ color: '#888' }}>{selectedChat.user?.email || ''}</small>
                </div>
                {selectedChat.status === 'open' && (
                  <button 
                    onClick={() => closeChat(selectedChat._id)} 
                    style={{ 
                      background: '#ff4757', 
                      color: 'white', 
                      border: 'none', 
                      padding: '8px 15px', 
                      borderRadius: '5px', 
                      cursor: 'pointer', 
                      fontWeight: '600',
                      transition: 'all 0.3s'
                    }}
                  >
                    ‚úì Finalizar Chat
                  </button>
                )}
              </div>
              
              {/* Mensajes */}
              <div style={{ 
                flex: 1, 
                padding: '20px', 
                overflowY: 'auto', 
                background: '#f8f9fa' 
              }}>
                {selectedChatMessages.length === 0 ? (
                  <div style={{ 
                    textAlign: 'center', 
                    color: '#999', 
                    marginTop: '50px' 
                  }}>
                    No hay mensajes todav√≠a
                  </div>
                ) : (
                  selectedChatMessages.map((m, i) => (
                    <div 
                      key={i} 
                      style={{ 
                        display: 'flex', 
                        justifyContent: m.sender === 'admin' ? 'flex-end' : 'flex-start', 
                        marginBottom: '15px'
                      }}
                    >
                      <div style={{ 
                        background: m.sender === 'admin' ? '#667eea' : 'white', 
                        color: m.sender === 'admin' ? 'white' : '#333',
                        padding: '10px 15px', 
                        borderRadius: '12px', 
                        maxWidth: '75%',
                        boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
                        border: m.sender === 'admin' ? 'none' : '1px solid #eee'
                      }}>
                        {m.sender === 'user' && (
                          <div style={{ 
                            fontSize: '11px', 
                            fontWeight: 'bold', 
                            marginBottom: '5px',
                            color: '#667eea'
                          }}>
                            {selectedChat.user?.name || 'Cliente'}
                          </div>
                        )}
                        <p style={{ margin: 0, wordWrap: 'break-word' }}>{m.text}</p>
                        <small style={{ 
                          display: 'block', 
                          marginTop: '5px', 
                          fontSize: '10px', 
                          opacity: 0.7 
                        }}>
                          {m.timestamp ? new Date(m.timestamp).toLocaleTimeString([], { 
                            hour: '2-digit', 
                            minute: '2-digit' 
                          }) : ''}
                        </small>
                      </div>
                    </div>
                  ))
                )}
                <div ref={messagesEndRef} />
              </div>

              {/* Input */}
              {selectedChat.status === 'open' ? (
                <form 
                  onSubmit={sendMessage} 
                  style={{ 
                    padding: '15px', 
                    borderTop: '1px solid #eee', 
                    display: 'flex', 
                    gap: '10px',
                    background: 'white'
                  }}
                >
                  <input 
                    type="text" 
                    value={message} 
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Escribe tu respuesta..."
                    disabled={!connected}
                    style={{ 
                      flex: 1, 
                      padding: '12px', 
                      borderRadius: '8px', 
                      border: '1px solid #ddd', 
                      outline: 'none',
                      fontSize: '14px'
                    }}
                  />
                  <button 
                    type="submit" 
                    disabled={!connected || !message.trim()}
                    style={{ 
                      background: connected && message.trim() ? '#667eea' : '#ccc', 
                      color: 'white', 
                      border: 'none', 
                      padding: '10px 25px', 
                      borderRadius: '8px', 
                      cursor: connected && message.trim() ? 'pointer' : 'not-allowed', 
                      fontWeight: 'bold',
                      transition: 'all 0.3s'
                    }}
                  >
                    Enviar
                  </button>
                </form>
              ) : (
                <div style={{
                  padding: '15px',
                  borderTop: '1px solid #eee',
                  textAlign: 'center',
                  color: '#888',
                  background: '#f8f9fa'
                }}>
                  ‚úì Esta conversaci√≥n est√° cerrada
                </div>
              )}
            </>
          ) : (
            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              alignItems: 'center', 
              justifyContent: 'center', 
              height: '100%', 
              color: '#aaa' 
            }}>
              <div style={{ fontSize: '48px', marginBottom: '20px' }}>üí¨</div>
              <p>Selecciona una conversaci√≥n para comenzar</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminChat;
