import React, { createContext, useState, useContext, useEffect } from 'react';
import toast from 'react-hot-toast';

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart debe usarse dentro de un CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCart(JSON.parse(savedCart));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

 const addToCart = (product) => {
  setCart((prevCart) => {
    const existingItem = prevCart.find((item) => item._id === product._id);

    let newCart;
    if (existingItem) {
      // Si ya existe, aumentar cantidad
      newCart = prevCart.map((item) =>
        item._id === product._id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );

      // âœ… TOAST para producto ya en carrito
      toast.success(
        `${product.name} (${existingItem.quantity + 1})`,
        {
          icon: 'ðŸ›’',
          duration: 2000,
          position: 'top-right'
        }
      );
    } else {
      // Si no existe, aÃ±adir nuevo
      newCart = [...prevCart, { ...product, quantity: 1 }];

      // âœ… TOAST para producto nuevo
      toast.success(
        `âœ… ${product.name} aÃ±adido al carrito`,
        {
          duration: 3000,
          position: 'top-right',
          style: {
            background: '#067D62',
            color: '#fff',
          }
        }
      );
    }

    localStorage.setItem('cart', JSON.stringify(newCart));
    return newCart;
  });
};

 const removeFromCart = (productId) => {
  setCart((prevCart) => {
    const removedItem = prevCart.find(item => item._id === productId);
    const newCart = prevCart.filter((item) => item._id !== productId);
    localStorage.setItem('cart', JSON.stringify(newCart));

    // âœ… TOAST de eliminaciÃ³n
    if (removedItem) {
      toast.error(
        `${removedItem.name} eliminado`,
        {
          duration: 2000,
          position: 'top-right'
        }
      );
    }

    return newCart;
  });
};

  const updateQuantity = (productId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(productId);
      return;
    }

    setCart(prevCart =>
      prevCart.map(item =>
        item._id === productId ? { ...item, quantity } : item
      )
    );
  };

  const clearCart = () => {
  setCart([]);
  localStorage.removeItem('cart');

  // âœ… TOAST de carrito vaciado
  toast.success('Carrito vaciado', {
    icon: 'ðŸ—‘ï¸',
    duration: 2000
  });
};

  const getCartTotal = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getCartCount = () => {
    return cart.reduce((count, item) => count + item.quantity, 0);
  };

  return (
    <CartContext.Provider value={{
      cart,
      addToCart,
      removeFromCart,
      updateQuantity,
      clearCart,
      getCartTotal,
      getCartCount
    }}>
      {children}
    </CartContext.Provider>
  );
};
