import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import '../styles/Auth.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Register = () => {
  const navigate = useNavigate();
  const { login } = useAuth();

  const [step, setStep] = useState(1); // 1: formulario, 2: verificaci√≥n
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [verificationCode, setVerificationCode] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Paso 1: Enviar c√≥digo
  const handleSendCode = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');

    if (formData.password !== formData.confirmPassword) {
      setError('Las contrase√±as no coinciden');
      return;
    }

    if (formData.password.length < 6) {
      setError('La contrase√±a debe tener al menos 6 caracteres');
      return;
    }

    setLoading(true);

    try {
      await axios.post(`${API_URL}/auth/register/send-code`, {
        email: formData.email,
        name: formData.name
      });

      setMessage('‚úÖ C√≥digo enviado a tu email');
      setStep(2);
    } catch (err) {
      setError(err.response?.data?.message || 'Error al enviar c√≥digo');
    } finally {
      setLoading(false);
    }
  };

  // Paso 2: Verificar c√≥digo y registrar
  const handleVerifyCode = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post(`${API_URL}/auth/register/verify-code`, {
        email: formData.email,
        code: verificationCode,
        name: formData.name,
        password: formData.password
      });

      login(response.data.user, response.data.token);
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'C√≥digo inv√°lido');
    } finally {
      setLoading(false);
    }
  };

  const handleResendCode = async () => {
    setLoading(true);
    setError('');
    setMessage('');

    try {
      await axios.post(`${API_URL}/auth/register/send-code`, {
        email: formData.email,
        name: formData.name
      });

      setMessage('‚úÖ Nuevo c√≥digo enviado');
    } catch (err) {
      setError('Error al reenviar c√≥digo');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="container">
        <div className="auth-card">
          <h1>Crear Cuenta</h1>

          {step === 1 ? (
            /* PASO 1: Formulario de registro */
            <>
              {error && <div className="error-message">{error}</div>}
              {message && <div className="success-message">{message}</div>}

              <form onSubmit={handleSendCode}>
                <div className="form-group">
                  <label htmlFor="name">Nombre completo</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="password">Contrase√±a</label>
                  <input
                    type="password"
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    minLength="6"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="confirmPassword">Confirmar contrase√±a</label>
                  <input
                    type="password"
                    id="confirmPassword"
                    name="confirmPassword"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    required
                  />
                </div>

                <button type="submit" className="submit-btn" disabled={loading}>
                  {loading ? 'Enviando...' : 'Continuar'}
                </button>
              </form>
            </>
          ) : (
            /* PASO 2: Verificaci√≥n de c√≥digo */
            <>
              <div className="verification-step">
                <div className="verification-icon">üìß</div>
                <p>Hemos enviado un c√≥digo de verificaci√≥n a:</p>
                <strong>{formData.email}</strong>
                <p className="small-text">El c√≥digo expira en 10 minutos</p>
              </div>

              {error && <div className="error-message">{error}</div>}
              {message && <div className="success-message">{message}</div>}

              <form onSubmit={handleVerifyCode}>
                <div className="form-group">
                  <label htmlFor="code">C√≥digo de verificaci√≥n</label>
                  <input
                    type="text"
                    id="code"
                    value={verificationCode}
                    onChange={(e) => setVerificationCode(e.target.value)}
                    placeholder="Ingresa el c√≥digo de 6 d√≠gitos"
                    maxLength="6"
                    required
                    className="code-input"
                  />
                </div>

                <button type="submit" className="submit-btn" disabled={loading}>
                  {loading ? 'Verificando...' : 'Verificar y Registrarse'}
                </button>
              </form>

              <div className="resend-code">
                <p>¬øNo recibiste el c√≥digo?</p>
                <button 
                  type="button" 
                  className="link-button" 
                  onClick={handleResendCode}
                  disabled={loading}
                >
                  Reenviar c√≥digo
                </button>
              </div>

              <button 
                type="button" 
                className="back-button" 
                onClick={() => setStep(1)}
              >
                ‚Üê Volver
              </button>
            </>
          )}

          <p className="auth-link">
            ¬øYa tienes cuenta? <Link to="/login">Inicia sesi√≥n aqu√≠</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
