import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';
import '../../styles/AdminChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const SOCKET_URL = API_URL.replace('/api', '');

// Mantener el socket fuera del componente o usar un ref para evitar reconexiones infinitas
let socket = null;

const AdminChat = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState('');
  const [view, setView] = useState('open'); // 'open' o 'closed'
  const [connected, setConnected] = useState(false);
  const messagesEndRef = useRef(null);

  // Inicializar Socket una sola vez
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    if (!socket) {
      socket = io(SOCKET_URL, {
        transports: ['websocket', 'polling'],
        auth: { token } // Enviamos el token en la conexiÃ³n inicial
      });

      socket.on('connect', () => {
        console.log('âœ… Admin Socket Conectado');
        setConnected(true);
        socket.emit('authenticate', token);
      });

      socket.on('disconnect', () => setConnected(false));

      // ESCUCHAR NUEVOS MENSAJES (De cualquier usuario)
      socket.on('new_message_to_admin', (data) => {
        // data trae el chat actualizado
        updateChatsList(data.chat);
      });

      // ESCUCHAR CUANDO UN CHAT SE CIERRA
      socket.on('chat_closed_by_user', (data) => {
        updateChatsList(data.chat);
      });
    }

    loadChats();

    return () => {
      if (socket) {
        socket.off('new_message_to_admin');
        socket.off('chat_closed_by_user');
      }
    };
  }, [view]);

  // Scroll automÃ¡tico cuando hay mensajes nuevos
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [selectedChat?.messages]);

  // FunciÃ³n para actualizar la lista de chats en memoria sin refrescar
  const updateChatsList = (updatedChat) => {
    setChats(prevChats => {
      // Si el chat ya existe en la lista, lo actualizamos, si no, lo aÃ±adimos al principio
      const exists = prevChats.find(c => c._id === updatedChat._id);
      if (exists) {
        return prevChats.map(c => c._id === updatedChat._id ? updatedChat : c);
      }
      return [updatedChat, ...prevChats];
    });

    // Si el chat que recibimos es el que tenemos abierto, actualizar la vista de mensajes
    setSelectedChat(prevSelected => {
      if (prevSelected?._id === updatedChat._id) {
        return updatedChat;
      }
      return prevSelected;
    });
  };

  const loadChats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/chat/admin/chats?status=${view}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setChats(response.data || []);
    } catch (err) {
      console.error('Error al cargar chats:', err);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!message.trim() || !selectedChat) return;

    try {
      const token = localStorage.getItem('token');
      // Usamos el socket para enviar o el axios, pero el socket es mÃ¡s rÃ¡pido
      // Emitimos el evento de respuesta del admin
      socket.emit('admin_send_message', {
        chatId: selectedChat._id,
        text: message
      });

      setMessage('');
      // Nota: El socket del servidor deberÃ­a responder con un 'new_message_to_admin' 
      // y eso dispararÃ¡ la actualizaciÃ³n automÃ¡tica por el listener de arriba.
    } catch (err) {
      console.error('Error enviando mensaje:', err);
    }
  };

  const handleCloseChat = async (chatId) => {
    if (!window.confirm('Â¿Finalizar conversaciÃ³n?')) return;
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/chat/admin/close-chat/${chatId}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSelectedChat(null);
      loadChats();
    } catch (err) { alert('Error al cerrar'); }
  };

  return (
    <div className="admin-chat-container luxury-admin-panel">
      <header className="admin-chat-header">
        <h1>ðŸ’¬ Soporte SimShop <span className={`status-dot ${connected ? 'online' : 'offline'}`}></span></h1>
        <div className="view-selector">
          <button className={view === 'open' ? 'active' : ''} onClick={() => setView('open')}>Activos</button>
          <button className={view === 'closed' ? 'active' : ''} onClick={() => setView('closed')}>Historial</button>
        </div>
      </header>

      <div className="admin-chat-main">
        {/* LISTADO */}
        <aside className="chat-sidebar">
          {chats.map(chat => (
            <div 
              key={chat._id} 
              className={`chat-item ${selectedChat?._id === chat._id ? 'selected' : ''}`}
              onClick={() => setSelectedChat(chat)}
            >
              <div className="chat-item-info">
                <strong>{chat.user?.name}</strong>
                <p>{chat.messages[chat.messages.length - 1]?.text}</p>
              </div>
              {chat.messages.filter(m => m.sender === 'user' && !m.read).length > 0 && (
                <span className="unread-badge">!</span>
              )}
            </div>
          ))}
        </aside>

        {/* MENSAJES */}
        <section className="chat-area">
          {selectedChat ? (
            <>
              <div className="chat-area-header">
                <div>
                  <h3>{selectedChat.user?.name}</h3>
                  <small>{selectedChat.user?.email}</small>
                </div>
                {view === 'open' && (
                  <button className="btn-close-chat" onClick={() => handleCloseChat(selectedChat._id)}>Cerrar Chat</button>
                )}
              </div>

              <div className="messages-container">
                {selectedChat.messages.map((m, i) => (
                  <div key={i} className={`admin-msg-wrapper ${m.sender}`}>
                    <div className="msg-bubble">
                      {m.text}
                      <span className="msg-time">{new Date(m.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
                    </div>
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>

              {view === 'open' && (
                <form className="admin-input-form" onSubmit={sendMessage}>
                  <input 
                    value={message} 
                    onChange={e => setMessage(e.target.value)} 
                    placeholder="Escribe una respuesta..."
                  />
                  <button type="submit" disabled={!message.trim()}>Enviar</button>
                </form>
              )}
            </>
          ) : (
            <div className="no-chat-selected">Selecciona una conversaciÃ³n para empezar</div>
          )}
        </section>
      </div>
    </div>
  );
};

export default AdminChat;
