import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUser, FaBox, FaBell, FaLock, FaEdit, FaSave, FaTimes } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import '../styles/Profile.css';
import TrackingView from '../components/TrackingView';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Profile = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated, logout, login } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
    } else {
      setLoading(false);
    }
  }, [isAuthenticated, navigate]);

  if (loading) {
    return <div className="loading">Cargando perfil...</div>;
  }

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-header">
          <div className="user-avatar">
            {user?.avatar ? (
              <img src={user.avatar} alt={user.name} />
            ) : (
              <div className="avatar-placeholder">
                {user?.name?.charAt(0).toUpperCase()}
              </div>
            )}
          </div>
          <div className="user-info">
            <h1>{user?.name}</h1>
            <p>{user?.email}</p>
          </div>
        </div>

        <div className="profile-tabs">
          <button className={activeTab === 'profile' ? 'active' : ''} onClick={() => setActiveTab('profile')}><FaUser /> Mi Perfil</button>
          <button className={activeTab === 'orders' ? 'active' : ''} onClick={() => setActiveTab('orders')}><FaBox /> Mis Pedidos</button>
          <button className={activeTab === 'notifications' ? 'active' : ''} onClick={() => setActiveTab('notifications')}><FaBell /> Notificaciones</button>
          <button className={activeTab === 'security' ? 'active' : ''} onClick={() => setActiveTab('security')}><FaLock /> Seguridad</button>
        </div>

        <div className="profile-content">
          {activeTab === 'profile' && <ProfileTab user={user} login={login} />}
          {activeTab === 'orders' && <OrdersTab />}
          {activeTab === 'notifications' && <NotificationsTab />}
          {activeTab === 'security' && <SecurityTab />}
        </div>
      </div>
    </div>
  );
};

// --- TAB: Mis Pedidos (CORREGIDA) ---
const OrdersTab = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/orders/my-orders`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(response.data);
    } catch (error) {
      console.error('Error al cargar pedidos:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-ES', { style: 'currency', currency: 'EUR' }).format(price);
  };

  if (loading) return <div className="loading">Cargando pedidos...</div>;

  if (orders.length === 0) {
    return (
      <div className="empty-state">
        <FaBox size={64} />
        <h3>No tienes pedidos todavía</h3>
        <p>Cuando hagas un pedido, aparecerá aquí</p>
      </div>
    );
  }

  return (
    <div className="orders-tab">
      <h2>Mis Pedidos ({orders.length})</h2>
      <div className="orders-list">
        {orders.map(order => (
          <div key={order._id} className="order-card">
            <div className="order-header">
              <div>
                <h3>Pedido #{order._id.slice(-8).toUpperCase()}</h3>
                <p className="order-date">{new Date(order.createdAt).toLocaleDateString()}</p>
              </div>
              <div className="order-status">
                <span className={`status-badge ${order.orderStatus}`}>{order.orderStatus}</span>
              </div>
            </div>

            {/* SECCIÓN DE TRACKING INTEGRADA */}
            {(order.orderStatus !== 'pendiente' && order.orderStatus !== 'cancelado') && (
              <div className="order-tracking-inline">
                <TrackingView orderId={order._id} />
              </div>
            )}

            <div className="order-items">
              {order.items.map((item, index) => (
                <div key={index} className="order-item">
                  <img src={item.product?.images?.[0] || 'https://via.placeholder.com/60'} alt={item.product?.name} />
                  <div className="item-info">
                    <p>{item.product?.name}</p>
                    <small>Cantidad: {item.quantity}</small>
                  </div>
                  <p className="item-price">{formatPrice(item.price * item.quantity)}</p>
                </div>
              ))}
            </div>

            <div className="order-footer">
              <strong>Total: {formatPrice(order.totalAmount)}</strong>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// --- TAB: Mi Perfil ---
const ProfileTab = ({ user, login }) => {
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name || '',
    phone: user?.phone || '',
    street: user?.address?.street || '',
    city: user?.address?.city || '',
    postalCode: user?.address?.postalCode || '',
    country: user?.address?.country || 'España'
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSave = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.put(`${API_URL}/auth/profile`, {
        name: formData.name,
        phone: formData.phone,
        address: {
          street: formData.street,
          city: formData.city,
          postalCode: formData.postalCode,
          country: formData.country
        }
      }, { headers: { Authorization: `Bearer ${token}` } });

      login({ ...user, ...response.data }, token);
      setEditing(false);
      alert('Perfil actualizado correctamente');
    } catch (error) {
      alert('Error al actualizar perfil');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-tab">
      <div className="tab-header">
        <h2>Información Personal</h2>
        {!editing ? (
          <button className="btn-edit" onClick={() => setEditing(true)}><FaEdit /> Editar</button>
        ) : (
          <div className="edit-actions">
            <button className="btn-save" onClick={handleSave} disabled={loading}><FaSave /> Guardar</button>
            <button className="btn-cancel" onClick={() => setEditing(false)}><FaTimes /> Cancelar</button>
          </div>
        )}
      </div>
      <div className="profile-form">
        <div className="form-group">
          <label>Nombre completo</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} disabled={!editing} />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input type="email" value={user?.email} disabled />
        </div>
        <div className="form-group">
          <label>Teléfono</label>
          <input type="tel" name="phone" value={formData.phone} onChange={handleChange} disabled={!editing} />
        </div>
        <h3>Dirección</h3>
        <div className="form-group">
          <label>Calle</label>
          <input type="text" name="street" value={formData.street} onChange={handleChange} disabled={!editing} />
        </div>
        <div className="form-row">
          <div className="form-group">
            <label>Ciudad</label>
            <input type="text" name="city" value={formData.city} onChange={handleChange} disabled={!editing} />
          </div>
          <div className="form-group">
            <label>Código Postal</label>
            <input type="text" name="postalCode" value={formData.postalCode} onChange={handleChange} disabled={!editing} />
          </div>
        </div>
      </div>
    </div>
  );
};

// --- TAB: Notificaciones ---
const NotificationsTab = () => {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => { loadNotifications(); }, []);

  const loadNotifications = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/notifications`, { headers: { Authorization: `Bearer ${token}` } });
      setNotifications(response.data.notifications);
      setUnreadCount(response.data.unreadCount);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="notifications-tab">
      <h2>Notificaciones ({unreadCount} nuevas)</h2>
      <div className="notifications-list">
        {notifications.map(notif => (
          <div key={notif._id} className={`notification-item ${!notif.read ? 'unread' : ''}`}>
            <div className="notification-content">
              <h4>{notif.title}</h4>
              <p>{notif.message}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// --- TAB: Seguridad ---
const SecurityTab = () => {
  const [formData, setFormData] = useState({ currentPassword: '', newPassword: '', confirmPassword: '' });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      await axios.post(`${API_URL}/auth/change-password`, formData, { headers: { Authorization: `Bearer ${token}` } });
      alert('Contraseña actualizada');
    } catch (err) {
      alert('Error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="security-tab">
      <h2>Cambiar Contraseña</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Contraseña actual</label>
          <input type="password" name="currentPassword" onChange={(e) => setFormData({...formData, currentPassword: e.target.value})} required />
        </div>
        <div className="form-group">
          <label>Nueva contraseña</label>
          <input type="password" name="newPassword" onChange={(e) => setFormData({...formData, newPassword: e.target.value})} required />
        </div>
        <button type="submit" className="btn-save" disabled={loading}>Guardar</button>
      </form>
    </div>
  );
};

export default Profile;
