import React, { useEffect, useState, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";
import { FaUser, FaUserShield, FaTimes } from "react-icons/fa";
import "../styles/CustomerChat.css";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";
const SOCKET_URL = API_URL.replace("/api", "");

const socket = io("http://localhost:5000");


const CustomerChat = () => {
  const [chat, setChat] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState("");
  const messagesEndRef = useRef();

  useEffect(() => {
    const token = localStorage.getItem("token");
    socket.emit("auth", token);

    socket.on("chat_update_" + getUserId(), (updatedChat) => {
      setChat(updatedChat);
      scrollDown();
    });

    loadChat();
  }, []);

  const getUserId = () => {
    const user = JSON.parse(localStorage.getItem("user"));
    return user?._id;
  };

  const loadChat = async () => {
    const token = localStorage.getItem("token");
    if (!token) return;

    const res = await axios.get(`${API_URL}/chat/my-chat`, {
      headers: { Authorization: `Bearer ${token}` },
    });

    setChat(res.data);
  };

  const scrollDown = () =>
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });

  const sendMessage = () => {
    if (!message.trim()) return;

    socket.emit("sendMessage", { text: message });
    setMessage("");
  };

  const closeChat = () => {
    socket.emit("closeChatUser");
    setIsOpen(false);
  };

  return (
    <>
      <button className="chat-toggle-btn" onClick={() => setIsOpen(!isOpen)}>
        ðŸ’¬
      </button>

      {isOpen && (
        <div className="customer-chat-window">
          <div className="chat-window-header">
            <h3>Soporte</h3>
            <button onClick={closeChat}>
              <FaTimes />
            </button>
          </div>

          <div className="chat-window-messages">
            {chat?.messages.map((msg, i) => (
              <div
                key={i}
                className={`msg ${msg.sender === "user" ? "user" : "admin"}`}
              >
                <p>{msg.text}</p>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          <div className="chat-window-input">
            <input
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyPress={(e) => e.key === "Enter" && sendMessage()}
            />
            <button onClick={sendMessage}>Enviar</button>
          </div>
        </div>
      )}
    </>
  );
};

export default CustomerChat;
