import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FaUser, FaUserShield, FaTimes, FaSync, FaComments, FaCheckCircle } from 'react-icons/fa';
import '../../styles/AdminChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const AdminChat = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);
  const [currentAdmin, setCurrentAdmin] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [filter, setFilter] = useState('open'); // 'open', 'closed', 'all'
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Obtener informaci√≥n del admin actual
    const userStr = localStorage.getItem('user');
    if (userStr) {
      const user = JSON.parse(userStr);
      setCurrentAdmin(user);
    }

    loadChats();

    // Actualizaci√≥n autom√°tica cada 2 segundos
    const interval = setInterval(() => {
      loadChats(true); // silent = true (no mostrar loading)
    }, 2000);

    return () => clearInterval(interval);
  }, [filter]);

  useEffect(() => {
    scrollToBottom();
  }, [selectedChat]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadChats = async (silent = false) => {
    try {
      if (!silent) setLoading(true);
      
      const token = localStorage.getItem('token');
      const statusParam = filter === 'all' ? '' : `?status=${filter}`;
      
      const response = await axios.get(`${API_URL}/chat/admin/chats${statusParam}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setChats(response.data);
      setLastUpdate(new Date());

      // Actualizar chat seleccionado si existe
      if (selectedChat) {
        const updatedSelected = response.data.find(c => c._id === selectedChat._id);
        if (updatedSelected) {
          setSelectedChat(updatedSelected);
        } else if (filter === 'open') {
          // Si el chat seleccionado fue cerrado, deseleccionarlo
          setSelectedChat(null);
        }
      }
    } catch (error) {
      console.error('Error al cargar chats:', error);
    } finally {
      if (!silent) setLoading(false);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!message.trim() || !selectedChat || sending) return;

    try {
      setSending(true);
      const token = localStorage.getItem('token');
      
      await axios.post(
        `${API_URL}/chat/admin/send-message/${selectedChat._id}`,
        { text: message },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage('');
      await loadChats(true);
      scrollToBottom();
    } catch (error) {
      console.error('Error al enviar mensaje:', error);
      alert('Error al enviar mensaje');
    } finally {
      setSending(false);
    }
  };

  const closeChat = async (chatId) => {
    if (!window.confirm('¬øFinalizar esta conversaci√≥n?')) return;

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `${API_URL}/chat/admin/close-chat/${chatId}`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      alert('‚úÖ Chat finalizado correctamente');
      setSelectedChat(null);
      await loadChats();
    } catch (error) {
      console.error('Error al finalizar chat:', error);
      alert('Error al finalizar chat');
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('es-ES', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return 'Hoy';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return 'Ayer';
    } else {
      return date.toLocaleDateString('es-ES', { 
        day: 'numeric', 
        month: 'short' 
      });
    }
  };

  const getUnreadCount = (chat) => {
    return chat.messages.filter(m => 
      m.sender === 'user' && !m.read
    ).length;
  };

  // Contar chats por estado
  const chatStats = {
    total: chats.length,
    open: chats.filter(c => c.status === 'open').length,
    closed: chats.filter(c => c.status === 'closed').length,
    unread: chats.reduce((sum, chat) => sum + getUnreadCount(chat), 0)
  };

  if (loading && chats.length === 0) {
    return (
      <div className="admin-chat-container">
        <div className="loading-state">
          <div className="spinner"></div>
          <p>Cargando conversaciones...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-chat-container">
      {/* Header */}
      <div className="chat-header-main">
        <div className="header-left">
          <h1>üí¨ Chat de Atenci√≥n al Cliente</h1>
          <p className="header-subtitle">
            Respondiendo como: <strong>{currentAdmin?.name || 'Administrador'}</strong>
            {lastUpdate && (
              <span className="last-update">
                {' ‚Ä¢ '} Actualizado: {formatTime(lastUpdate)}
              </span>
            )}
          </p>
        </div>
        <div className="header-actions">
          <button 
            className="btn btn-secondary btn-refresh"
            onClick={() => loadChats()}
            disabled={loading}
          >
            <FaSync className={loading ? 'spinning' : ''} /> Actualizar
          </button>
        </div>
      </div>

      {/* Filtros y estad√≠sticas */}
      <div className="chat-filters">
        <div className="filter-buttons">
          <button 
            className={`filter-btn ${filter === 'open' ? 'active' : ''}`}
            onClick={() => setFilter('open')}
          >
            <FaComments /> Activos ({chatStats.open})
          </button>
          <button 
            className={`filter-btn ${filter === 'closed' ? 'active' : ''}`}
            onClick={() => setFilter('closed')}
          >
            <FaCheckCircle /> Cerrados ({chatStats.closed})
          </button>
          <button 
            className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
            onClick={() => setFilter('all')}
          >
            Todos ({chatStats.total})
          </button>
        </div>
        
        {chatStats.unread > 0 && (
          <div className="unread-alert">
            <span>üîî {chatStats.unread} mensaje(s) sin leer</span>
          </div>
        )}
      </div>

      <div className="chat-layout">
        {/* Lista de chats */}
        <div className="chats-list">
          <div className="chats-list-header">
            <h3>
              {filter === 'open' && 'üìã Conversaciones Activas'}
              {filter === 'closed' && '‚úÖ Conversaciones Cerradas'}
              {filter === 'all' && 'üìÇ Todas las Conversaciones'}
            </h3>
            <span className="badge-count">{chats.length}</span>
          </div>
          
          {chats.length === 0 ? (
            <div className="no-chats">
              <div className="no-chats-icon">
                {filter === 'open' && 'üí¨'}
                {filter === 'closed' && '‚úÖ'}
                {filter === 'all' && 'üì≠'}
              </div>
              <p>
                {filter === 'open' && 'No hay chats activos'}
                {filter === 'closed' && 'No hay chats cerrados'}
                {filter === 'all' && 'No hay conversaciones'}
              </p>
              <small>
                {filter === 'open' && 'Las nuevas conversaciones aparecer√°n aqu√≠'}
                {filter === 'closed' && 'Los chats finalizados aparecer√°n aqu√≠'}
                {filter === 'all' && 'A√∫n no hay ninguna conversaci√≥n'}
              </small>
            </div>
          ) : (
            <div className="chats-list-items">
              {chats.map(chat => {
                const lastMessage = chat.messages[chat.messages.length - 1];
                const unreadCount = getUnreadCount(chat);
                const isClosed = chat.status === 'closed';
                
                return (
                  <div
                    key={chat._id}
                    className={`chat-item ${selectedChat?._id === chat._id ? 'active' : ''} ${unreadCount > 0 ? 'has-unread' : ''} ${isClosed ? 'closed' : ''}`}
                    onClick={() => setSelectedChat(chat)}
                  >
                    <div className="chat-item-avatar">
                      <FaUser />
                      {unreadCount > 0 && (
                        <span className="unread-badge">{unreadCount}</span>
                      )}
                      {isClosed && (
                        <span className="closed-badge">‚úì</span>
                      )}
                    </div>
                    
                    <div className="chat-item-content">
                      <div className="chat-item-header">
                        <strong className="chat-item-name">
                          {chat.user?.name || 'Usuario'}
                        </strong>
                        <span className="chat-item-time">
                          {formatDate(lastMessage?.timestamp)}
                        </span>
                      </div>
                      
                      <div className="chat-item-preview">
                        <span className={`message-sender ${lastMessage?.sender === 'admin' ? 'you' : 'them'}`}>
                          {lastMessage?.sender === 'admin' ? 'T√∫: ' : ''}
                        </span>
                        <span className="message-text">
                          {lastMessage?.text.substring(0, 50)}
                          {lastMessage?.text.length > 50 ? '...' : ''}
                        </span>
                      </div>
                      
                      {isClosed && (
                        <div className="chat-status-tag">
                          <span className="status-closed">Cerrado</span>
                        </div>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>

        {/* Conversaci√≥n */}
        <div className="chat-conversation">
          {selectedChat ? (
            <>
              {/* Header del chat */}
              <div className="conversation-header">
                <div className="conversation-user-info">
                  <div className="user-avatar-large">
                    <FaUser />
                  </div>
                  <div className="user-details">
                    <h3>{selectedChat.user?.name || 'Usuario'}</h3>
                    <p className="user-email">{selectedChat.user?.email || 'Sin email'}</p>
                    <span className={`chat-status-badge ${selectedChat.status === 'open' ? 'open' : 'closed'}`}>
                      {selectedChat.status === 'open' ? '‚óè Activo' : '‚úì Cerrado'}
                    </span>
                  </div>
                </div>
                
                <div className="conversation-actions">
                  {selectedChat.status === 'open' && (
                    <button 
                      onClick={() => closeChat(selectedChat._id)} 
                      className="btn-close-chat"
                      title="Finalizar conversaci√≥n"
                    >
                      <FaTimes /> Finalizar
                    </button>
                  )}
                </div>
              </div>

              {/* Mensajes */}
              <div className="messages-container">
                <div className="messages-wrapper">
                  {selectedChat.messages.map((msg, index) => {
                    const isAdmin = msg.sender === 'admin';
                    
                    return (
                      <div 
                        key={index} 
                        className={`message-bubble-wrapper ${isAdmin ? 'admin-message' : 'user-message'}`}
                      >
                        <div className="message-avatar">
                          {isAdmin ? (
                            <div className="avatar admin-avatar">
                              <FaUserShield />
                            </div>
                          ) : (
                            <div className="avatar user-avatar">
                              <FaUser />
                            </div>
                          )}
                        </div>

                        <div className="message-content-wrapper">
                          <div className="message-sender-label">
                            {isAdmin ? (
                              <span className="sender-name admin">
                                üõ°Ô∏è {currentAdmin?.name || 'Soporte'}
                              </span>
                            ) : (
                              <span className="sender-name user">
                                üë§ {selectedChat.user?.name || 'Cliente'}
                              </span>
                            )}
                          </div>

                          <div className={`message-bubble ${isAdmin ? 'admin' : 'user'}`}>
                            <p className="message-text">{msg.text}</p>
                            <div className="message-footer">
                              <span className="message-time">
                                {formatTime(msg.timestamp)}
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                  <div ref={messagesEndRef} />
                </div>
              </div>

              {/* Input de mensaje */}
              {selectedChat.status === 'open' ? (
                <div className="message-input-container">
                  <form onSubmit={sendMessage} className="input-wrapper">
                    <input
                      type="text"
                      value={message}
                      onChange={(e) => setMessage(e.target.value)}
                      placeholder={`Responder a ${selectedChat.user?.name || 'usuario'}...`}
                      disabled={sending}
                      className="message-input"
                    />
                    <button 
                      type="submit"
                      disabled={sending || !message.trim()}
                      className={`btn-send ${message.trim() ? 'active' : ''}`}
                    >
                      {sending ? '‚è≥' : '‚úàÔ∏è'} Enviar
                    </button>
                  </form>
                  
                  <div className="input-help">
                    <small>
                      üí° Presiona Enter para enviar
                    </small>
                  </div>
                </div>
              ) : (
                <div className="chat-closed-footer">
                  <p>‚úì Esta conversaci√≥n est√° cerrada</p>
                </div>
              )}
            </>
          ) : (
            <div className="no-chat-selected">
              <div className="no-chat-icon">üí¨</div>
              <h3>Selecciona una conversaci√≥n</h3>
              <p>Elige un chat de la lista para ver los mensajes</p>
              {chats.length === 0 && (
                <div className="empty-state-tip">
                  <p>üì± Los clientes pueden iniciar chats desde el widget flotante</p>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminChat;
