import React, { useState, useEffect } from 'react';
import { FaUser, FaEnvelope, FaPhone, FaMapMarkerAlt, FaEdit, FaCheck, FaTimes } from 'react-icons/fa';

const ProfileTab = ({ user, login }) => {
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '', email: '', phone: '', city: ''
  });

  useEffect(() => {
    if (user) {
      setFormData({
        name: user.name || '',
        email: user.email || '',
        phone: user.phone || '',
        city: user.address?.city || '',
      });
    }
  }, [user]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="profile-details-container">
      <div className="tab-status-bar">
        <span className="status-tag">Datos Personales</span>
        {!editing ? (
          <button className="edit-trigger-link" onClick={() => setEditing(true)}>
            <FaEdit /> Modificar perfil
          </button>
        ) : (
          <div className="edit-confirm-group">
            <button className="btn-save-pill" onClick={() => setEditing(false)}>
              <FaCheck /> Guardar cambios
            </button>
            <button className="btn-cancel-icon" onClick={() => setEditing(false)}>
              <FaTimes />
            </button>
          </div>
        )}
      </div>

      <div className={`luxury-grid ${editing ? 'edit-active' : ''}`}>
        {/* Campo Nombre */}
        <div className="luxury-input-group">
          <label><FaUser className="label-icon" /> Nombre</label>
          <input 
            type="text" 
            name="name" 
            value={formData.name} 
            onChange={handleChange} 
            disabled={!editing}
            placeholder="Introduce tu nombre"
          />
        </div>

        {/* Campo Email */}
        <div className="luxury-input-group">
          <label><FaEnvelope className="label-icon" /> Correo Electrónico</label>
          <input 
            type="email" 
            name="email" 
            value={formData.email} 
            disabled 
            className="readonly-input"
          />
        </div>

        {/* Campo Teléfono */}
        <div className="luxury-input-group">
          <label><FaPhone className="label-icon" /> Teléfono</label>
          <input 
            type="text" 
            name="phone" 
            value={formData.phone} 
            onChange={handleChange} 
            disabled={!editing}
            placeholder="Sin teléfono registrado"
          />
        </div>

        {/* Campo Ciudad */}
        <div className="luxury-input-group">
          <label><FaMapMarkerAlt className="label-icon" /> Ubicación</label>
          <input 
            type="text" 
            name="city" 
            value={formData.city} 
            onChange={handleChange} 
            disabled={!editing}
            placeholder="Ciudad"
          />
        </div>
      </div>
    </div>
  );
};

export default ProfileTab;
