import React, { useState, useEffect, useRef } from 'react';
import { FaEye, FaEdit, FaPhone, FaTruck, FaFilePdf, FaSync, FaPrint, FaCheckCircle } from 'react-icons/fa';
import axios from 'axios';
import '../../styles/AdminOrders.css';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true); // Solo para la primera carga
  const [refreshing, setRefreshing] = useState(false); // Para feedback visual opcional
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [statusUpdate, setStatusUpdate] = useState({ orderStatus: '', paymentStatus: '' });
  const [error, setError] = useState(null);

  // Ref para evitar duplicidad en alertas de impresi√≥n
  const notifiedOrders = useRef(new Set());

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  // 1. CARGA INICIAL Y CONFIGURACI√ìN DE INTERVALO
  useEffect(() => {
    loadOrders(true); // true indica que es la carga inicial (con spinner)

    const interval = setInterval(() => {
      loadOrders(false); // false indica carga silenciosa (sin spinner)
    }, 30000);

    return () => clearInterval(interval);
  }, []);

  // 2. MONITOR DE IMPRESI√ìN (Solo pedidos nuevos)
  useEffect(() => {
    const pendingToPrint = orders.find(o => 
      o.needsPrinting === true && 
      o.orderStatus === 'procesando' &&
      !notifiedOrders.current.has(o._id) // Si no ha sido notificado en esta sesi√≥n
    );

    if (pendingToPrint) {
      notifiedOrders.current.add(pendingToPrint._id);
      handleAutoPrintPrompt(pendingToPrint);
    }
  }, [orders]);

  const loadOrders = async (isInitialLoad = false) => {
    try {
      if (isInitialLoad) setLoading(true);
      setRefreshing(true);
      setError(null);

      const token = localStorage.getItem('token');
      if (!token) {
        setError('No est√°s autenticado.');
        setLoading(false);
        return;
      }

      const response = await axios.get(`${API_URL}/orders`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      const ordersData = Array.isArray(response.data) ? response.data : [];
      setOrders(ordersData);
    } catch (error) {
      console.error('‚ùå Error:', error);
      if (isInitialLoad) setError('Error al cargar pedidos');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  // --- FUNCIONES DE ETIQUETA ---
  const handleAutoPrintPrompt = (order) => {
    const audio = new Audio('https://assets.mixkit.co/active_storage/sfx/2358/2358-preview.mp3');
    audio.play().catch(() => {});

    // Usamos setTimeout para que el confirm no bloquee el hilo principal inmediatamente
    setTimeout(() => {
        if (window.confirm(`üì¶ ¬°PEDIDO NUEVO! #${order._id.slice(-6).toUpperCase()}\n¬øDeseas imprimir la etiqueta ahora?`)) {
          const tId = order.tracking?._id || order.tracking;
          if (tId) {
            downloadLabel(tId);
            markAsPrinted(order._id);
          }
        }
    }, 500);
  };

  const downloadLabel = (trackingId) => {
    const token = localStorage.getItem('token');
    window.open(`${API_URL}/tracking/label/${trackingId}?token=${token}`, '_blank');
  };

  const markAsPrinted = async (orderId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/orders/${orderId}/printed`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      loadOrders(false); // Refresco silencioso tras marcar
    } catch (err) {
      console.error("Error al marcar como impreso", err);
    }
  };

  // ... (Resto de funciones: handleCreateTracking, updateOrderStatus permanecen igual)

  const handleCreateTracking = async (orderId) => {
    const carrier = prompt("Introduce el transportista:", "SimShip Express");
    if (!carrier) return;
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_URL}/tracking/create`, { orderId, carrier }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      loadOrders(false);
    } catch (err) {
      alert("Error al generar tracking");
    }
  };

  const updateOrderStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/orders/${selectedOrder._id}/status`, statusUpdate, {
        headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' }
      });
      closeDetailModal();
      loadOrders(false);
    } catch (error) {
      alert(error.response?.data?.message || 'Error al actualizar');
    }
  };

  // RENDERIZADO
  if (loading) return (
    <div className="admin-orders">
      <div className="loading-container">
        <div className="spinner-large"></div>
        <p>Iniciando panel de gesti√≥n...</p>
      </div>
    </div>
  );

  return (
    <div className="admin-orders">
      <div className="page-header">
        <h2>Gesti√≥n de Pedidos {refreshing && <small className="refresh-indicator">üîÑ Actualizando...</small>}</h2>
        <div className="orders-stats">
          <span className="stat-item">Total: <strong>{orders.length}</strong></span>
          <span className="stat-item">Pendientes: <strong>{orders.filter(o => o.orderStatus === 'pendiente').length}</strong></span>
        </div>
        <button className="btn btn-secondary" onClick={() => loadOrders(true)}>
            <FaSync className={refreshing ? 'spin' : ''} /> Refrescar
        </button>
      </div>

      {/* El resto del JSX de tu tabla y modal se mantiene igual */}
      <div className="table-responsive">
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID Pedido</th>
              <th>Cliente</th>
              <th>Total</th>
              <th>Estado Pago</th>
              <th>Estado Pedido</th>
              <th>Log√≠stica</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id} className={order.needsPrinting ? 'row-urgent' : ''}>
                <td><strong>#{order._id.slice(-8)}</strong></td>
                <td>
                  <div className="customer-cell">
                    <strong>{order.user?.name || 'Usuario'}</strong>
                    <small><FaPhone /> {order.phone || 'S/T'}</small>
                  </div>
                </td>
                <td><strong>{new Intl.NumberFormat('es-ES', { style: 'currency', currency: 'EUR' }).format(order.totalAmount)}</strong></td>
                <td><span className={`badge badge-${order.paymentStatus}`}>{order.paymentStatus}</span></td>
                <td><span className={`badge badge-${order.orderStatus}`}>{order.orderStatus}</span></td>
                <td>
                  {!order.tracking ? (
                    <button className="btn-table btn-generate" onClick={() => handleCreateTracking(order._id)}>
                      <FaTruck /> Enviar
                    </button>
                  ) : (
                    <button className="btn-table btn-pdf" onClick={() => downloadLabel(order.tracking._id || order.tracking)}>
                      <FaPrint /> Etiqueta
                    </button>
                  )}
                </td>
                <td>
                    <button className="btn-icon btn-view" onClick={() => {
                        setSelectedOrder(order);
                        setStatusUpdate({ orderStatus: order.orderStatus, paymentStatus: order.paymentStatus });
                        setShowDetailModal(true);
                    }}>
                        <FaEye />
                    </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {/* ... Incluir modal aqu√≠ ... */}
    </div>
  );
};

export default AdminOrders;
