import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Auth.css';

const Login = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { login, isAuthenticated, user, loading: authLoading } = useAuth();

  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Si ya est√° autenticado, redirigir
  useEffect(() => {
    if (!authLoading && isAuthenticated()) {
      console.log('‚úÖ [LOGIN] Ya autenticado, redirigiendo...');

      // Redirigir seg√∫n el rol
      if (user?.role === 'admin') {
        navigate('/admin', { replace: true });
      } else {
        const from = location.state?.from?.pathname || '/';
        navigate(from, { replace: true });
      }
    }
  }, [authLoading, isAuthenticated, user, navigate, location]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });

    // Limpiar error cuando el usuario empiece a escribir
    if (error) {
      setError('');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones b√°sicas
    if (!formData.email || !formData.password) {
      setError('Por favor completa todos los campos');
      return;
    }

    setError('');
    setLoading(true);

    console.log('üîµ [LOGIN] Iniciando proceso de login...');
    console.log('üîµ [LOGIN] Email:', formData.email);

    try {
      // Usar la funci√≥n login del AuthContext
      const result = await login(formData.email, formData.password);

      console.log('üîµ [LOGIN] Resultado del login:', result);

      if (result.success) {
        console.log('‚úÖ [LOGIN] Login exitoso');
        console.log('‚úÖ [LOGIN] Usuario:', result.user);

        // Peque√±o delay para asegurar que el estado se actualice
        setTimeout(() => {
          // Redirigir seg√∫n el rol
          if (result.user?.role === 'admin') {
            console.log('üîµ [LOGIN] Redirigiendo a panel admin...');
            navigate('/admin', { replace: true });
          } else {
            const from = location.state?.from?.pathname || '/';
            console.log('üîµ [LOGIN] Redirigiendo a:', from);
            navigate(from, { replace: true });
          }
        }, 100);
      } else {
        console.log('‚ùå [LOGIN] Login fallido:', result.message);
        setError(result.message || 'Error al iniciar sesi√≥n');
      }
    } catch (err) {
      console.error('‚ùå [LOGIN] Error inesperado:', err);
      setError('Error inesperado al iniciar sesi√≥n. Por favor, intenta de nuevo.');
    } finally {
      setLoading(false);
    }
  };

  // Mostrar loading mientras verifica autenticaci√≥n
  if (authLoading) {
    return (
      <div className="auth-page">
        <div className="container">
          <div className="loading-screen">
            <div className="spinner"></div>
            <p>Verificando sesi√≥n...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="auth-page">
      <div className="container">
        <div className="auth-card">
          <div className="auth-header">
            <h1>Iniciar Sesi√≥n</h1>
            <p>Accede a tu cuenta de SimShop</p>
          </div>

          {error && (
            <div className="error-message">
              <span>‚ö†Ô∏è</span>
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} className="auth-form">
            <div className="form-group">
              <label htmlFor="email">
                Email
                <span className="required">*</span>
              </label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="tu@email.com"
                required
                autoComplete="email"
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">
                Contrase√±a
                <span className="required">*</span>
              </label>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                required
                autoComplete="current-password"
                disabled={loading}
                minLength={6}
              />
            </div>

            <button 
              type="submit" 
              className="submit-btn" 
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="btn-spinner"></span>
                  Iniciando sesi√≥n...
                </>
              ) : (
                'Iniciar Sesi√≥n'
              )}
            </button>
          </form>

          <div className="auth-footer">
            <p className="auth-link">
              ¬øNo tienes cuenta? <Link to="/registro">Reg√≠strate aqu√≠</Link>
            </p>
            <p className="auth-link">
              <Link to="/recuperar-password">¬øOlvidaste tu contrase√±a?</Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
