import React, { useEffect, useState } from "react";
import { io } from "socket.io-client";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";
const SOCKET_URL = API_URL.replace("/api", "");
const socket = io(SOCKET_URL);

const AdminChat = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState("");

 useEffect(() => {
  const token = localStorage.getItem("token");
  socket.emit("auth", token);

  socket.on("admin_newMessage", loadChats);
  socket.on("admin_chatUpdated", loadChats);
}, []);


  const loadChats = async () => {
    const token = localStorage.getItem("token");

    const res = await axios.get(`${API_URL}/chat/admin/chats?status=open`, {
      headers: { Authorization: `Bearer ${token}` },
    });

    setChats(res.data);
  };

  const sendMessage = () => {
    socket.emit("adminSendMessage", {
      chatId: selectedChat._id,
      text: message,
    });

    setMessage("");
  };

  const closeChat = () => {
    socket.emit("closeChatAdmin", { chatId: selectedChat._id });
    setSelectedChat(null);
  };

  return (
    <div className="admin-chat-layout">

      <div className="chat-list">
        {chats.map((chat) => (
          <div key={chat._id} onClick={() => setSelectedChat(chat)}>
            {chat.user.name}
          </div>
        ))}
      </div>

      <div className="chat-box">
        {selectedChat ? (
          <>
            <button onClick={closeChat}>Cerrar chat</button>

            <div className="messages">
              {selectedChat.messages.map((msg, i) => (
                <p key={i}>
                  <b>{msg.sender}:</b> {msg.text}
                </p>
              ))}
            </div>

            <input
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyPress={(e) => e.key === "Enter" && sendMessage()}
            />

            <button onClick={sendMessage}>Enviar</button>
          </>
        ) : (
          <p>Selecciona un chat</p>
        )}
      </div>

    </div>
  );
};

export default AdminChat;
