import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, PaymentElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import '../styles/Checkout.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const stripeKey = process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY;
const stripePromise = stripeKey ? loadStripe(stripeKey) : null;

// Validar c√≥digo postal espa√±ol
const validarCodigoPostal = (cp) => {
  const regex = /^(?:0[1-9]|[1-4]\d|5[0-2])\d{3}$/;
  return regex.test(cp);
};

const provinciasEspana = [
  '√Ålava', 'Albacete', 'Alicante', 'Almer√≠a', 'Asturias', '√Åvila', 'Badajoz', 
  'Barcelona', 'Burgos', 'C√°ceres', 'C√°diz', 'Cantabria', 'Castell√≥n', 'Ciudad Real',
  'C√≥rdoba', 'Cuenca', 'Girona', 'Granada', 'Guadalajara', 'Guip√∫zcoa', 'Huelva',
  'Huesca', 'Islas Baleares', 'Ja√©n', 'La Coru√±a', 'La Rioja', 'Las Palmas', 'Le√≥n',
  'Lleida', 'Lugo', 'Madrid', 'M√°laga', 'Murcia', 'Navarra', 'Ourense', 'Palencia',
  'Pontevedra', 'Salamanca', 'Segovia', 'Sevilla', 'Soria', 'Tarragona', 
  'Santa Cruz de Tenerife', 'Teruel', 'Toledo', 'Valencia', 'Valladolid', 
  'Vizcaya', 'Zamora', 'Zaragoza'
];

// Componente de pago Stripe
const StripePaymentForm = ({ onSuccess }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return;

    setLoading(true);
    setError('');

    try {
      const { error: stripeError, paymentIntent } = await stripe.confirmPayment({
        elements,
        redirect: 'if_required'
      });

      if (stripeError) {
        setError(stripeError.message);
        setLoading(false);
        return;
      }

      if (paymentIntent && paymentIntent.status === 'succeeded') {
        onSuccess(paymentIntent.id);
      }
    } catch (err) {
      console.error('Error:', err);
      setError('Error al procesar el pago');
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="payment-form">
      <PaymentElement />
      {error && <div className="error-message">{error}</div>}
      <button 
        type="submit" 
        className="btn-finalizar-compra" 
        disabled={!stripe || loading}
      >
        {loading ? 'Procesando...' : 'üí≥ Pagar Ahora'}
      </button>
      <div className="secure-payment">üîí Pago 100% seguro con Stripe</div>
    </form>
  );
};

const Checkout = () => {
  const navigate = useNavigate();
  const { cart, getCartTotal, clearCart } = useCart();
  const { user, isAuthenticated } = useAuth();

  const [paymentMethod, setPaymentMethod] = useState('stripe');
  const [clientSecret, setClientSecret] = useState('');
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    street: '',
    city: '',
    province: '',
    postalCode: '',
    phone: '',
    notes: ''
  });

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login', { state: { from: { pathname: '/checkout' } } });
      return;
    }

    if (!cart || cart.length === 0) {
      navigate('/carrito');
      return;
    }
  }, [isAuthenticated, cart, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.street.trim()) newErrors.street = 'Direcci√≥n obligatoria';
    if (!formData.city.trim()) newErrors.city = 'Ciudad obligatoria';
    if (!formData.province) newErrors.province = 'Selecciona provincia';

    if (!formData.postalCode) {
      newErrors.postalCode = 'C√≥digo postal obligatorio';
    } else if (!validarCodigoPostal(formData.postalCode)) {
      newErrors.postalCode = 'C√≥digo postal inv√°lido';
    }

    // ‚úÖ VALIDACI√ìN DE TEL√âFONO MEJORADA
    if (!formData.phone.trim()) {
      newErrors.phone = 'Tel√©fono obligatorio';
    } else {
      // Validar formato espa√±ol: 9 d√≠gitos comenzando por 6, 7, 8 o 9
      const phoneRegex = /^[6-9]\d{8}$/;
      if (!phoneRegex.test(formData.phone)) {
        newErrors.phone = 'Tel√©fono inv√°lido (9 d√≠gitos, ej: 612345678)';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const createPaymentIntent = async () => {
    if (!validateForm()) {
      alert('Completa todos los campos correctamente');
      return false;
    }

    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const response = await axios.post(
        `${API_URL}/stripe/create-payment-intent`,
        {
          items: cart.map(item => ({
            product: item._id,
            quantity: item.quantity,
            price: item.price,
            name: item.name // A√±adir nombre del producto
          })),
          totalAmount: getCartTotal(),
          // ‚úÖ A√ëADIR INFORMACI√ìN DEL USUARIO
          shippingInfo: {
            name: user?.name || 'Cliente',
            email: user?.email || '',
            phone: formData.phone, // ‚úÖ IMPORTANTE
            address: {
              street: formData.street,
              city: formData.city,
              province: formData.province,
              postalCode: formData.postalCode,
              country: 'Espa√±a'
            }
          }
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setClientSecret(response.data.clientSecret);
      return true;
    } catch (error) {
      console.error('Error:', error);
      alert(error.response?.data?.error || 'Error al inicializar pago');
      return false;
    } finally {
      setLoading(false);
    }
  };

  const handlePaymentMethodChange = async (method) => {
    setPaymentMethod(method);
    if (method === 'stripe' && stripePromise) {
      await createPaymentIntent();
    }
  };

  // ‚úÖ FUNCI√ìN CORREGIDA - INCLUYE PHONE
  const handleStripeSuccess = async (paymentIntentId) => {
    try {
      const token = localStorage.getItem('token');

      console.log('Confirmando pago con datos:', {
        paymentIntentId,
        phone: formData.phone,
        items: cart.length
      });

      const response = await axios.post(
        `${API_URL}/stripe/confirm-payment`,
        {
          paymentIntentId,
          // ‚úÖ ENVIAR ITEMS COMPLETOS
          cartItems: cart.map(item => ({
            product: item._id,
            productId: item._id,
            quantity: item.quantity,
            price: item.price,
            name: item.name
          })),
          totalAmount: getCartTotal(),
          // ‚úÖ INFORMACI√ìN DE ENV√çO COMPLETA
          shippingInfo: {
            name: user?.name || 'Cliente',
            email: user?.email || '',
            phone: formData.phone, // ‚úÖ CR√çTICO - INCLUIR PHONE
            address: {
              street: formData.street,
              city: formData.city,
              province: formData.province,
              postalCode: formData.postalCode,
              country: 'Espa√±a'
            }
          },
          // Tambi√©n enviar campos individuales por compatibilidad
          shippingAddress: {
            street: formData.street,
            city: formData.city,
            province: formData.province,
            postalCode: formData.postalCode,
            country: 'Espa√±a'
          },
          phone: formData.phone, // ‚úÖ A√ëADIR AQU√ç TAMBI√âN
          notes: formData.notes
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log('‚úÖ Pedido confirmado:', response.data);

      clearCart();
      alert('¬°Pago realizado con √©xito!');
      navigate('/perfil');
    } catch (error) {
      console.error('‚ùå Error al confirmar pedido:', error);

      // Mostrar error espec√≠fico
      const errorMessage = error.response?.data?.error || 
                          error.response?.data?.details || 
                          'Error al confirmar pedido';

      alert(errorMessage);

      // Si falta el tel√©fono, resaltar el error
      if (errorMessage.includes('phone')) {
        setErrors(prev => ({ ...prev, phone: 'El tel√©fono es requerido' }));
      }
    }
  };

  const handleOtherPaymentMethod = async () => {
    if (!validateForm()) {
      alert('Completa todos los campos');
      return;
    }

    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      await axios.post(
        `${API_URL}/orders`,
        {
          items: cart.map(item => ({
            product: item._id,
            quantity: item.quantity,
            price: item.price,
            name: item.name
          })),
          totalAmount: paymentMethod === 'contrareembolso' ? getCartTotal() + 3 : getCartTotal(),
          shippingAddress: {
            street: formData.street,
            city: formData.city,
            province: formData.province,
            postalCode: formData.postalCode,
            country: 'Espa√±a'
          },
          name: user?.name || 'Cliente',
          email: user?.email || '',
          phone: formData.phone, // ‚úÖ INCLUIR PHONE
          paymentMethod,
          notes: formData.notes
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      clearCart();
      alert('¬°Pedido realizado con √©xito!');
      navigate('/perfil');
    } catch (error) {
      console.error('Error:', error);
      alert(error.response?.data?.error || 'Error al crear pedido');
    } finally {
      setLoading(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-ES', {
      style: 'currency',
      currency: 'EUR'
    }).format(price);
  };

  if (!cart || cart.length === 0) {
    return null;
  }

  return (
    <div className="checkout-page">
      <div className="container">
        <h1>Finalizar Compra</h1>

        <div className="checkout-layout">
          <div className="checkout-main">
            {/* Direcci√≥n */}
            <div className="checkout-section">
              <h2>üìç Direcci√≥n de Env√≠o</h2>

              <div className="form-group">
                <label>Direcci√≥n *</label>
                <input
                  type="text"
                  name="street"
                  value={formData.street}
                  onChange={handleInputChange}
                  placeholder="Calle, n√∫mero, piso..."
                  className={errors.street ? 'error' : ''}
                />
                {errors.street && <span className="error-text">{errors.street}</span>}
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Ciudad *</label>
                  <input
                    type="text"
                    name="city"
                    value={formData.city}
                    onChange={handleInputChange}
                    className={errors.city ? 'error' : ''}
                  />
                  {errors.city && <span className="error-text">{errors.city}</span>}
                </div>

                <div className="form-group">
                  <label>C√≥digo Postal *</label>
                  <input
                    type="text"
                    name="postalCode"
                    value={formData.postalCode}
                    onChange={handleInputChange}
                    placeholder="28001"
                    maxLength="5"
                    className={errors.postalCode ? 'error' : ''}
                  />
                  {errors.postalCode && <span className="error-text">{errors.postalCode}</span>}
                </div>
              </div>

              <div className="form-group">
                <label>Provincia *</label>
                <select
                  name="province"
                  value={formData.province}
                  onChange={handleInputChange}
                  className={errors.province ? 'error' : ''}
                >
                  <option value="">Selecciona provincia</option>
                  {provinciasEspana.map(p => <option key={p} value={p}>{p}</option>)}
                </select>
                {errors.province && <span className="error-text">{errors.province}</span>}
              </div>

              {/* ‚úÖ CAMPO DE TEL√âFONO CON VALIDACI√ìN MEJORADA */}
              <div className="form-group">
                <label>Tel√©fono * <small>(Necesario para la entrega)</small></label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  placeholder="612345678"
                  maxLength="9"
                  pattern="[6-9][0-9]{8}"
                  className={errors.phone ? 'error' : ''}
                />
                {errors.phone && <span className="error-text">{errors.phone}</span>}
                <small className="help-text">
                  üì± 9 d√≠gitos comenzando por 6, 7, 8 o 9 (sin espacios ni guiones)
                </small>
              </div>

              <div className="form-group">
                <label>Notas (opcional)</label>
                <textarea
                  name="notes"
                  value={formData.notes}
                  onChange={handleInputChange}
                  rows="3"
                  placeholder="Instrucciones especiales..."
                />
              </div>
            </div>

            {/* M√©todos de pago */}
            <div className="checkout-section">
              <h2>üí≥ M√©todo de Pago</h2>

              <div className="payment-methods">
                {stripePromise && (
                  <button
                    className={`payment-method-btn ${paymentMethod === 'stripe' ? 'active' : ''}`}
                    onClick={() => handlePaymentMethodChange('stripe')}
                    type="button"
                  >
                    <span className="payment-icon">üí≥</span>
                    <span>Tarjeta</span>
                  </button>
                )}

                <button
                  className={`payment-method-btn ${paymentMethod === 'bizum' ? 'active' : ''}`}
                  onClick={() => setPaymentMethod('bizum')}
                  type="button"
                >
                  <span className="payment-icon">üì±</span>
                  <span>Bizum</span>
                </button>

                <button
                  className={`payment-method-btn ${paymentMethod === 'transferencia' ? 'active' : ''}`}
                  onClick={() => setPaymentMethod('transferencia')}
                  type="button"
                >
                  <span className="payment-icon">üè¶</span>
                  <span>Transferencia</span>
                </button>

                <button
                  className={`payment-method-btn ${paymentMethod === 'contrareembolso' ? 'active' : ''}`}
                  onClick={() => setPaymentMethod('contrareembolso')}
                  type="button"
                >
                  <span className="payment-icon">üíµ</span>
                  <span>Contrareembolso (+3‚Ç¨)</span>
                </button>
              </div>

              {/* Formulario Stripe */}
              {paymentMethod === 'stripe' && clientSecret && stripePromise && (
                <Elements stripe={stripePromise} options={{ clientSecret }}>
                  <StripePaymentForm onSuccess={handleStripeSuccess} />
                </Elements>
              )}

              {/* Otros m√©todos */}
              {paymentMethod !== 'stripe' && (
                <div className="other-payment-section">
                  {paymentMethod === 'bizum' && (
                    <p className="payment-info">Recibir√°s instrucciones por email</p>
                  )}
                  {paymentMethod === 'transferencia' && (
                    <p className="payment-info">Recibir√°s datos bancarios por email</p>
                  )}
                  {paymentMethod === 'contrareembolso' && (
                    <p className="payment-info">Pagar√°s al recibir (+3‚Ç¨)</p>
                  )}
                  <button 
                    className="btn-finalizar-compra"
                    onClick={handleOtherPaymentMethod}
                    disabled={loading}
                  >
                    {loading ? 'Procesando...' : '‚úÖ Confirmar Pedido'}
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Resumen */}
          <div className="order-summary">
            <h2>Resumen</h2>
            <div className="order-items">
              {cart.map(item => (
                <div key={item._id} className="order-item">
                  <img src={item.images?.[0] || '/placeholder.jpg'} alt={item.name} />
                  <div className="item-details">
                    <p className="item-name">{item.name}</p>
                    <p className="item-qty">Cantidad: {item.quantity}</p>
                  </div>
                  <p className="item-price">{formatPrice(item.price * item.quantity)}</p>
                </div>
              ))}
            </div>
            <div className="summary-totals">
              <div className="summary-row">
                <span>Subtotal</span>
                <span>{formatPrice(getCartTotal())}</span>
              </div>
              <div className="summary-row">
                <span>Env√≠o</span>
                <span>Gratis</span>
              </div>
              {paymentMethod === 'contrareembolso' && (
                <div className="summary-row">
                  <span>Contrareembolso</span>
                  <span>3,00 ‚Ç¨</span>
                </div>
              )}
              <div className="summary-row total">
                <span>Total</span>
                <span>{formatPrice(paymentMethod === 'contrareembolso' ? getCartTotal() + 3 : getCartTotal())}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
