import React, { Suspense, lazy } from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';

// Componente de carga (puedes crearlo en un archivo aparte)
const PageLoader = () => (
  <div className="full-page-loader">
    <div className="spinner"></div>
    <p>Cargando sección...</p>
  </div>
);

// ✅ IMPORTACIONES DINÁMICAS (Lazy Loading)
// El navegador NO descargará estas páginas hasta que el usuario haga clic en ellas.
const Home = lazy(() => import('./pages/Home'));
const Products = lazy(() => import('./pages/Products'));
const ProductDetail = lazy(() => import('./pages/ProductDetail'));
const Cart = lazy(() => import('./pages/Cart'));
const Checkout = lazy(() => import('./pages/Checkout'));
const Login = lazy(() => import('./pages/Login'));
const Profile = lazy(() => import('./pages/Profile'));

// Secciones de Admin (Suelen ser las más pesadas, ideal para Lazy)
const AdminDashboard = lazy(() => import('./pages/admin/AdminDashboard'));

function App() {
  return (
    <Router>
      <Header />

      {/* Suspense envuelve todas las rutas. 
         Si una página tarda 200ms en bajar, mostrará el 'fallback' (PageLoader). 
      */}
      <Suspense fallback={<PageLoader />}>
        <main className="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/products" element={<Products />} />
            <Route path="/product/:id" element={<ProductDetail />} />
            <Route path="/cart" element={<Cart />} />
            <Route path="/checkout" element={<Checkout />} />
            <Route path="/login" element={<Login />} />
            <Route path="/profile/*" element={<Profile />} />

            {/* Rutas de Administración */}
            <Route path="/admin/*" element={<AdminDashboard />} />
          </Routes>
        </main>
      </Suspense>

      <Footer />
    </Router>
  );
}

export default App;
