import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  FaTruck, FaBox, FaCheckCircle, FaMapMarkerAlt, 
  FaClock, FaExclamationTriangle, FaExternalLinkAlt, FaDownload 
} from 'react-icons/fa';
import '../styles/Tracking.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Configuración de transportistas y sus links externos
const carrierLinks = {
  'SimShip Express': 'http://localhost:3000/track/', // Tu propia web
  'Correos': 'https://www.correos.es/es/es/herramientas/localizador/envios/detalle?recurso=',
  'SEUR': 'https://www.seur.com/es/acceder-seguimiento/?procedencia=0&n_pedido=',
  'DHL': 'https://www.dhl.com/es-es/home/tracking/tracking-express.html?submit=1&tracking-id='
};

const statusIcons = {
  pendiente: <FaClock />,
  en_preparacion: <FaBox />,
  enviado: <FaTruck />,
  en_transito: <FaTruck />,
  en_reparto: <FaTruck />,
  entregado: <FaCheckCircle />,
  incidencia: <FaExclamationTriangle />
};

const TrackingView = ({ orderId }) => {
  const [tracking, setTracking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (orderId) loadTracking();
  }, [orderId]);

  const loadTracking = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/chat/order/${orderId}`, { // Ajusta la ruta según tu backend
        headers: { Authorization: `Bearer ${token}` }
      });
      setTracking(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Información de envío no disponible');
    } finally {
      setLoading(false);
    }
  };

  const downloadLabel = () => {
    const token = localStorage.getItem('token');
    window.open(`${API_URL}/chat/label/${tracking._id}?token=${token}`, '_blank');
  };

  if (loading) return <div className="tracking-status-msg">⏳ Cargando seguimiento...</div>;
  if (error) return <div className="tracking-error-msg">ℹ️ {error}</div>;
  if (!tracking) return null;

  // Calcular progreso
  const steps = ['pendiente', 'en_preparacion', 'enviado', 'en_reparto', 'entregado'];
  const currentStepIndex = steps.indexOf(tracking.currentStatus);

  return (
    <div className="tracking-card">
      {/* CABECERA */}
      <div className="tracking-header">
        <div>
          <h4>Número de Seguimiento</h4>
          <span className="tracking-id">{tracking.trackingNumber}</span>
        </div>
        <div className="carrier-badge">
          {tracking.carrier}
        </div>
      </div>

      {/* BARRA DE PROGRESO VISUAL */}
      
      <div className="progress-track">
        {steps.map((step, index) => (
          <div key={step} className={`step ${index <= currentStepIndex ? 'active' : ''} ${tracking.currentStatus === 'incidencia' && index === currentStepIndex ? 'error' : ''}`}>
            <div className="step-icon">{statusIcons[step]}</div>
            <span className="step-text">{step.replace('_', ' ')}</span>
          </div>
        ))}
      </div>

      {/* INFO PRINCIPAL */}
      <div className="tracking-main-info">
        <div className="status-banner">
          <div className="status-label">Estado Actual:</div>
          <div className="status-value">{tracking.currentStatus.toUpperCase()}</div>
        </div>
        
        {tracking.estimatedDelivery && tracking.currentStatus !== 'entregado' && (
          <div className="delivery-est">
            <FaClock /> Entrega estimada: <strong>{new Date(tracking.estimatedDelivery).toLocaleDateString()}</strong>
          </div>
        )}
      </div>

      {/* ACCIONES EXTERNAS */}
      <div className="tracking-actions">
        <a 
          href={`${carrierLinks[tracking.carrier] || '#'}${tracking.trackingNumber}`} 
          target="_blank" 
          rel="noopener noreferrer"
          className="btn-track-external"
        >
          <FaExternalLinkAlt /> Web del Transportista
        </a>
        
        {/* Solo mostrar descargar etiqueta si es admin (opcional) */}
        <button onClick={downloadLabel} className="btn-download-label">
          <FaDownload /> Descargar Etiqueta (PDF)
        </button>
      </div>

      {/* HISTORIAL DETALLADO */}
      <div className="tracking-history">
        <h5>Historial de eventos</h5>
        <div className="history-list">
          {tracking.events.map((event, idx) => (
            <div key={idx} className="history-item">
              <div className="history-dot"></div>
              <div className="history-info">
                <span className="history-date">{new Date(event.timestamp).toLocaleString()}</span>
                <p className="history-desc"><strong>{event.status.toUpperCase()}:</strong> {event.description}</p>
                {event.location && <span className="history-loc"><FaMapMarkerAlt /> {event.location}</span>}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TrackingView;
