import React, { useState } from 'react';
import axios from 'axios';
import { FaEdit } from 'react-icons/fa';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const ProfileTab = ({ user, login }) => {
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name || '',
    phone: user?.phone || '',
    street: user?.address?.street || '',
    city: user?.address?.city || '',
    postalCode: user?.address?.postalCode || '',
    country: user?.address?.country || 'España'
  });

  const handleSave = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`${API_URL}/auth/profile`, {
        name: formData.name, phone: formData.phone,
        address: { street: formData.street, city: formData.city, postalCode: formData.postalCode, country: formData.country }
      }, { headers: { Authorization: `Bearer ${token}` } });
      login({ ...user, ...response.data }, token);
      setEditing(false);
      alert('Perfil actualizado');
    } catch (err) { alert('Error al guardar'); }
  };

  return (
    <div className="profile-tab">
      <div className="tab-header">
        <h2>Mis Datos</h2>
        {!editing ? <button className="btn-edit" onClick={() => setEditing(true)}><FaEdit /> Editar</button> : 
        <div className="edit-actions">
          <button className="btn-save" onClick={handleSave}>Guardar</button>
          <button className="btn-cancel" onClick={() => setEditing(false)}>Cancelar</button>
        </div>}
      </div>
      <div className="profile-form">
        <div className="form-grid">
           <input name="name" value={formData.name} onChange={e => setFormData({...formData, name: e.target.value})} disabled={!editing} placeholder="Nombre" />
           <input name="phone" value={formData.phone} onChange={e => setFormData({...formData, phone: e.target.value})} disabled={!editing} placeholder="Teléfono" />
        </div>
      </div>
    </div>
  );
};

export default ProfileTab;
