import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';
import { FaComments, FaTimes, FaPaperPlane, FaHeadset, FaCheckCircle, FaRedo } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import '../styles/ChatWidget.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const SOCKET_URL = API_URL.replace('/api', '');

let socket = null;

const ChatWidget = () => {
  const { isAuthenticated } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const [connected, setConnected] = useState(false);
  const [chatStatus, setChatStatus] = useState('open');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (!isAuthenticated()) return;

    initializeSocket();

    return () => {
      // No desconectar socket para mantener conexi√≥n
    };
  }, [isAuthenticated]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const initializeSocket = () => {
    if (socket) return;

    socket = io(SOCKET_URL, {
      transports: ['websocket', 'polling'],
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000
    });

    socket.on('connect', () => {
      console.log('‚úÖ Socket conectado');
      setConnected(true);

      const token = localStorage.getItem('token');
      if (token) {
        socket.emit('authenticate', token);
      }
    });

    socket.on('disconnect', () => {
      console.log('‚ùå Socket desconectado');
      setConnected(false);
    });

    socket.on('authenticated', () => {
      console.log('‚úÖ Autenticado');
      loadChat();
    });

    socket.on('new_admin_message', (chat) => {
      console.log('üì® Nuevo mensaje del admin');
      setMessages(chat.messages);
      setChatStatus(chat.status);

      const unread = chat.messages.filter(m => m.sender === 'admin' && !m.read).length;
      setUnreadCount(unread);
    });

    socket.on('chat_updated', (chat) => {
      setMessages(chat.messages);
      setChatStatus(chat.status);
    });

    socket.on('chat_closed', (chat) => {
      setMessages(chat.messages);
      setChatStatus('closed');
    });

    socket.on('message_sent', (data) => {
      if (data.success && data.chat) {
        setMessages(data.chat.messages);
      }
      setLoading(false);
    });

    socket.on('message_error', (data) => {
      console.error('Error:', data.message);
      alert('Error al enviar mensaje');
      setLoading(false);
    });
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadChat = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await axios.get(`${API_URL}/chat/my-chat`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessages(response.data.messages);
      setChatStatus(response.data.status);

      const unread = response.data.messages.filter(
        m => m.sender === 'admin' && !m.read
      ).length;
      setUnreadCount(unread);

    } catch (error) {
      console.error('Error al cargar chat:', error);
    }
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || loading || !connected || chatStatus === 'closed') return;

    setLoading(true);
    socket.emit('user_send_message', { text: inputMessage });
    setInputMessage('');
  };

  const closeChat = async () => {
    if (!window.confirm('¬øEst√°s seguro de que quieres finalizar el chat?')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `${API_URL}/chat/close-my-chat`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setChatStatus('closed');
      alert('‚úÖ Chat finalizado. Puedes iniciar uno nuevo cuando lo necesites.');
      loadChat();
    } catch (error) {
      console.error('Error al cerrar chat:', error);
      alert('Error al cerrar chat');
    }
  };

  // ‚úÖ NUEVA FUNCI√ìN: Iniciar nuevo chat
  const startNewChat = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // Crear un nuevo chat
      const response = await axios.post(
        `${API_URL}/chat/start-new-chat`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessages(response.data.messages);
      setChatStatus(response.data.status);
      setUnreadCount(0);

      console.log('‚úÖ Nuevo chat iniciado');

    } catch (error) {
      console.error('Error al iniciar nuevo chat:', error);
      alert('Error al iniciar nuevo chat');
    } finally {
      setLoading(false);
    }
  };

  const toggleChat = () => {
    if (!isAuthenticated()) {
      alert('Debes iniciar sesi√≥n para usar el chat de soporte');
      return;
    }
    setIsOpen(!isOpen);

    if (!isOpen && unreadCount > 0) {
      setUnreadCount(0);
      const token = localStorage.getItem('token');
      axios.put(`${API_URL}/chat/mark-read`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      }).catch(err => console.error(err));
    }
  };

  const formatTime = (date) => {
    return new Date(date).toLocaleTimeString('es-ES', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="chat-widget">
      <button 
        className="chat-button" 
        onClick={toggleChat}
        aria-label="Chat de soporte"
      >
        {isOpen ? <FaTimes /> : <FaComments />}
        {!isOpen && unreadCount > 0 && (
          <span className="chat-badge">{unreadCount}</span>
        )}
      </button>

      {isOpen && (
        <div className="chat-window">
          <div className="chat-header">
            <div className="chat-header-info">
              <FaHeadset size={24} />
              <div>
                <h3>üí¨ Atenci√≥n al Cliente</h3>
                <p className="status-indicator">
                  <span className={`status-dot ${connected ? 'online' : 'offline'}`}></span>
                  {connected ? 'En l√≠nea' : 'Desconectado'}
                </p>
              </div>
            </div>
            <div className="chat-header-actions">
              {chatStatus === 'open' && (
                <button 
                  className="btn-close-conversation" 
                  onClick={closeChat}
                  title="Finalizar conversaci√≥n"
                >
                  <FaCheckCircle /> Finalizar
                </button>
              )}
              <button onClick={toggleChat} aria-label="Cerrar chat">
                <FaTimes />
              </button>
            </div>
          </div>

          {/* ‚úÖ MENSAJE CUANDO EL CHAT EST√Å CERRADO */}
          {chatStatus === 'closed' && (
            <div className="chat-closed-notice">
              <FaCheckCircle size={48} color="#4ade80" />
              <h4>Conversaci√≥n finalizada</h4>
              <p>Esta conversaci√≥n ha sido cerrada.</p>
              <button 
                className="btn-new-chat"
                onClick={startNewChat}
                disabled={loading}
              >
                <FaRedo /> {loading ? 'Iniciando...' : 'Iniciar nueva conversaci√≥n'}
              </button>
            </div>
          )}

          {/* MENSAJES */}
          <div className="chat-messages">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`message ${message.sender === 'user' ? 'user-message' : 'admin-message'}`}
              >
                {message.sender === 'admin' && (
                  <div className="message-avatar">
                    <FaHeadset />
                  </div>
                )}
                <div className="message-content">
                  {message.sender === 'admin' && (
                    <span className="message-sender">Soporte SimShop</span>
                  )}
                  <p>{message.text}</p>
                  <span className="message-time">{formatTime(message.timestamp)}</span>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          {/* INPUT O MENSAJE DE CERRADO */}
          {chatStatus === 'open' ? (
            <form onSubmit={sendMessage} className="chat-input-form">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="Escribe tu mensaje..."
                disabled={loading || !connected}
              />
              <button type="submit" disabled={loading || !inputMessage.trim() || !connected}>
                <FaPaperPlane />
              </button>
            </form>
          ) : (
            <div className="chat-input-disabled">
              <p>üí¨ Chat finalizado - Puedes iniciar uno nuevo arriba</p>
            </div>
          )}

          {/* FOOTER */}
          <div className="chat-footer">
            <small>üîí {connected ? 'Conectado y seguro' : 'Reconectando...'}</small>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatWidget;
