import React, { useState, useEffect } from 'react';
import { FaEye, FaEdit, FaPhone, FaTruck, FaFilePdf, FaSync } from 'react-icons/fa'; // Nuevos iconos
import axios from 'axios';
import '../../styles/AdminOrders.css';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [statusUpdate, setStatusUpdate] = useState({
    orderStatus: '',
    paymentStatus: ''
  });
  const [error, setError] = useState(null);

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No est√°s autenticado.');
        setLoading(false);
        return;
      }

      const response = await axios.get(`${API_URL}/orders`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      const ordersData = Array.isArray(response.data) ? response.data : [];
      setOrders(ordersData);
    } catch (error) {
      console.error('Error al cargar pedidos:', error);
      setError('Error al cargar los pedidos');
    } finally {
      setLoading(false);
    }
  };

  // --- NUEVA FUNCI√ìN: GENERAR TRACKING ---
  const handleCreateTracking = async (orderId) => {
    const carrier = prompt("Introduce el transportista (Correos, SEUR, DHL, SimShip):", "Correos");
    if (!carrier) return;

    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_URL}/tracking/create`, 
        { orderId, carrier }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("‚úÖ Tracking generado y n√∫mero asignado.");
      loadOrders(); // Recargar para ver los cambios
    } catch (err) {
      alert("‚ùå Error: " + (err.response?.data?.message || "No se pudo crear el tracking"));
    }
  };

  // --- NUEVA FUNCI√ìN: DESCARGAR PDF ---
  const downloadLabel = (trackingId) => {
    const token = localStorage.getItem('token');
    // Abrimos el PDF en una nueva pesta√±a
    window.open(`${API_URL}/tracking/label/${trackingId}?token=${token}`, '_blank');
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-ES', { style: 'currency', currency: 'EUR' }).format(price);
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('es-ES', {
      year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit'
    });
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      pendiente: 'badge-warning', procesando: 'badge-info', enviado: 'badge-primary',
      entregado: 'badge-success', cancelado: 'badge-danger', pagado: 'badge-success', fallido: 'badge-danger'
    };
    return statusMap[status] || 'badge-secondary';
  };

  const openDetailModal = (order) => {
    setSelectedOrder(order);
    setStatusUpdate({ orderStatus: order.orderStatus, paymentStatus: order.paymentStatus });
    setShowDetailModal(true);
  };

  const closeDetailModal = () => {
    setShowDetailModal(false);
    setSelectedOrder(null);
  };

  const handleStatusChange = (e) => {
    setStatusUpdate({ ...statusUpdate, [e.target.name]: e.target.value });
  };

  const updateOrderStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/orders/${selectedOrder._id}/status`, statusUpdate, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      alert('Estado actualizado correctamente');
      closeDetailModal();
      loadOrders();
    } catch (error) {
      alert('Error al actualizar');
    }
  };

  if (loading) return <div className="admin-orders"><div className="loading-container"><div className="spinner-large"></div><p>Cargando...</p></div></div>;

  return (
    <div className="admin-orders">
      <div className="page-header">
        <h2>Gesti√≥n de Pedidos</h2>
        <button className="btn btn-secondary" onClick={loadOrders}><FaSync /> Actualizar</button>
      </div>

      <div className="table-responsive">
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID Pedido</th>
              <th>Cliente</th>
              <th>Total</th>
              <th>Estado Pedido</th>
              <th>Env√≠o / Etiqueta</th> {/* Nueva Columna */}
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id}>
                <td><strong>#{order._id.slice(-8)}</strong></td>
                <td>{order.user?.name || 'Usuario'}</td>
                <td>{formatPrice(order.totalAmount)}</td>
                <td>
                  <span className={`badge ${getStatusBadge(order.orderStatus)}`}>
                    {order.orderStatus}
                  </span>
                </td>

                {/* COLUMNA DE LOG√çSTICA INTEGRADA */}
                <td>
                  {!order.tracking ? (
                    <button 
                      className="btn-action btn-generate" 
                      onClick={() => handleCreateTracking(order._id)}
                    >
                      <FaTruck /> Generar Env√≠o
                    </button>
                  ) : (
                    <button 
                      className="btn-action btn-pdf" 
                      onClick={() => downloadLabel(order.tracking._id || order.tracking)}
                    >
                      <FaFilePdf /> Etiqueta
                    </button>
                  )}
                </td>

                <td>
                  <div className="action-buttons">
                    <button className="btn-icon btn-view" onClick={() => openDetailModal(order)}>
                      <FaEye />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal de detalle con Tracking incorporado */}
      {showDetailModal && selectedOrder && (
        <div className="modal-overlay" onClick={closeDetailModal}>
          <div className="modal-content large" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Pedido #{selectedOrder._id.slice(-8)}</h3>
              <button className="modal-close" onClick={closeDetailModal}>√ó</button>
            </div>

            <div className="order-detail">
              <div className="detail-section">
                <h4>üöö Log√≠stica y Seguimiento</h4>
                <div className="tracking-info-admin">
                  {selectedOrder.tracking ? (
                    <div className="tracking-active">
                      <p><strong>N√∫mero:</strong> {selectedOrder.tracking.trackingNumber || 'Generando...'}</p>
                      <button 
                        className="btn btn-pdf" 
                        onClick={() => downloadLabel(selectedOrder.tracking._id || selectedOrder.tracking)}
                      >
                        <FaFilePdf /> Descargar Etiqueta de Env√≠o
                      </button>
                    </div>
                  ) : (
                    <p className="text-muted">No se ha generado tracking para este pedido.</p>
                  )}
                </div>
              </div>

              <div className="detail-section">
                <h4>‚öôÔ∏è Actualizar Estado</h4>
                <div className="status-update-form">
                  <div className="form-group">
                    <label>Estado Pedido</label>
                    <select name="orderStatus" value={statusUpdate.orderStatus} onChange={handleStatusChange} className="form-select">
                      <option value="pendiente">Pendiente</option>
                      <option value="procesando">Procesando</option>
                      <option value="enviado">Enviado</option>
                      <option value="entregado">Entregado</option>
                      <option value="cancelado">Cancelado</option>
                    </select>
                  </div>
                  <button className="btn btn-primary" onClick={updateOrderStatus}><FaEdit /> Guardar Cambios</button>
                </div>
              </div>

              {/* ... Resto de secciones (Direcci√≥n, Productos, etc.) ... */}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminOrders;
