import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Auth.css';

const ForgotPassword = () => {
  const navigate = useNavigate();
  const { sendPasswordResetCode, resetPassword } = useAuth();
  const [step, setStep] = useState(1); // 1: Pedir Email, 2: Poner Código y Pass
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  const [formData, setFormData] = useState({
    email: '', code: '', newPassword: '', confirmPassword: ''
  });

  const handleSubmitEmail = async (e) => {
    e.preventDefault();
    setError(''); setLoading(true);
    const res = await sendPasswordResetCode(formData.email);
    if (res.success) {
      setMessage('Código enviado con éxito.');
      setStep(2);
    } else {
      setError(res.message);
    }
    setLoading(false);
  };

  const handleSubmitReset = async (e) => {
    e.preventDefault();
    if (formData.newPassword !== formData.confirmPassword) return setError('Las claves no coinciden');
    setError(''); setLoading(true);
    const res = await resetPassword(formData.email, formData.code, formData.newPassword);
    if (res.success) {
      alert('¡Contraseña cambiada!');
      navigate('/login');
    } else {
      setError(res.message);
    }
    setLoading(false);
  };

  return (
    <div className="auth-page">
      <div className="auth-card">
        <h2>{step === 1 ? 'Recuperar Cuenta' : 'Nueva Contraseña'}</h2>
        {error && <p className="error-message">{error}</p>}
        {message && <p className="success-message">{message}</p>}

        {step === 1 ? (
          <form onSubmit={handleSubmitEmail}>
            <input type="email" placeholder="Email" required 
              onChange={(e) => setFormData({...formData, email: e.target.value})} />
            <button className="submit-btn" disabled={loading}>Enviar Código</button>
          </form>
        ) : (
          <form onSubmit={handleSubmitReset}>
            <input type="text" placeholder="Código de 6 dígitos" required
              onChange={(e) => setFormData({...formData, code: e.target.value})} />
            <input type="password" placeholder="Nueva Contraseña" required
              onChange={(e) => setFormData({...formData, newPassword: e.target.value})} />
            <input type="password" placeholder="Confirmar Contraseña" required
              onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})} />
            <button className="submit-btn" disabled={loading}>Actualizar Contraseña</button>
          </form>
        )}
        <Link to="/login" className="auth-link">Volver al inicio</Link>
      </div>
    </div>
  );
};

export default ForgotPassword;
