import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';
import { FaPaperPlane, FaComments, FaCheckCircle } from 'react-icons/fa';
import '../../styles/AdminChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const SOCKET_URL = API_URL.replace('/api', '');

// Socket global para evitar m√∫ltiples conexiones
let socket = null;
let isSocketInitialized = false;

const AdminChat = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [connected, setConnected] = useState(false);
  const messagesEndRef = useRef(null);
  const socketInitialized = useRef(false);

  useEffect(() => {
    console.log('üì± AdminChat montado');
    
    if (!socketInitialized.current) {
      initializeSocket();
      socketInitialized.current = true;
    }
    
    loadChats();

    // NO limpiar el socket al desmontar
    return () => {
      console.log('üîÑ AdminChat desmontado (socket permanece activo)');
    };
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [selectedChat?.messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const initializeSocket = () => {
    if (isSocketInitialized) {
      console.log('‚ö†Ô∏è Socket ya inicializado');
      return;
    }

    console.log('üîå Inicializando socket...');
    
    socket = io(SOCKET_URL, {
      transports: ['websocket', 'polling'],
      reconnection: true,
      reconnectionAttempts: 10,
      reconnectionDelay: 1000
    });

    socket.on('connect', () => {
      console.log('‚úÖ Socket conectado:', socket.id);
      setConnected(true);
      
      const token = localStorage.getItem('token');
      if (token) {
        console.log('üîë Autenticando...');
        socket.emit('authenticate', token);
      } else {
        console.error('‚ùå No hay token');
      }
    });

    socket.on('disconnect', (reason) => {
      console.log('‚ùå Socket desconectado:', reason);
      setConnected(false);
    });

    socket.on('authenticated', (data) => {
      console.log('‚úÖ Autenticado:', data);
      loadChats();
    });

    socket.on('auth_error', (data) => {
      console.error('‚ùå Error de autenticaci√≥n:', data.message);
      alert('Error de autenticaci√≥n: ' + data.message);
    });

    socket.on('new_user_message', ({ chat }) => {
      console.log('üì® NUEVO MENSAJE DE USUARIO:', chat.user?.name);
      
      setChats(prevChats => {
        const exists = prevChats.find(c => c._id === chat._id);
        if (exists) {
          return prevChats.map(c => c._id === chat._id ? chat : c);
        }
        return [chat, ...prevChats];
      });

      if (selectedChat && selectedChat._id === chat._id) {
        setSelectedChat(chat);
      }

      // Notificaci√≥n sonora (opcional)
      try {
        new Audio('data:audio/wav;base64,').play();
      } catch (e) {}
    });

    socket.on('chat_updated', (chat) => {
      console.log('üîÑ Chat actualizado:', chat._id);
      setChats(prevChats => prevChats.map(c => c._id === chat._id ? chat : c));
      
      if (selectedChat && selectedChat._id === chat._id) {
        setSelectedChat(chat);
      }
    });

    socket.on('message_sent', ({ success, chat }) => {
      if (success) {
        console.log('‚úÖ Mensaje enviado correctamente');
        setLoading(false);
        setMessage('');
        
        if (selectedChat && selectedChat._id === chat._id) {
          setSelectedChat(chat);
        }
      }
    });

    socket.on('message_error', (data) => {
      console.error('‚ùå Error al enviar mensaje:', data.message);
      alert('Error: ' + data.message);
      setLoading(false);
    });

    isSocketInitialized = true;
    console.log('‚úÖ Socket inicializado completamente');
  };

  const loadChats = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        console.error('‚ùå No hay token para cargar chats');
        return;
      }

      console.log('üì• Cargando chats...');
      const response = await axios.get(`${API_URL}/chat/admin/chats?status=open`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      console.log(`‚úÖ Chats cargados: ${response.data.length}`);
      setChats(response.data);
    } catch (error) {
      console.error('‚ùå Error al cargar chats:', error);
      if (error.response?.status === 403) {
        alert('No tienes permisos de administrador');
      }
    }
  };

  const sendMessage = () => {
    if (!message.trim() || !selectedChat || loading) {
      console.log('‚ö†Ô∏è No se puede enviar mensaje:', { 
        hasMessage: !!message.trim(), 
        hasChat: !!selectedChat, 
        loading 
      });
      return;
    }

    if (!socket || !connected) {
      console.error('‚ùå Socket no conectado');
      alert('Socket desconectado. Recarga la p√°gina.');
      return;
    }

    console.log('üì§ Enviando mensaje admin:', message);
    setLoading(true);
    
    socket.emit('admin_send_message', {
      chatId: selectedChat._id,
      text: message.trim()
    });
  };

  const closeChat = async () => {
    if (!selectedChat) return;

    if (!window.confirm('¬øFinalizar esta conversaci√≥n?')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `${API_URL}/chat/admin/close-chat/${selectedChat._id}`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert('Chat cerrado correctamente');
      setSelectedChat(null);
      loadChats();
    } catch (error) {
      console.error('Error al cerrar chat:', error);
      alert('Error al cerrar chat');
    }
  };

  const formatTime = (date) => {
    return new Date(date).toLocaleTimeString('es-ES', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="admin-chat-container">
      <div className="chat-header-main">
        <div className="header-left">
          <h1>üí¨ Chat de Atenci√≥n al Cliente</h1>
          <p className="header-subtitle">
            {connected ? 'üü¢ Conectado' : 'üî¥ Desconectado'}
          </p>
        </div>
        <div className="header-stats">
          <div className="stat-badge">
            <span className="stat-number">{chats.length}</span>
            <span className="stat-label">Chats Abiertos</span>
          </div>
        </div>
      </div>

      <div className="chat-layout">
        {/* Lista de chats */}
        <div className="chats-list">
          <div className="chats-list-header">
            <h3>Conversaciones</h3>
            <span className="badge-count">{chats.length}</span>
          </div>

          <div className="chats-list-items">
            {chats.length === 0 ? (
              <div className="no-chats">
                <div className="no-chats-icon"><FaComments size={48} /></div>
                <p>No hay chats abiertos</p>
                <small>Los nuevos mensajes aparecer√°n autom√°ticamente</small>
              </div>
            ) : (
              chats.map(chat => {
                const lastMessage = chat.messages[chat.messages.length - 1];
                const unread = chat.messages.filter(m => m.sender === 'user' && !m.read).length;

                return (
                  <div
                    key={chat._id}
                    className={`chat-item ${selectedChat?._id === chat._id ? 'active' : ''} ${unread > 0 ? 'has-unread' : ''}`}
                    onClick={() => {
                      console.log('üìå Chat seleccionado:', chat.user?.name);
                      setSelectedChat(chat);
                    }}
                  >
                    <div className="chat-item-avatar">
                      üë§
                      {unread > 0 && <span className="unread-badge">{unread}</span>}
                    </div>
                    <div className="chat-item-content">
                      <div className="chat-item-header">
                        <span className="chat-item-name">{chat.user?.name || 'Usuario'}</span>
                        <span className="chat-item-time">
                          {formatTime(lastMessage.timestamp)}
                        </span>
                      </div>
                      <div className="chat-item-preview">
                        <span className={`message-sender ${lastMessage.sender === 'admin' ? 'you' : ''}`}>
                          {lastMessage.sender === 'admin' ? 'T√∫: ' : ''}
                        </span>
                        {lastMessage.text.substring(0, 40)}...
                      </div>
                    </div>
                  </div>
                );
              })
            )}
          </div>
        </div>

        {/* Conversaci√≥n */}
        <div className="chat-conversation">
          {selectedChat ? (
            <>
              <div className="conversation-header">
                <div className="conversation-user-info">
                  <div className="user-avatar-large">üë§</div>
                  <div className="user-details">
                    <h3>{selectedChat.user?.name || 'Usuario'}</h3>
                    <p className="user-email">{selectedChat.user?.email || ''}</p>
                  </div>
                </div>
                <button className="btn-close-chat" onClick={closeChat}>
                  <FaCheckCircle /> Finalizar Chat
                </button>
              </div>

              <div className="messages-container">
                {selectedChat.messages.map((msg, index) => (
                  <div
                    key={index}
                    className={`message-bubble-wrapper ${msg.sender === 'admin' ? 'admin-message' : ''}`}
                  >
                    <div className="message-avatar">
                      <div className={`avatar ${msg.sender === 'admin' ? 'admin-avatar' : 'user-avatar'}`}>
                        {msg.sender === 'admin' ? 'üë®‚Äçüíº' : 'üë§'}
                      </div>
                    </div>
                    <div className="message-content-wrapper">
                      <div className="message-bubble ${msg.sender}">
                        <p className="message-text">{msg.text}</p>
                        <span className="message-time">{formatTime(msg.timestamp)}</span>
                      </div>
                    </div>
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>

              <div className="message-input-container">
                <div className="input-wrapper">
                  <input
                    type="text"
                    className="message-input"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        sendMessage();
                      }
                    }}
                    placeholder="Escribe tu respuesta..."
                    disabled={loading || !connected}
                  />
                  <button
                    className={`btn-send ${message.trim() ? 'active' : ''}`}
                    onClick={sendMessage}
                    disabled={loading || !message.trim() || !connected}
                  >
                    <FaPaperPlane /> Enviar
                  </button>
                </div>
                <small style={{textAlign:'center',display:'block',marginTop:'8px',color:'#666'}}>
                  {connected ? 'Enter para enviar' : 'Reconectando...'}
                </small>
              </div>
            </>
          ) : (
            <div className="no-chat-selected">
              <FaComments size={64} style={{color:'#cbd5e1',marginBottom:'20px'}} />
              <h3>Selecciona una conversaci√≥n</h3>
              <p>Elige un chat para ver y responder mensajes</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminChat;
