import React, { useState, useEffect, useCallback } from 'react'; // 1. Importaci√≥n correcta
import axios from 'axios';
import { FaBox, FaTruck, FaExternalLinkAlt, FaMapMarkerAlt, FaClock, FaCheckCircle, FaShippingFast } from 'react-icons/fa';
import '../../styles/Profile.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const OrdersTab = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedTracking, setExpandedTracking] = useState(null);

  // 2. Definici√≥n de loadOrders (FUERA de cualquier otro hook o funci√≥n)
  const loadOrders = useCallback(async (isSilent = false) => {
    try {
      const token = sessionStorage.getItem('token');
      if (!token) return;

      if (!isSilent) setLoading(true);

      const response = await axios.get(`${API_URL}/orders/my-orders`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setOrders(response.data);
    } catch (error) {
      console.error('‚ùå Error al cargar pedidos:', error);
      if (!isSilent) setError('Error al cargar pedidos');
    } finally {
      setLoading(false);
    }
  }, []);

  // 3. Efecto de carga inicial
  useEffect(() => {
    loadOrders();
  }, [loadOrders]);

  // 4. Efecto de Tiempo Real (Polling) - Detecta cambios cada 10 seg
  useEffect(() => {
    // Solo activamos el polling si hay pedidos que NO est√°n entregados o cancelados
    const hasActiveOrders = orders.some(o => 
      o.orderStatus !== 'entregado' && o.orderStatus !== 'cancelado'
    );

    if (hasActiveOrders) {
      const interval = setInterval(() => {
        console.log('üîÑ Sincronizando con Admin...');
        loadOrders(true); // Carga silenciosa
      }, 10000); 

      return () => clearInterval(interval);
    }
  }, [orders, loadOrders]);

  // --- Funciones auxiliares ---
  const fetchTrackingDetails = async (trackingNumber) => {
    try {
      const token = sessionStorage.getItem('token');
      const response = await axios.get(`${API_URL}/tracking/${trackingNumber}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      return response.data;
    } catch (error) {
      return null;
    }
  };

  const toggleTrackingDetails = async (orderId, trackingNumber) => {
    if (expandedTracking === orderId) {
      setExpandedTracking(null);
    } else {
      setExpandedTracking(orderId);
      const order = orders.find(o => o._id === orderId);
      if (order.tracking && !order.tracking.events) {
        const details = await fetchTrackingDetails(trackingNumber);
        if (details) {
          setOrders(prev => prev.map(o => 
            o._id === orderId ? { ...o, tracking: { ...o.tracking, ...details } } : o
          ));
        }
      }
    }
  };

  const getStatusIcon = (status) => {
    const icons = { en_preparacion: 'üì¶', enviado: 'üöö', entregado: '‚úÖ' };
    return icons[status] || 'üìã';
  };

  const getStatusColor = (status) => {
    const colors = { en_preparacion: '#f39c12', enviado: '#3498db', entregado: '#27ae60' };
    return colors[status] || '#95a5a6';
  };

  const formatPrice = (p) => new Intl.NumberFormat('es-ES', { style: 'currency', currency: 'EUR' }).format(p);

  // --- Renderizado ---
  if (loading) return <div className="tab-loading">Cargando...</div>;
  if (error) return <div className="empty-state"><h3>{error}</h3></div>;

  return (
    <div className="orders-tab">
      <h2>Historial de Pedidos</h2>
      <div className="orders-list">
        {orders.map(order => (
          <div key={order._id} className="order-card">
            <div className="order-header">
              <span className="order-id">#{order._id.slice(-8).toUpperCase()}</span>
              <span className={`status-badge ${order.orderStatus}`}>{order.orderStatus}</span>
            </div>

            {order.tracking && (
              <div className="tracking-section-enhanced">
                <div className="tracking-card">
                  <div className="status-header">
                    <FaTruck className="truck-anim" />
                    <div>
                      <h4>Estado del Env√≠o</h4>
                      <p>N¬∫: <strong>{order.tracking.trackingNumber}</strong></p>
                    </div>
                  </div>
                  
                  <div className="progress-track">
                    <div className={`step ${order.tracking ? 'active' : ''}`}>üì¶ Preparado</div>
                    <div className={`step ${['enviado', 'entregado'].includes(order.orderStatus) ? 'active' : ''}`}>üöö Enviado</div>
                    <div className={`step ${order.orderStatus === 'entregado' ? 'active' : ''}`}>üè† Recibido</div>
                  </div>

                  <button className="btn-details" onClick={() => toggleTrackingDetails(order._id, order.tracking.trackingNumber)}>
                    {expandedTracking === order._id ? 'Cerrar' : 'Rastrear'}
                  </button>
                </div>
              </div>
            )}

            <div className="order-items">
              {order.items?.map((item, i) => (
                <div key={i} className="order-item">
                  <span>{item.quantity}x {item.product?.name}</span>
                  <span>{formatPrice(item.price * item.quantity)}</span>
                </div>
              ))}
            </div>
            
            <div className="order-footer">
              <span>Total: <strong>{formatPrice(order.totalAmount)}</strong></span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrdersTab;
