import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Verificar autenticaci√≥n al cargar la app
  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      console.log('üîë Token encontrado:', token ? 'S√≠' : 'No');

      if (token) {
        try {
          const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
          console.log('üì° Verificando autenticaci√≥n en:', `${API_URL}/auth/me`);

          const response = await axios.get(`${API_URL}/auth/me`, {
            headers: { Authorization: `Bearer ${token}` }
          });

          console.log('‚úÖ Usuario verificado:', response.data);
          setUser(response.data);
        } catch (error) {
          console.error('‚ùå Error verificando autenticaci√≥n:', error.response?.data || error.message);
          localStorage.removeItem('token');
          setUser(null);
        }
      }

      setLoading(false);
      console.log('üèÅ Verificaci√≥n de autenticaci√≥n completada');
    };

    checkAuth();
  }, []);

  // Funci√≥n de login
  const login = async (email, password) => {
    try {
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
      console.log('üîê [AuthContext] Intentando login en:', `${API_URL}/auth/login`);
      console.log('üîê [AuthContext] Email:', email);

      const response = await axios.post(`${API_URL}/auth/login`, { 
        email, 
        password 
      });

      console.log('üì• [AuthContext] Respuesta del backend:', response.data);

      const { token, user: userData } = response.data;

      // Validar formato de respuesta
      if (!token || !userData) {
        console.error('‚ùå [AuthContext] Formato incorrecto:', response.data);
        throw new Error('Respuesta del servidor incorrecta');
      }

      // Guardar token en localStorage
      localStorage.setItem('token', token);

      // Actualizar estado del usuario
      setUser(userData);

      console.log('‚úÖ [AuthContext] Login exitoso. Usuario guardado:', userData);
      console.log('‚úÖ [AuthContext] Rol del usuario:', userData.role);

      return { 
        success: true, 
        user: userData 
      };
    } catch (error) {
      console.error('‚ùå [AuthContext] Error en login:', error.response?.data || error.message);
      return { 
        success: false, 
        message: error.response?.data?.message || 'Error al iniciar sesi√≥n' 
      };
    }
  };

  // Funci√≥n de logout
  const logout = () => {
    console.log('üëã [AuthContext] Cerrando sesi√≥n...');
    localStorage.removeItem('token');
    setUser(null);
    window.location.href = '/';
  };

  // Verificar si est√° autenticado
  const isAuthenticated = () => {
    const authenticated = !!user;
    console.log('üîç [AuthContext] isAuthenticated:', authenticated, 'User:', user);
    return authenticated;
  };

  // Verificar si es administrador
  const isAdmin = () => {
    const admin = user?.role === 'admin';
    console.log('üîç [AuthContext] isAdmin:', admin, 'Role:', user?.role);
    return admin;
  };

  // Registro de usuario
  const register = async (userData) => {
    try {
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
      console.log('üìù [AuthContext] Registrando usuario...');

      const response = await axios.post(`${API_URL}/auth/register`, userData);

      console.log('‚úÖ [AuthContext] Registro exitoso:', response.data);

      const { token, user: newUser } = response.data;

      if (token && newUser) {
        localStorage.setItem('token', token);
        setUser(newUser);

        return { success: true, user: newUser };
      }

      return { success: true };
    } catch (error) {
      console.error('‚ùå [AuthContext] Error en registro:', error.response?.data || error.message);
      return { 
        success: false, 
        message: error.response?.data?.message || 'Error al registrarse' 
      };
    }
  };

  // Actualizar perfil
  const updateProfile = async (profileData) => {
    try {
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
      const token = localStorage.getItem('token');

      console.log('üîÑ [AuthContext] Actualizando perfil...');

      const response = await axios.put(`${API_URL}/auth/profile`, profileData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      console.log('‚úÖ [AuthContext] Perfil actualizado:', response.data);

      setUser(response.data);

      return { success: true, user: response.data };
    } catch (error) {
      console.error('‚ùå [AuthContext] Error al actualizar perfil:', error.response?.data || error.message);
      return { 
        success: false, 
        message: error.response?.data?.message || 'Error al actualizar perfil' 
      };
    }
  };

  // Cambiar contrase√±a
  const changePassword = async (currentPassword, newPassword) => {
    try {
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
      const token = localStorage.getItem('token');

      console.log('üîí [AuthContext] Cambiando contrase√±a...');

      await axios.post(`${API_URL}/auth/change-password`, 
        { currentPassword, newPassword },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log('‚úÖ [AuthContext] Contrase√±a cambiada');

      return { success: true, message: 'Contrase√±a actualizada correctamente' };
    } catch (error) {
      console.error('‚ùå [AuthContext] Error al cambiar contrase√±a:', error.response?.data || error.message);
      return { 
        success: false, 
        message: error.response?.data?.message || 'Error al cambiar contrase√±a' 
      };
    }
  };

  const value = {
    user,
    loading,
    login,
    logout,
    register,
    updateProfile,
    changePassword,
    isAuthenticated,
    isAdmin
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook personalizado para usar el contexto
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe usarse dentro de AuthProvider');
  }
  return context;
};

export default AuthContext;
