import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaTruck, FaBox, FaCheckCircle, FaMapMarkerAlt, FaClock } from 'react-icons/fa';
import '../styles/Tracking.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const statusIcons = {
  pendiente: <FaClock />,
  en_preparacion: <FaBox />,
  enviado: <FaTruck />,
  en_transito: <FaTruck />,
  en_reparto: <FaTruck />,
  entregado: <FaCheckCircle />,
  incidencia: <FaClock />
};

const statusColors = {
  pendiente: '#f39c12',
  en_preparacion: '#3498db',
  enviado: '#9b59b6',
  en_transito: '#9b59b6',
  en_reparto: '#e67e22',
  entregado: '#27ae60',
  incidencia: '#e74c3c'
};

const statusLabels = {
  pendiente: 'Pendiente',
  en_preparacion: 'En Preparación',
  enviado: 'Enviado',
  en_transito: 'En Tránsito',
  en_reparto: 'En Reparto',
  entregado: 'Entregado',
  incidencia: 'Incidencia'
};

const TrackingView = ({ orderId }) => {
  const [tracking, setTracking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadTracking();
  }, [orderId]);

  const loadTracking = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/tracking/order/${orderId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTracking(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'No hay información de envío disponible');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="tracking-loading">Cargando información de envío...</div>;
  }

  if (error) {
    return <div className="tracking-error">{error}</div>;
  }

  if (!tracking) {
    return <div className="tracking-info">No hay información de tracking disponible</div>;
  }

  return (
    <div className="tracking-container">
      <div className="tracking-header">
        <div className="tracking-number">
          <h3>Número de Seguimiento</h3>
          <p className="tracking-code">{tracking.trackingNumber}</p>
        </div>
        <div className="tracking-carrier">
          <strong>{tracking.carrier}</strong>
        </div>
      </div>

      <div className="tracking-status-current">
        <div className="status-icon" style={{ color: statusColors[tracking.currentStatus] }}>
          {statusIcons[tracking.currentStatus]}
        </div>
        <div className="status-info">
          <h2>{statusLabels[tracking.currentStatus]}</h2>
          {tracking.estimatedDelivery && tracking.currentStatus !== 'entregado' && (
            <p className="estimated-delivery">
              Entrega estimada: {new Date(tracking.estimatedDelivery).toLocaleDateString('es-ES', {
                weekday: 'long',
                year: 'numeric',
                month: 'long',
                day: 'numeric'
              })}
            </p>
          )}
        </div>
      </div>

      <div className="tracking-timeline">
        <h3>Historial de Envío</h3>
        <div className="timeline">
          {tracking.events.map((event, index) => (
            <div key={index} className="timeline-item">
              <div className="timeline-marker" style={{ borderColor: statusColors[event.status] }}>
                {statusIcons[event.status]}
              </div>
              <div className="timeline-content">
                <div className="timeline-header">
                  <strong>{statusLabels[event.status]}</strong>
                  <span className="timeline-date">
                    {new Date(event.timestamp).toLocaleString('es-ES')}
                  </span>
                </div>
                <p className="timeline-description">{event.description}</p>
                {event.location && (
                  <p className="timeline-location">
                    <FaMapMarkerAlt /> {event.location}
                  </p>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TrackingView;
