import React, { useState, useEffect, useRef } from 'react';
import { FaEye, FaEdit, FaPhone, FaTruck, FaFilePdf, FaSync, FaPrint, FaCheckCircle } from 'react-icons/fa';
import axios from 'axios';
import '../../styles/AdminOrders.css';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [statusUpdate, setStatusUpdate] = useState({
    orderStatus: '',
    paymentStatus: ''
  });
  const [error, setError] = useState(null);

  // Refs para controlar notificaciones y evitar bucles
  const notifiedOrders = useRef(new Set());
  const isFirstLoad = useRef(true);

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  // 1. CARGA Y REFRESCO AUTOM√ÅTICO (Sin bloquear la pantalla)
  useEffect(() => {
    loadOrders(true); // Carga inicial con spinner
    const interval = setInterval(() => loadOrders(false), 30000); // Refrescos silenciosos
    return () => clearInterval(interval);
  }, []);

  // 2. MONITOR DE IMPRESI√ìN (Solo para pedidos nuevos reales)
  useEffect(() => {
    const pendingToPrint = orders.find(o => 
      o.needsPrinting === true && 
      o.orderStatus === 'procesando' &&
      !notifiedOrders.current.has(o._id)
    );

    if (pendingToPrint) {
      notifiedOrders.current.add(pendingToPrint._id);
      // No avisar si es la primera vez que cargamos la p√°gina (evita spam de pedidos viejos)
      if (!isFirstLoad.current) {
        handleAutoPrintPrompt(pendingToPrint);
      }
    }
  }, [orders]);

  const loadOrders = async (initial = false) => {
    try {
      if (initial) setLoading(true);
      setRefreshing(true);
      setError(null);
      const token = localStorage.getItem('token');

      if (!token) {
        setError('No est√°s autenticado. Por favor, inicia sesi√≥n.');
        setLoading(false);
        return;
      }

      const response = await axios.get(`${API_URL}/orders`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const ordersData = Array.isArray(response.data) ? response.data : [];
      setOrders(ordersData);

      // Si es la carga inicial, marcamos los actuales como "ya vistos"
      if (initial) {
        ordersData.forEach(o => notifiedOrders.current.add(o._id));
        isFirstLoad.current = false;
      }
    } catch (error) {
      console.error('‚ùå Error al cargar pedidos:', error);
      if (initial) setError(error.response?.data?.message || 'Error al cargar los pedidos');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  // --- FUNCIONES DE ETIQUETA ---
  const handleAutoPrintPrompt = (order) => {
    const audio = new Audio('https://assets.mixkit.co/active_storage/sfx/2358/2358-preview.mp3');
    audio.play().catch(() => {});

    setTimeout(() => {
      if (window.confirm(`üì¶ ¬°PEDIDO NUEVO! #${order._id.slice(-6).toUpperCase()}\n¬øDeseas imprimir la etiqueta de preparaci√≥n ahora?`)) {
        const tId = order.tracking?._id || order.tracking;
        if (tId) {
          downloadLabel(tId);
          markAsPrinted(order._id);
        }
      }
    }, 1000);
  };

  const downloadLabel = (trackingId) => {
    const token = localStorage.getItem('token');
    // ‚≠ê CORRECCI√ìN: Enviamos token por URL para evitar el error de autenticaci√≥n en la pesta√±a nueva
    window.open(`${API_URL}/tracking/label/${trackingId}?token=${token}`, '_blank');
  };

  const markAsPrinted = async (orderId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/orders/${orderId}/printed`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // Actualizaci√≥n local r√°pida para no esperar al servidor
      setOrders(prev => prev.map(o => o._id === orderId ? { ...o, needsPrinting: false } : o));
    } catch (err) {
      console.error("Error al marcar como impreso", err);
    }
  };

  const handleCreateTracking = async (orderId) => {
    const carrier = prompt("Introduce el transportista:", "SimShip Express");
    if (!carrier) return;
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_URL}/tracking/create`, { orderId, carrier }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert("‚úÖ Tracking generado.");
      loadOrders(false);
    } catch (err) {
      alert("Error al generar tracking");
    }
  };

  // --- FORMATEO Y ESTILOS ---
  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-ES', { style: 'currency', currency: 'EUR' }).format(price);
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      pendiente: 'badge-warning', procesando: 'badge-info', enviado: 'badge-primary',
      entregado: 'badge-success', cancelado: 'badge-danger', pagado: 'badge-success', fallido: 'badge-danger'
    };
    return statusMap[status] || 'badge-secondary';
  };

  const getStatusLabel = (status) => {
    const labels = { pendiente: 'Pendiente', procesando: 'Procesando', enviado: 'Enviado', entregado: 'Entregado', cancelado: 'Cancelado', pagado: 'Pagado', fallido: 'Fallido' };
    return labels[status] || status;
  };

  // --- MANEJO DEL MODAL ---
  const openDetailModal = (order) => {
    setSelectedOrder(order);
    setStatusUpdate({ orderStatus: order.orderStatus, paymentStatus: order.paymentStatus });
    setShowDetailModal(true);
  };

  const closeDetailModal = () => {
    setShowDetailModal(false);
    setSelectedOrder(null);
  };

  const handleStatusChange = (e) => {
    setStatusUpdate({ ...statusUpdate, [e.target.name]: e.target.value });
  };

  const updateOrderStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/orders/${selectedOrder._id}/status`, statusUpdate, {
        headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' }
      });
      alert('Estado actualizado correctamente');
      closeDetailModal();
      loadOrders(false);
    } catch (error) {
      alert(error.response?.data?.message || 'Error al actualizar');
    }
  };

  if (loading) return <div className="admin-orders"><div className="loading-container"><div className="spinner-large"></div><p>Cargando pedidos...</p></div></div>;

  if (error) return <div className="admin-orders"><div className="error-container"><h3>Error</h3><p>{error}</p><button className="btn btn-primary" onClick={() => loadOrders(true)}>üîÑ Reintentar</button></div></div>;

  return (
    <div className="admin-orders">
      <div className="page-header">
        <h2>Gesti√≥n de Pedidos {refreshing && <small style={{fontSize: '12px', marginLeft: '10px'}}>üîÑ Actualizando...</small>}</h2>
        <div className="orders-stats">
          <span className="stat-item">Total: <strong>{orders.length}</strong></span>
          <span className="stat-item">Pendientes: <strong>{orders.filter(o => o.orderStatus === 'pendiente').length}</strong></span>
        </div>
        <button className="btn btn-secondary" onClick={() => loadOrders(true)}>üîÑ Actualizar</button>
      </div>

      <div className="table-responsive">
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID Pedido</th>
              <th>Cliente</th>
              <th>Total</th>
              <th>Estado Pago</th>
              <th>Estado Pedido</th>
              <th>Log√≠stica / Etiqueta</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id} className={order.needsPrinting ? 'row-urgent' : ''}>
                <td><strong>#{order._id.slice(-8)}</strong></td>
                <td>
                  <div className="customer-cell">
                    <strong>{order.user?.name || 'Usuario'}</strong>
                    <small><FaPhone /> {order.phone || 'S/T'}</small>
                  </div>
                </td>
                <td><strong>{formatPrice(order.totalAmount)}</strong></td>
                <td><span className={`badge ${getStatusBadge(order.paymentStatus)}`}>{getStatusLabel(order.paymentStatus)}</span></td>
                <td><span className={`badge ${getStatusBadge(order.orderStatus)}`}>{getStatusLabel(order.orderStatus)}</span></td>

                <td>
                  {!order.tracking ? (
                    <button className="btn-table btn-generate" onClick={() => handleCreateTracking(order._id)}>
                      <FaTruck /> Generar Env√≠o
                    </button>
                  ) : (
                    <button className="btn-table btn-pdf" onClick={() => downloadLabel(order.tracking._id || order.tracking)}>
                      <FaPrint /> Imprimir Etiqueta
                    </button>
                  )}
                </td>

                <td>
                  <div className="action-buttons">
                    <button className="btn-icon btn-view" onClick={() => openDetailModal(order)} title="Ver detalles">
                      <FaEye />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showDetailModal && selectedOrder && (
        <div className="modal-overlay" onClick={closeDetailModal}>
          <div className="modal-content large" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Detalle del Pedido #{selectedOrder._id.slice(-8)}</h3>
              <button className="modal-close" onClick={closeDetailModal}>√ó</button>
            </div>

            <div className="order-detail">
              <div className="detail-section label-highlight">
                <h4>üñ®Ô∏è Documentaci√≥n de Env√≠o</h4>
                <div className="label-status-container">
                  {selectedOrder.needsPrinting ? (
                    <p className="text-danger">‚ö†Ô∏è Etiqueta pendiente de impresi√≥n.</p>
                  ) : (
                    <p className="text-success"><FaCheckCircle /> Etiqueta procesada correctamente.</p>
                  )}
                  {selectedOrder.tracking && (
                    <button className="btn-print-modal" onClick={() => downloadLabel(selectedOrder.tracking._id || selectedOrder.tracking)}>
                      <FaFilePdf /> DESCARGAR ETIQUETA T√âRMICA
                    </button>
                  )}
                </div>
              </div>

              <div className="detail-section">
                <h4>üìç Direcci√≥n de Env√≠o</h4>
                <div className="address-box">
                  <p>
                    <strong>{selectedOrder.shippingAddress?.street}</strong><br />
                    {selectedOrder.shippingAddress?.city}, {selectedOrder.shippingAddress?.postalCode}<br />
                    {selectedOrder.shippingAddress?.country}
                  </p>
                </div>
              </div>

              <div className="detail-section">
                <h4>üì¶ Productos ({selectedOrder.items?.length || 0})</h4>
                <div className="order-items">
                  {selectedOrder.items?.map((item, index) => (
                    <div key={index} className="order-item">
                      <div className="item-details">
                        <strong>{item.product?.name || 'Producto'}</strong>
                        <span>Cantidad: {item.quantity}</span>
                      </div>
                      <strong>{formatPrice(item.price * item.quantity)}</strong>
                    </div>
                  ))}
                </div>
              </div>

              <div className="detail-section">
                <h4>‚öôÔ∏è Actualizar Estado</h4>
                <div className="status-update-form">
                  <select name="orderStatus" value={statusUpdate.orderStatus} onChange={handleStatusChange} className="form-select">
                    <option value="pendiente">Pendiente</option>
                    <option value="procesando">Procesando</option>
                    <option value="enviado">Enviado</option>
                    <option value="entregado">Entregado</option>
                    <option value="cancelado">Cancelado</option>
                  </select>
                  <button className="btn btn-primary" onClick={updateOrderStatus}><FaEdit /> Guardar Cambios</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminOrders;
