import React, { useState, useEffect } from 'react';
import { FaEye, FaEdit, FaPhone } from 'react-icons/fa';
import axios from 'axios';
import '../../styles/AdminOrders.css';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [statusUpdate, setStatusUpdate] = useState({
    orderStatus: '',
    paymentStatus: ''
  });
  const [error, setError] = useState(null);

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      setLoading(true);
      setError(null);

      // ‚úÖ OBTENER TOKEN DE AUTENTICACI√ìN
      const token = localStorage.getItem('token');

      if (!token) {
        setError('No est√°s autenticado. Por favor, inicia sesi√≥n.');
        setLoading(false);
        return;
      }

      console.log('üîÑ Cargando pedidos...');

      // ‚úÖ INCLUIR TOKEN EN LA PETICI√ìN
      const response = await axios.get(`${API_URL}/orders`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('‚úÖ Pedidos cargados:', response.data);

      // Asegurarse de que response.data sea un array
      const ordersData = Array.isArray(response.data) ? response.data : [];
      setOrders(ordersData);

    } catch (error) {
      console.error('‚ùå Error al cargar pedidos:', error);

      // Mensajes de error espec√≠ficos
      if (error.response?.status === 401) {
        setError('No autorizado. Por favor, inicia sesi√≥n como administrador.');
      } else if (error.response?.status === 403) {
        setError('No tienes permisos para ver los pedidos.');
      } else if (error.response?.status === 404) {
        setError('Ruta de pedidos no encontrada. Verifica la configuraci√≥n del servidor.');
      } else {
        setError(error.response?.data?.message || 'Error al cargar los pedidos');
      }
    } finally {
      setLoading(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-ES', {
      style: 'currency',
      currency: 'EUR'
    }).format(price);
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      pendiente: 'badge-warning',
      procesando: 'badge-info',
      enviado: 'badge-primary',
      entregado: 'badge-success',
      cancelado: 'badge-danger',
      pagado: 'badge-success',
      fallido: 'badge-danger'
    };
    return statusMap[status] || 'badge-secondary';
  };

  const getStatusLabel = (status) => {
    const labels = {
      pendiente: 'Pendiente',
      procesando: 'Procesando',
      enviado: 'Enviado',
      entregado: 'Entregado',
      cancelado: 'Cancelado',
      pagado: 'Pagado',
      fallido: 'Fallido'
    };
    return labels[status] || status;
  };

  const openDetailModal = (order) => {
    console.log('üìã Abriendo detalle de orden:', order);
    setSelectedOrder(order);
    setStatusUpdate({
      orderStatus: order.orderStatus,
      paymentStatus: order.paymentStatus
    });
    setShowDetailModal(true);
  };

  const closeDetailModal = () => {
    setShowDetailModal(false);
    setSelectedOrder(null);
  };

  const handleStatusChange = (e) => {
    setStatusUpdate({
      ...statusUpdate,
      [e.target.name]: e.target.value
    });
  };

  const updateOrderStatus = async () => {
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        alert('No est√°s autenticado');
        return;
      }

      console.log('üîÑ Actualizando estado del pedido:', selectedOrder._id);

      await axios.put(
        `${API_URL}/orders/${selectedOrder._id}/status`, 
        statusUpdate,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('‚úÖ Estado actualizado');
      alert('Estado del pedido actualizado correctamente');
      closeDetailModal();
      loadOrders();
    } catch (error) {
      console.error('‚ùå Error al actualizar estado:', error);
      alert(error.response?.data?.message || 'Error al actualizar el estado del pedido');
    }
  };

  // Estado de carga
  if (loading) {
    return (
      <div className="admin-orders">
        <div className="loading-container">
          <div className="spinner-large"></div>
          <p>Cargando pedidos...</p>
        </div>
      </div>
    );
  }

  // Estado de error
  if (error) {
    return (
      <div className="admin-orders">
        <div className="error-container">
          <div className="error-icon">‚ö†Ô∏è</div>
          <h3>Error al cargar pedidos</h3>
          <p>{error}</p>
          <button className="btn btn-primary" onClick={loadOrders}>
            üîÑ Reintentar
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-orders">
      <div className="page-header">
        <h2>Gesti√≥n de Pedidos</h2>
        <div className="orders-stats">
          <span className="stat-item">
            Total: <strong>{orders.length}</strong>
          </span>
          <span className="stat-item">
            Pendientes: <strong>{orders.filter(o => o.orderStatus === 'pendiente').length}</strong>
          </span>
          <span className="stat-item">
            Procesando: <strong>{orders.filter(o => o.orderStatus === 'procesando').length}</strong>
          </span>
          <span className="stat-item">
            Pagados: <strong>{orders.filter(o => o.paymentStatus === 'pagado').length}</strong>
          </span>
        </div>
        <button className="btn btn-secondary" onClick={loadOrders}>
          üîÑ Actualizar
        </button>
      </div>

      <div className="table-responsive">
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID Pedido</th>
              <th>Cliente</th>
              <th>Tel√©fono</th>
              <th>Fecha</th>
              <th>Total</th>
              <th>Estado Pago</th>
              <th>Estado Pedido</th>
              <th>M√©todo Pago</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id}>
                <td>
                  <strong>#{order._id.slice(-8)}</strong>
                </td>
                <td>
                  <div className="customer-cell">
                    <strong>{order.user?.name || 'Usuario'}</strong>
                    <small>{order.user?.email || 'Sin email'}</small>
                  </div>
                </td>
                <td>
                  <div className="phone-cell">
                    <FaPhone className="phone-icon" />
                    {order.phone || 'Sin tel√©fono'}
                  </div>
                </td>
                <td>{formatDate(order.createdAt)}</td>
                <td><strong>{formatPrice(order.totalAmount)}</strong></td>
                <td>
                  <span className={`badge ${getStatusBadge(order.paymentStatus)}`}>
                    {getStatusLabel(order.paymentStatus)}
                  </span>
                </td>
                <td>
                  <span className={`badge ${getStatusBadge(order.orderStatus)}`}>
                    {getStatusLabel(order.orderStatus)}
                  </span>
                </td>
                <td>
                  <span className="payment-method">
                    {order.paymentMethod === 'stripe' && 'üí≥'}
                    {order.paymentMethod === 'bizum' && 'üì±'}
                    {order.paymentMethod === 'transferencia' && 'üè¶'}
                    {order.paymentMethod === 'contrareembolso' && 'üíµ'}
                    {' '}
                    {order.paymentMethod}
                  </span>
                </td>
                <td>
                  <div className="action-buttons">
                    <button 
                      className="btn-icon btn-view"
                      onClick={() => openDetailModal(order)}
                      title="Ver detalles"
                    >
                      <FaEye />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {orders.length === 0 && (
          <div className="no-data">
            <p>üì¶ No hay pedidos registrados</p>
            <button className="btn btn-secondary" onClick={loadOrders}>
              üîÑ Recargar
            </button>
          </div>
        )}
      </div>

      {/* Modal de detalle del pedido */}
      {showDetailModal && selectedOrder && (
        <div className="modal-overlay" onClick={closeDetailModal}>
          <div className="modal-content large" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Detalle del Pedido #{selectedOrder._id.slice(-8)}</h3>
              <button className="modal-close" onClick={closeDetailModal}>√ó</button>
            </div>

            <div className="order-detail">
              {/* Informaci√≥n del cliente */}
              <div className="detail-section">
                <h4>üìã Informaci√≥n del Cliente</h4>
                <div className="info-grid">
                  <div>
                    <strong>Nombre:</strong> {selectedOrder.user?.name || 'No disponible'}
                  </div>
                  <div>
                    <strong>Email:</strong> {selectedOrder.user?.email || 'No disponible'}
                  </div>
                  <div>
                    <strong>Tel√©fono:</strong> 
                    <span className="phone-highlight">
                      <FaPhone /> {selectedOrder.phone || 'No proporcionado'}
                    </span>
                  </div>
                  <div>
                    <strong>Fecha:</strong> {formatDate(selectedOrder.createdAt)}
                  </div>
                  <div>
                    <strong>M√©todo de pago:</strong> {selectedOrder.paymentMethod}
                  </div>
                  {selectedOrder.stripePaymentIntentId && (
                    <div>
                      <strong>ID Stripe:</strong> 
                      <small>{selectedOrder.stripePaymentIntentId}</small>
                    </div>
                  )}
                </div>
              </div>

              {/* Direcci√≥n de env√≠o */}
              <div className="detail-section">
                <h4>üìç Direcci√≥n de Env√≠o</h4>
                <div className="address-box">
                  <p>
                    <strong>{selectedOrder.shippingAddress.street}</strong><br />
                    {selectedOrder.shippingAddress.city}, {selectedOrder.shippingAddress.postalCode}
                    {selectedOrder.shippingAddress.province && (
                      <><br />{selectedOrder.shippingAddress.province}</>
                    )}
                    <br />
                    {selectedOrder.shippingAddress.country}
                  </p>
                </div>
              </div>

              {/* Productos */}
              <div className="detail-section">
                <h4>üì¶ Productos ({selectedOrder.items.length})</h4>
                <div className="order-items">
                  {selectedOrder.items.map((item, index) => (
                    <div key={index} className="order-item">
                      <img 
                        src={item.product?.images?.[0] || 'https://via.placeholder.com/60'} 
                        alt={item.product?.name || 'Producto'}
                        onError={(e) => {
                          e.target.src = 'https://via.placeholder.com/60?text=Sin+Imagen';
                        }}
                      />
                      <div className="item-details">
                        <strong>{item.product?.name || 'Producto no disponible'}</strong>
                        <span>Cantidad: {item.quantity}</span>
                        <small>Precio unitario: {formatPrice(item.price)}</small>
                      </div>
                      <div className="item-price">
                        <strong>{formatPrice(item.price * item.quantity)}</strong>
                      </div>
                    </div>
                  ))}
                </div>

                <div className="order-total">
                  <strong>Total del Pedido:</strong> 
                  <span className="total-amount">{formatPrice(selectedOrder.totalAmount)}</span>
                </div>
              </div>

              {/* Notas */}
              {selectedOrder.notes && (
                <div className="detail-section">
                  <h4>üìù Notas del Cliente</h4>
                  <div className="notes-box">
                    <p>{selectedOrder.notes}</p>
                  </div>
                </div>
              )}

              {/* N√∫mero de seguimiento */}
              {selectedOrder.trackingNumber && (
                <div className="detail-section">
                  <h4>üöö Seguimiento</h4>
                  <div className="tracking-box">
                    <strong>N√∫mero de seguimiento:</strong> {selectedOrder.trackingNumber}
                  </div>
                </div>
              )}

              {/* Actualizar estados */}
              <div className="detail-section">
                <h4>‚öôÔ∏è Actualizar Estado del Pedido</h4>
                <div className="status-update-form">
                  <div className="form-group">
                    <label>Estado del Pedido</label>
                    <select
                      name="orderStatus"
                      value={statusUpdate.orderStatus}
                      onChange={handleStatusChange}
                      className="form-select"
                    >
                      <option value="pendiente">Pendiente</option>
                      <option value="procesando">Procesando</option>
                      <option value="enviado">Enviado</option>
                      <option value="entregado">Entregado</option>
                      <option value="cancelado">Cancelado</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label>Estado del Pago</label>
                    <select
                      name="paymentStatus"
                      value={statusUpdate.paymentStatus}
                      onChange={handleStatusChange}
                      className="form-select"
                    >
                      <option value="pendiente">Pendiente</option>
                      <option value="pagado">Pagado</option>
                      <option value="fallido">Fallido</option>
                    </select>
                  </div>

                  <button 
                    className="btn btn-primary btn-update"
                    onClick={updateOrderStatus}
                  >
                    <FaEdit /> Actualizar Estado
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminOrders;
