import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FaPaperPlane, FaComments, FaCheckCircle } from 'react-icons/fa';
import socketService from '../../services/socketService';
import '../../styles/AdminChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const AdminChat = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [connected, setConnected] = useState(false);
  const messagesEndRef = useRef(null);
  const initialized = useRef(false);

  useEffect(() => {
    if (initialized.current) {
      console.log('‚ö†Ô∏è AdminChat ya inicializado, saltando...');
      return;
    }

    console.log('üöÄ Inicializando AdminChat...');
    initialized.current = true;

    // Inicializar socket
    const socket = socketService.initialize();
    
    // Autenticar
    const token = localStorage.getItem('token');
    if (token) {
      socketService.authenticate(token);
    }

    // Configurar listeners
    setupSocketListeners();

    // Cargar chats
    loadChats();

    // NO CLEANUP - Dejamos el socket activo
    return () => {
      console.log('‚ö†Ô∏è AdminChat desmontado (socket permanece activo)');
    };
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [selectedChat?.messages]);

  const setupSocketListeners = () => {
    socketService.on('connect', () => {
      console.log('‚úÖ Socket conectado');
      setConnected(true);
    });

    socketService.on('disconnect', (reason) => {
      console.log('‚ùå Socket desconectado:', reason);
      setConnected(false);
    });

    socketService.on('authenticated', (data) => {
      console.log('‚úÖ Autenticado como:', data.user?.name);
      loadChats();
    });

    socketService.on('auth_error', (data) => {
      console.error('‚ùå Error autenticaci√≥n:', data.message);
      alert('Error de autenticaci√≥n: ' + data.message);
    });

    socketService.on('new_user_message', ({ chat }) => {
      console.log('üì® NUEVO MENSAJE DE USUARIO:', chat.user?.name);
      
      setChats(prevChats => {
        const exists = prevChats.find(c => c._id === chat._id);
        if (exists) {
          return prevChats.map(c => c._id === chat._id ? chat : c);
        }
        return [chat, ...prevChats];
      });

      if (selectedChat && selectedChat._id === chat._id) {
        setSelectedChat(chat);
      }

      // Notificaci√≥n de escritorio (opcional)
      if (Notification.permission === 'granted') {
        new Notification('Nuevo mensaje de ' + chat.user?.name, {
          body: chat.messages[chat.messages.length - 1]?.text.substring(0, 50),
          icon: '/logo192.png'
        });
      }
    });

    socketService.on('chat_updated', (chat) => {
      console.log('üîÑ Chat actualizado');
      setChats(prevChats => prevChats.map(c => c._id === chat._id ? chat : c));
      
      if (selectedChat && selectedChat._id === chat._id) {
        setSelectedChat(chat);
      }
    });

    socketService.on('message_sent', ({ success, chat }) => {
      if (success) {
        console.log('‚úÖ Mensaje enviado');
        setLoading(false);
        setMessage('');
        
        if (selectedChat && selectedChat._id === chat._id) {
          setSelectedChat(chat);
        }
      }
    });

    socketService.on('message_error', (data) => {
      console.error('‚ùå Error:', data.message);
      alert('Error: ' + data.message);
      setLoading(false);
    });
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadChats = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      console.log('üì• Cargando chats...');
      const response = await axios.get(`${API_URL}/chat/admin/chats?status=open`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      console.log(`‚úÖ ${response.data.length} chats cargados`);
      setChats(response.data);
    } catch (error) {
      console.error('‚ùå Error al cargar chats:', error);
      if (error.response?.status === 403) {
        alert('No tienes permisos de administrador');
      }
    }
  };

  const sendMessage = () => {
    if (!message.trim() || !selectedChat || loading) {
      return;
    }

    if (!socketService.isConnected()) {
      alert('Socket desconectado. Recargando...');
      window.location.reload();
      return;
    }

    console.log('üì§ Enviando mensaje:', message.substring(0, 30));
    setLoading(true);
    
    socketService.emit('admin_send_message', {
      chatId: selectedChat._id,
      text: message.trim()
    });
  };

  const closeChat = async () => {
    if (!selectedChat || !window.confirm('¬øFinalizar esta conversaci√≥n?')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `${API_URL}/chat/admin/close-chat/${selectedChat._id}`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert('Chat cerrado');
      setSelectedChat(null);
      loadChats();
    } catch (error) {
      console.error('Error:', error);
      alert('Error al cerrar chat');
    }
  };

  const formatTime = (date) => {
    return new Date(date).toLocaleTimeString('es-ES', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Pedir permisos de notificaci√≥n
  useEffect(() => {
    if (Notification.permission === 'default') {
      Notification.requestPermission();
    }
  }, []);

  return (
    <div className="admin-chat-container">
      <div className="chat-header-main">
        <div className="header-left">
          <h1>üí¨ Chat de Atenci√≥n al Cliente</h1>
          <p className="header-subtitle">
            {connected ? 'üü¢ Conectado' : 'üî¥ Desconectado'}
          </p>
        </div>
        <div className="header-stats">
          <div className="stat-badge">
            <span className="stat-number">{chats.length}</span>
            <span className="stat-label">Chats Abiertos</span>
          </div>
        </div>
      </div>

      <div className="chat-layout">
        <div className="chats-list">
          <div className="chats-list-header">
            <h3>Conversaciones</h3>
            <span className="badge-count">{chats.length}</span>
          </div>

          <div className="chats-list-items">
            {chats.length === 0 ? (
              <div className="no-chats">
                <FaComments size={48} style={{color:'#cbd5e1',marginBottom:'15px'}} />
                <p>No hay chats abiertos</p>
                <small>Los mensajes aparecer√°n autom√°ticamente</small>
              </div>
            ) : (
              chats.map(chat => {
                const lastMsg = chat.messages[chat.messages.length - 1];
                const unread = chat.messages.filter(m => m.sender === 'user' && !m.read).length;

                return (
                  <div
                    key={chat._id}
                    className={`chat-item ${selectedChat?._id === chat._id ? 'active' : ''} ${unread > 0 ? 'has-unread' : ''}`}
                    onClick={() => setSelectedChat(chat)}
                  >
                    <div className="chat-item-avatar">
                      üë§
                      {unread > 0 && <span className="unread-badge">{unread}</span>}
                    </div>
                    <div className="chat-item-content">
                      <div className="chat-item-header">
                        <span className="chat-item-name">{chat.user?.name || 'Usuario'}</span>
                        <span className="chat-item-time">{formatTime(lastMsg.timestamp)}</span>
                      </div>
                      <div className="chat-item-preview">
                        {lastMsg.sender === 'admin' && 'T√∫: '}
                        {lastMsg.text.substring(0, 40)}...
                      </div>
                    </div>
                  </div>
                );
              })
            )}
          </div>
        </div>

        <div className="chat-conversation">
          {selectedChat ? (
            <>
              <div className="conversation-header">
                <div className="conversation-user-info">
                  <div className="user-avatar-large">üë§</div>
                  <div className="user-details">
                    <h3>{selectedChat.user?.name || 'Usuario'}</h3>
                    <p className="user-email">{selectedChat.user?.email || ''}</p>
                  </div>
                </div>
                <button className="btn-close-chat" onClick={closeChat}>
                  <FaCheckCircle /> Finalizar
                </button>
              </div>

              <div className="messages-container">
                {selectedChat.messages.map((msg, idx) => (
                  <div key={idx} className={`message-bubble-wrapper ${msg.sender === 'admin' ? 'admin-message' : ''}`}>
                    <div className="message-avatar">
                      <div className={`avatar ${msg.sender === 'admin' ? 'admin-avatar' : 'user-avatar'}`}>
                        {msg.sender === 'admin' ? 'üë®‚Äçüíº' : 'üë§'}
                      </div>
                    </div>
                    <div className="message-content-wrapper">
                      <div className={`message-bubble ${msg.sender}`}>
                        <p className="message-text">{msg.text}</p>
                        <span className="message-time">{formatTime(msg.timestamp)}</span>
                      </div>
                    </div>
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>

              <div className="message-input-container">
                <input
                  type="text"
                  className="message-input"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && (e.preventDefault(), sendMessage())}
                  placeholder="Escribe tu respuesta..."
                  disabled={loading || !connected}
                />
                <button
                  className={`btn-send ${message.trim() ? 'active' : ''}`}
                  onClick={sendMessage}
                  disabled={loading || !message.trim() || !connected}
                >
                  <FaPaperPlane /> Enviar
                </button>
              </div>
            </>
          ) : (
            <div className="no-chat-selected">
              <FaComments size={64} style={{color:'#cbd5e1',marginBottom:'20px'}} />
              <h3>Selecciona una conversaci√≥n</h3>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminChat;
