import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../styles/AdminChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const AdminChat = () => {
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    loadChats();
    const interval = setInterval(loadChats, 3000);
    return () => clearInterval(interval);
  }, []);

  const loadChats = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        setError('No hay token de autenticaci√≥n');
        setLoading(false);
        return;
      }

      console.log('üì° Cargando chats...');
      
      const response = await axios.get(`${API_URL}/chat/admin/chats?status=open`, {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('‚úÖ Chats recibidos:', response.data);
      setChats(response.data || []);
      setError(null);
      
    } catch (err) {
      console.error('‚ùå Error:', err);
      setError(err.response?.data?.message || 'Error al cargar chats');
    } finally {
      setLoading(false);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!message.trim() || !selectedChat) return;

    try {
      const token = localStorage.getItem('token');
      
      await axios.post(
        `${API_URL}/chat/admin/send-message/${selectedChat._id}`,
        { text: message },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage('');
      loadChats();
    } catch (err) {
      alert('Error al enviar mensaje');
    }
  };

  const closeChat = async (chatId) => {
    if (!window.confirm('¬øCerrar este chat?')) return;

    try {
      const token = localStorage.getItem('token');
      
      await axios.put(
        `${API_URL}/chat/admin/close-chat/${chatId}`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSelectedChat(null);
      loadChats();
      alert('Chat cerrado');
    } catch (err) {
      alert('Error al cerrar chat');
    }
  };

  // ESTADOS DE CARGA Y ERROR
  if (loading && chats.length === 0) {
    return (
      <div style={{ padding: '40px', textAlign: 'center' }}>
        <h2>Cargando chats...</h2>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ padding: '40px', textAlign: 'center' }}>
        <h2 style={{ color: 'red' }}>‚ùå Error</h2>
        <p>{error}</p>
        <button onClick={loadChats} style={{ padding: '10px 20px', marginTop: '20px' }}>
          Reintentar
        </button>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <h1>üí¨ Chat de Atenci√≥n al Cliente</h1>
      
      <div style={{ display: 'grid', gridTemplateColumns: '300px 1fr', gap: '20px', marginTop: '20px' }}>
        
        {/* LISTA DE CHATS */}
        <div style={{ border: '1px solid #ddd', borderRadius: '8px', padding: '16px', background: 'white' }}>
          <h3>Conversaciones ({chats.length})</h3>
          
          {chats.length === 0 ? (
            <div style={{ padding: '40px 20px', textAlign: 'center', color: '#999' }}>
              <p>üì≠</p>
              <p>No hay chats activos</p>
            </div>
          ) : (
            <div>
              {chats.map(chat => (
                <div
                  key={chat._id}
                  onClick={() => setSelectedChat(chat)}
                  style={{
                    padding: '12px',
                    border: '1px solid #eee',
                    borderRadius: '6px',
                    marginBottom: '8px',
                    cursor: 'pointer',
                    background: selectedChat?._id === chat._id ? '#e3f2fd' : 'white'
                  }}
                >
                  <strong>{chat.user?.name || 'Usuario'}</strong>
                  <br />
                  <small style={{ color: '#666' }}>
                    {chat.messages[chat.messages.length - 1]?.text.substring(0, 40)}...
                  </small>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* CONVERSACI√ìN */}
        <div style={{ border: '1px solid #ddd', borderRadius: '8px', background: 'white', display: 'flex', flexDirection: 'column', height: '600px' }}>
          
          {selectedChat ? (
            <>
              {/* HEADER */}
              <div style={{ padding: '16px', borderBottom: '1px solid #ddd', display: 'flex', justifyContent: 'space-between' }}>
                <div>
                  <h3 style={{ margin: 0 }}>{selectedChat.user?.name || 'Usuario'}</h3>
                  <small>{selectedChat.user?.email}</small>
                </div>
                <button 
                  onClick={() => closeChat(selectedChat._id)}
                  style={{ padding: '8px 16px', background: '#ef4444', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer' }}
                >
                  Cerrar Chat
                </button>
              </div>

              {/* MENSAJES */}
              <div style={{ flex: 1, overflowY: 'auto', padding: '16px', background: '#f9fafb' }}>
                {selectedChat.messages.map((msg, idx) => (
                  <div
                    key={idx}
                    style={{
                      marginBottom: '12px',
                      display: 'flex',
                      justifyContent: msg.sender === 'admin' ? 'flex-end' : 'flex-start'
                    }}
                  >
                    <div
                      style={{
                        maxWidth: '70%',
                        padding: '10px 14px',
                        borderRadius: '12px',
                        background: msg.sender === 'admin' ? '#667eea' : 'white',
                        color: msg.sender === 'admin' ? 'white' : 'black',
                        boxShadow: '0 1px 2px rgba(0,0,0,0.1)'
                      }}
                    >
                      <strong style={{ fontSize: '12px', opacity: 0.8 }}>
                        {msg.sender === 'admin' ? 'T√∫' : selectedChat.user?.name}
                      </strong>
                      <p style={{ margin: '4px 0 0 0' }}>{msg.text}</p>
                      <small style={{ opacity: 0.7, fontSize: '11px' }}>
                        {new Date(msg.timestamp).toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' })}
                      </small>
                    </div>
                  </div>
                ))}
              </div>

              {/* INPUT */}
              <form onSubmit={sendMessage} style={{ padding: '16px', borderTop: '1px solid #ddd', display: 'flex', gap: '8px' }}>
                <input
                  type="text"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  placeholder="Escribe tu respuesta..."
                  style={{
                    flex: 1,
                    padding: '12px',
                    border: '1px solid #ddd',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                />
                <button
                  type="submit"
                  disabled={!message.trim()}
                  style={{
                    padding: '12px 24px',
                    background: message.trim() ? '#667eea' : '#ccc',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: message.trim() ? 'pointer' : 'not-allowed',
                    fontWeight: '600'
                  }}
                >
                  Enviar
                </button>
              </form>
            </>
          ) : (
            <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#999' }}>
