import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaSignOutAlt } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import ProfileTab from '../components/profile/ProfileTab';
import '../styles/Profile.css';

const Profile = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated, logout, login, loading } = useAuth();

  useEffect(() => {
    if (!loading && !isAuthenticated()) {
      navigate('/login');
    }
  }, [isAuthenticated, navigate, loading]);

  if (loading) return <div className="loading">Cargando tu perfil...</div>;
  if (!user) return null;

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-header">
          <div className="user-info">
            <h1>Mi Perfil</h1>
            <p className="user-email-subtitle">Gestiona tus datos personales</p>
          </div>
        </div>

        <div className="profile-content">
          {/* Solo cargamos el componente de datos personales */}
          <ProfileTab user={user} login={login} />
        </div>

        <button className="btn-logout-main" onClick={logout}>
          <FaSignOutAlt /> Cerrar Sesi√≥n
        </button>
      </div>
    </div>
  );
};

export default Profile;
