import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";
import { FaComments, FaTimes, FaPaperPlane } from "react-icons/fa";
import { useAuth } from "../context/AuthContext";



const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";
const SOCKET_URL = API_URL.replace("/api", "");

const socket = io(SOCKET_URL);

const ChatWidget = () => {
  const { isAuthenticated } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const messagesEndRef = useRef(null);

  const userId = JSON.parse(localStorage.getItem("user"))?._id;

  // Socket: autenticar y recibir mensajes
  useEffect(() => {
    if (!isAuthenticated() || !userId) return;

    const token = localStorage.getItem("token");
    socket.emit("auth", token);

    socket.on("chat_update_" + userId, (data) => {
      setMessages(data.messages);
    });

    return () => socket.off("chat_update_" + userId);
  }, [isOpen, isAuthenticated, userId]);

  // Scroll al final del chat
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const loadChat = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token || !userId) return;

      const res = await axios.get(`${API_URL}/chat/my-chat`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setMessages(res.data.messages);

      const unread = res.data.messages.filter(
        (m) => m.sender === "admin" && !m.read
      ).length;
      setUnreadCount(unread);

      if (isOpen && unread > 0) {
        await axios.put(
          `${API_URL}/chat/mark-read`,
          {},
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }
    } catch (error) {
      console.error("Error al cargar chat:", error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || loading) return;

    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      if (!token) return;

      await axios.post(
        `${API_URL}/chat/send-message`,
        { text: inputMessage },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setInputMessage("");
      await loadChat();
    } catch (error) {
      console.error("Error al enviar mensaje:", error);
      alert("Error al enviar mensaje");
    } finally {
      setLoading(false);
    }
  };

  const toggleChat = () => {
    if (!isAuthenticated()) {
      alert("Debes iniciar sesiÃ³n para usar el chat");
      return;
    }
    setIsOpen(!isOpen);
  };

  const formatTime = (date) =>
    new Date(date).toLocaleTimeString("es-ES", {
      hour: "2-digit",
      minute: "2-digit",
    });

  return (
    <div className="chat-widget">
      <button className="chat-button" onClick={toggleChat} aria-label="Chat">
        {isOpen ? <FaTimes /> : <FaComments />}
        {!isOpen && unreadCount > 0 && (
          <span className="chat-badge">{unreadCount}</span>
        )}
      </button>

      {isOpen && (
        <div className="chat-window">
          <div className="chat-header">
            <h3>ðŸ’¬ AtenciÃ³n al Cliente</h3>
            <button onClick={toggleChat} aria-label="Cerrar chat">
              <FaTimes />
            </button>
          </div>

          <div className="chat-messages">
            {messages.map((msg, i) => (
              <div
                key={i}
                className={`message ${
                  msg.sender === "user" ? "user-message" : "admin-message"
                }`}
              >
                <div className="message-content">
                  <p>{msg.text}</p>
                  <span className="message-time">
                    {formatTime(msg.timestamp)}
                  </span>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          <form onSubmit={sendMessage} className="chat-input-form">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              placeholder="Escribe tu mensaje..."
              disabled={loading}
            />
            <button type="submit" disabled={loading || !inputMessage.trim()}>
              <FaPaperPlane />
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default ChatWidget;
