import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUser, FaBox, FaBell, FaLock } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import '../styles/Profile.css';

import ProfileTab from '../components/profile/ProfileTab';
import OrdersTab from '../components/profile/OrdersTab';
import NotificationsTab from '../components/profile/NotificationsTab';
import SecurityTab from '../components/profile/SecurityTab';

const Profile = () => {
  const navigate = useNavigate();
  // Extraemos también "loading" (o como lo llames en tu Context)
  const { user, isAuthenticated, logout, login, loading } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');

  useEffect(() => {
    // Solo redirigir si el proceso de carga ha terminado y no hay usuario
    if (!loading && !isAuthenticated()) {
      navigate('/login');
    }
  }, [isAuthenticated, navigate, loading]);

  // 1. MEJORA: Si está cargando el contexto, mostramos un spinner
  if (loading) {
    return (
      <div className="profile-loading-container">
        <div className="spinner"></div>
        <p>Cargando tu perfil...</p>
      </div>
    );
  }

  // 2. MEJORA: Si no hay usuario todavía, no renderizamos el contenido
  if (!user) return null;

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-header">
          {/* Usamos el encadenamiento opcional para evitar errores de undefined */}
          <div className="user-info">
            <h1>Bienvenido, {user.name}</h1>

          </div>
        </div>



        <div className="profile-content">
          {/* Importante: Pasar login y user asegurándonos de que existen */}
          {activeTab === 'profile' && <ProfileTab user={user} login={login} />}
          {activeTab === 'orders' && <OrdersTab />}
          {activeTab === 'notifications' && <NotificationsTab />}
          {activeTab === 'security' && <SecurityTab />}
        </div>

        <button className="btn-logout-main" onClick={logout}>Cerrar Sesión</button>
      </div>
    </div>
  );
};

export default Profile;
