import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';
import '../../styles/AdminChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const SOCKET_URL = API_URL.replace('/api', '');

let socket = null;

const AdminChat = () => {
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState('');
  const [view, setView] = useState('open');
  const [connected, setConnected] = useState(false);
  const messagesEndRef = useRef(null);

  // ‚úÖ FUNCI√ìN PARA OBTENER TOKEN (sessionStorage O localStorage)
  const getToken = () => {
    return sessionStorage.getItem('token') || localStorage.getItem('token');
  };

  // ‚úÖ AUTO-SCROLL cuando cambian los mensajes
  useEffect(() => {
    scrollToBottom();
  }, [selectedChat?.messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // ‚úÖ INICIALIZAR SOCKET CON TOKEN CORRECTO
  useEffect(() => {
    const token = getToken();
    if (!token) {
      console.error('‚ùå No hay token disponible para admin');
      return;
    }

    if (!socket) {
      console.log('üîå Conectando socket admin...');
      
      socket = io(SOCKET_URL, { 
        transports: ['websocket', 'polling'],
        reconnection: true,
        reconnectionAttempts: 5,
        reconnectionDelay: 1000
      });

      socket.on('connect', () => {
        console.log('‚úÖ Socket admin conectado');
        setConnected(true);
        socket.emit('authenticate', token);
      });

      socket.on('disconnect', () => {
        console.log('‚ùå Socket admin desconectado');
        setConnected(false);
      });

      socket.on('authenticated', () => {
        console.log('‚úÖ Admin autenticado en socket');
      });

      // ‚úÖ ESCUCHAR MENSAJES DEL USUARIO EN TIEMPO REAL
      socket.on('new_user_message', (chat) => {
        console.log('üì® Nuevo mensaje del usuario recibido:', chat);
        updateChatState(chat);
      });

      socket.on('new_admin_message', (chat) => {
        console.log('üì§ Confirmaci√≥n de mensaje admin enviado:', chat);
        updateChatState(chat);
      });

      socket.on('chat_updated', (chat) => {
        console.log('üîÑ Chat actualizado:', chat);
        updateChatState(chat);
      });

      socket.on('chat_closed', (chat) => {
        console.log('üîí Chat cerrado:', chat);
        updateChatState(chat);
      });

      socket.on('message_sent', (data) => {
        console.log('‚úÖ Mensaje enviado correctamente:', data);
        if (data.success && data.chat) {
          updateChatState(data.chat);
        }
      });

      socket.on('message_error', (data) => {
        console.error('‚ùå Error al enviar mensaje:', data.message);
        alert('Error al enviar mensaje: ' + data.message);
      });
    }

    loadChats();

    return () => {
      // NO desconectar socket para mantener conexi√≥n
    };
  }, [view]);

  // ‚úÖ ACTUALIZAR ESTADO SIN RECARGAR
  const updateChatState = (updatedChat) => {
    console.log('üîÑ Actualizando estado del chat:', updatedChat._id);
    
    // Actualizar lista de chats
    setChats(prev => {
      const exists = prev.find(c => c._id === updatedChat._id);
      if (exists) {
        return prev.map(c => c._id === updatedChat._id ? updatedChat : c);
      } else {
        // Si es un chat nuevo y estamos en vista "open", agregarlo
        if (updatedChat.status === 'open' && view === 'open') {
          return [updatedChat, ...prev];
        }
        return prev;
      }
    });

    // Actualizar chat seleccionado si es el mismo
    setSelectedChat(prev => {
      if (prev?._id === updatedChat._id) {
        return updatedChat;
      }
      return prev;
    });
  };

  // ‚úÖ CARGAR CHATS CON TOKEN CORRECTO
  const loadChats = async () => {
    try {
      const token = getToken();
      if (!token) {
        setError('No hay sesi√≥n activa');
        return;
      }

      console.log('üì• Cargando chats...', view);

      const response = await axios.get(`${API_URL}/chat/admin/chats?status=${view}`, {
        headers: { 
          'Authorization': `Bearer ${token}`
        }
      });

      console.log('‚úÖ Chats cargados:', response.data.length);
      setChats(response.data || []);
      setError(null);
    } catch (err) {
      console.error('‚ùå Error al cargar chats:', err);
      setError('Error al conectar con el servidor');
      
      if (err.response?.status === 401) {
        alert('Sesi√≥n expirada. Por favor, inicia sesi√≥n nuevamente.');
        window.location.href = '/login';
      }
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ ENVIAR MENSAJE CON VALIDACI√ìN
  const sendMessage = async (e) => {
    e.preventDefault();
    
    if (!message.trim() || !selectedChat) {
      console.warn('‚ö†Ô∏è Mensaje vac√≠o o sin chat seleccionado');
      return;
    }

    if (!connected) {
      alert('No hay conexi√≥n con el servidor. Intentando reconectar...');
      return;
    }

    const token = getToken();
    if (!token) {
      alert('Sesi√≥n expirada. Por favor, inicia sesi√≥n nuevamente.');
      window.location.href = '/login';
      return;
    }

    console.log('üì§ Enviando mensaje admin:', {
      chatId: selectedChat._id,
      text: message.substring(0, 50) + '...'
    });

    // Enviar por socket
    socket.emit('admin_send_message', {
      chatId: selectedChat._id,
      text: message
    });

    setMessage('');
  };

  // ‚úÖ CERRAR CHAT CON TOKEN CORRECTO
  const closeChat = async (chatId) => {
    if (!window.confirm('¬øDeseas finalizar este chat?')) return;
    
    try {
      const token = getToken();
      if (!token) {
        alert('Sesi√≥n expirada. Por favor, inicia sesi√≥n nuevamente.');
        window.location.href = '/login';
        return;
      }

      console.log('üîí Cerrando chat:', chatId);

      await axios.put(
        `${API_URL}/chat/admin/close-chat/${chatId}`, 
        {}, 
        {
          headers: { 
            'Authorization': `Bearer ${token}`
          }
        }
      );

      console.log('‚úÖ Chat cerrado correctamente');
      setSelectedChat(null);
      loadChats();
    } catch (err) {
      console.error('‚ùå Error al cerrar chat:', err);
      alert('Error al cerrar la conversaci√≥n');
      
      if (err.response?.status === 401) {
        alert('Sesi√≥n expirada. Por favor, inicia sesi√≥n nuevamente.');
        window.location.href = '/login';
      }
    }
  };

  return (
    <div className="admin-chat-container" style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1>üí¨ Panel de Soporte</h1>
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '10px',
          padding: '8px 15px',
          background: connected ? '#d4edda' : '#f8d7da',
          borderRadius: '20px',
          fontSize: '14px'
        }}>
          <span style={{ 
            width: '10px', 
            height: '10px', 
            borderRadius: '50%', 
            background: connected ? '#28a745' : '#dc3545',
            display: 'inline-block'
          }}></span>
          {connected ? 'Conectado' : 'Desconectado'}
        </div>
      </div>

      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <button 
          onClick={() => { setView('open'); setSelectedChat(null); }}
          style={{
            padding: '10px 20px', 
            borderRadius: '20px', 
            border: 'none',
            background: view === 'open' ? '#667eea' : '#ddd',
            color: view === 'open' ? 'white' : '#666',
            cursor: 'pointer', 
            fontWeight: 'bold',
            transition: 'all 0.3s'
          }}
        >
          üî• Chats Activos ({chats.filter(c => c.status === 'open').length})
        </button>
        <button 
          onClick={() => { setView('closed'); setSelectedChat(null); }}
          style={{
            padding: '10px 20px', 
            borderRadius: '20px', 
            border: 'none',
            background: view === 'closed' ? '#2c3e50' : '#ddd',
            color: view === 'closed' ? 'white' : '#666',
            cursor: 'pointer', 
            fontWeight: 'bold',
            transition: 'all 0.3s'
          }}
        >
          üìÅ Historial
        </button>
      </div>

      {error && (
        <div style={{ 
          background: '#f8d7da', 
          color: '#721c24', 
          padding: '15px', 
          borderRadius: '8px', 
          marginBottom: '20px',
          border: '1px solid #f5c6cb'
        }}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      <div style={{ display: 'grid', gridTemplateColumns: '350px 1fr', gap: '20px' }}>
        {/* LISTA DE CHATS */}
        <div style={{ 
          background: 'white', 
          borderRadius: '8px', 
          border: '1px solid #ddd', 
          height: '70vh', 
          overflowY: 'auto',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
        }}>
          <div style={{ 
            padding: '15px', 
            borderBottom: '1px solid #eee', 
            background: '#fcfcfc',
            position: 'sticky',
            top: 0,
            zIndex: 10
          }}>
            <strong>
              {view === 'open' ? 'Conversaciones en curso' : 'Chats finalizados'} ({chats.length})
            </strong>
          </div>
          
          {loading ? (
            <div style={{ padding: '20px', textAlign: 'center', color: '#999' }}>
              Cargando...
            </div>
          ) : chats.length === 0 ? (
            <div style={{ padding: '20px', textAlign: 'center', color: '#999' }}>
              No hay chats {view === 'open' ? 'activos' : 'cerrados'}
            </div>
          ) : (
            chats.map(chat => (
              <div 
                key={chat._id}
                onClick={() => setSelectedChat(chat)}
                style={{
                  padding: '15px', 
                  borderBottom: '1px solid #f5f5f5', 
                  cursor: 'pointer',
                  background: selectedChat?._id === chat._id ? '#f0f4ff' : 'transparent',
                  borderLeft: selectedChat?._id === chat._id ? '4px solid #667eea' : 'none',
                  transition: 'all 0.2s'
                }}
              >
                <div style={{ fontWeight: 'bold', color: '#333', marginBottom: '5px' }}>
                  {chat.user?.name || 'Cliente'}
                </div>
                <div style={{ 
                  fontSize: '12px', 
                  color: '#666', 
                  whiteSpace: 'nowrap', 
                  overflow: 'hidden', 
                  textOverflow: 'ellipsis' 
                }}>
                  {chat.messages[chat.messages.length - 1]?.text || 'Sin mensajes'}
                </div>
                <div style={{ fontSize: '11px', color: '#999', marginTop: '5px' }}>
                  {new Date(chat.messages[chat.messages.length - 1]?.timestamp).toLocaleString('es-ES', {
                    day: '2-digit',
                    month: '2-digit',
                    hour: '2-digit',
                    minute: '2-digit'
                  })}
                </div>
              </div>
            ))
          )}
        </div>

        {/* VENTANA DE CHAT */}
        <div style={{ 
          background: 'white', 
          borderRadius: '8px', 
          border: '1px solid #ddd', 
          display: 'flex', 
          flexDirection: 'column', 
          height: '70vh',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
        }}>
          {selectedChat ? (
            <>
              {/* HEADER */}
              <div style={{ 
                padding: '15px', 
                borderBottom: '1px solid #eee', 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                background: '#fcfcfc'
              }}>
                <div>
                  <h3 style={{ margin: 0, color: '#333' }}>{selectedChat.user?.name}</h3>
                  <small style={{ color: '#888' }}>{selectedChat.user?.email}</small>
                </div>
                {view === 'open' && (
                  <button 
                    onClick={() => closeChat(selectedChat._id)} 
                    style={{ 
                      background: '#ff4757', 
                      color: 'white', 
                      border: 'none', 
                      padding: '8px 15px', 
                      borderRadius: '5px', 
                      cursor: 'pointer', 
                      fontWeight: '600',
                      transition: 'all 0.3s'
                    }}
                    onMouseOver={(e) => e.target.style.background = '#ff3838'}
                    onMouseOut={(e) => e.target.style.background = '#ff4757'}
                  >
                    ‚úì Finalizar Caso
                  </button>
                )}
              </div>
              
              {/* MENSAJES */}
              <div style={{ 
                flex: 1, 
                padding: '20px', 
                overflowY: 'auto', 
                background: '#f8f9fa' 
              }}>
                {selectedChat.messages.length === 0 ? (
                  <div style={{ 
                    textAlign: 'center', 
                    color: '#999', 
                    marginTop: '50px' 
                  }}>
                    No hay mensajes todav√≠a
                  </div>
                ) : (
                  selectedChat.messages.map((m, i) => (
                    <div 
                      key={i} 
                      style={{ 
                        display: 'flex', 
                        justifyContent: m.sender === 'admin' ? 'flex-end' : 'flex-start', 
                        marginBottom: '15px',
                        animation: 'fadeIn 0.3s'
                      }}
                    >
                      <div style={{ 
                        background: m.sender === 'admin' ? '#667eea' : 'white', 
                        color: m.sender === 'admin' ? 'white' : '#333',
                        padding: '10px 15px', 
                        borderRadius: '12px', 
                        maxWidth: '75%',
                        boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
                        border: m.sender === 'admin' ? 'none' : '1px solid #eee'
                      }}>
                        {m.sender === 'user' && (
                          <div style={{ 
                            fontSize: '11px', 
                            fontWeight: 'bold', 
                            marginBottom: '5px',
                            color: '#667eea'
                          }}>
                            {selectedChat.user?.name}
                          </div>
                        )}
                        <p style={{ margin: 0, wordWrap: 'break-word' }}>{m.text}</p>
                        <small style={{ 
                          display: 'block', 
                          marginTop: '5px', 
                          fontSize: '10px', 
                          opacity: 0.7 
                        }}>
                          {new Date(m.timestamp).toLocaleTimeString([], { 
                            hour: '2-digit', 
                            minute: '2-digit' 
                          })}
                        </small>
                      </div>
                    </div>
                  ))
                )}
                <div ref={messagesEndRef} />
              </div>

              {/* INPUT */}
              {view === 'open' && (
                <form 
                  onSubmit={sendMessage} 
                  style={{ 
                    padding: '15px', 
                    borderTop: '1px solid #eee', 
                    display: 'flex', 
                    gap: '10px',
                    background: 'white'
                  }}
                >
                  <input 
                    type="text" 
                    value={message} 
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Escribe tu respuesta..."
                    disabled={!connected}
                    style={{ 
                      flex: 1, 
                      padding: '12px', 
                      borderRadius: '8px', 
                      border: '1px solid #ddd', 
                      outline: 'none',
                      fontSize: '14px'
                    }}
                  />
                  <button 
                    type="submit" 
                    disabled={!connected || !message.trim()}
                    style={{ 
                      background: connected && message.trim() ? '#667eea' : '#ccc', 
                      color: 'white', 
                      border: 'none', 
                      padding: '10px 25px', 
                      borderRadius: '8px', 
                      cursor: connected && message.trim() ? 'pointer' : 'not-allowed', 
                      fontWeight: 'bold',
                      transition: 'all 0.3s'
                    }}
                  >
                    Enviar
                  </button>
                </form>
              )}
            </>
          ) : (
            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              alignItems: 'center', 
              justifyContent: 'center', 
              height: '100%', 
              color: '#aaa' 
            }}>
              <div style={{ fontSize: '48px', marginBottom: '20px' }}>üí¨</div>
              <p>Selecciona una conversaci√≥n para comenzar</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminChat;
