import React, { useState, useEffect } from 'react';
import { FaUser, FaEnvelope, FaPhone, FaMapMarkerAlt, FaEdit, FaCheck, FaTimes } from 'react-icons/fa';
import '../styles/Profile.css';

const ProfileTab = ({ user, login }) => {
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '', email: '', phone: '', street: '', city: '', postalCode: ''
  });

  useEffect(() => {
    if (user) {
      setFormData({
        name: user.name || '',
        email: user.email || '',
        phone: user.phone || '',
        street: user.address?.street || '',
        city: user.address?.city || '',
        postalCode: user.address?.postalCode || '',
      });
    }
  }, [user]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="profile-details-wrapper">
      <div className="profile-tab-header">
        <h3>Datos de contacto</h3>
        {!editing ? (
          <button className="btn-edit-mode" onClick={() => setEditing(true)}>
            <FaEdit /> Editar datos
          </button>
        ) : (
          <div className="edit-mode-actions">
            <button className="btn-save-data" onClick={() => setEditing(false)}>
              <FaCheck /> Guardar
            </button>
            <button className="btn-cancel-data" onClick={() => setEditing(false)}>
              <FaTimes />
            </button>
          </div>
        )}
      </div>

      <div className={`profile-form-grid ${editing ? 'is-editing' : ''}`}>
        {/* Nombre */}
        <div className="input-group-luxury">
          <label><FaUser /> Nombre Completo</label>
          <input 
            type="text" 
            name="name" 
            value={formData.name} 
            onChange={handleChange} 
            disabled={!editing} 
            placeholder="Tu nombre"
          />
        </div>

        {/* Email */}
        <div className="input-group-luxury">
          <label><FaEnvelope /> Email</label>
          <input 
            type="email" 
            name="email" 
            value={formData.email} 
            disabled 
            className="input-disabled"
          />
        </div>

        {/* Teléfono */}
        <div className="input-group-luxury">
          <label><FaPhone /> Teléfono móvil</label>
          <input 
            type="text" 
            name="phone" 
            value={formData.phone} 
            onChange={handleChange} 
            disabled={!editing} 
            placeholder="+34 000 000 000"
          />
        </div>

        {/* Ciudad */}
        <div className="input-group-luxury">
          <label><FaMapMarkerAlt /> Ciudad</label>
          <input 
            type="text" 
            name="city" 
            value={formData.city} 
            onChange={handleChange} 
            disabled={!editing} 
            placeholder="Ej: Madrid"
          />
        </div>
      </div>
    </div>
  );
};

export default ProfileTab;
