import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Auth.css';

const ForgotPassword = () => {
  const navigate = useNavigate();
  const { sendPasswordResetCode, resetPassword } = useAuth();

  // Estado para controlar si ya enviamos el código o no
  const [step, setStep] = useState(1); 
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  const [formData, setFormData] = useState({
    email: '',
    code: '',
    newPassword: '',
    confirmPassword: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // PASO 1: Solicitar el código
  const handleRequestCode = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await sendPasswordResetCode(formData.email);
    if (result.success) {
      setMessage('Código enviado. Revisa tu bandeja de entrada.');
      setStep(2); // Pasamos al formulario final
    } else {
      setError(result.message || 'No se pudo enviar el código.');
    }
    setLoading(false);
  };

  // PASO 2: Enviar todo junto (Email, Código y Nueva Clave)
  const handleResetFinal = async (e) => {
    e.preventDefault();
    setError('');

    if (formData.newPassword !== formData.confirmPassword) {
      return setError('Las contraseñas no coinciden.');
    }

    setLoading(true);
    const result = await resetPassword(formData.email, formData.code, formData.newPassword);
    
    if (result.success) {
      alert('Contraseña actualizada con éxito');
      navigate('/login');
    } else {
      setError(result.message || 'Código incorrecto o expirado.');
    }
    setLoading(false);
  };

  return (
    <div className="auth-page">
      <div className="auth-card">
        <h1>{step === 1 ? 'Recuperar Contraseña' : 'Establecer Nueva Contraseña'}</h1>
        
        {error && <div className="error-message">{error}</div>}
        {message && <div className="success-message">{message}</div>}

        {step === 1 ? (
          /* FORMULARIO PASO 1 */
          <form onSubmit={handleRequestCode}>
            <p>Introduce tu email para recibir el código de verificación.</p>
            <div className="form-group">
              <label>Email</label>
              <input 
                type="email" 
                name="email" 
                value={formData.email} 
                onChange={handleChange} 
                required 
              />
            </div>
            <button type="submit" className="submit-btn" disabled={loading}>
              {loading ? 'Enviando...' : 'Obtener Código'}
            </button>
          </form>
        ) : (
          /* FORMULARIO PASO 2: TODO JUNTO */
          <form onSubmit={handleResetFinal}>
            <p>Ingresa el código que enviamos a <strong>{formData.email}</strong></p>
            
            <div className="form-group">
              <label>Código de Verificación</label>
              <input 
                type="text" 
                name="code" 
                placeholder="6 dígitos"
                value={formData.code} 
                onChange={handleChange} 
                required 
              />
            </div>

            <div className="form-group">
              <label>Nueva Contraseña</label>
              <input 
                type="password" 
                name="newPassword" 
                value={formData.newPassword} 
                onChange={handleChange} 
                required 
              />
            </div>

            <div className="form-group">
              <label>Confirmar Contraseña</label>
              <input 
                type="password" 
                name="confirmPassword" 
                value={formData.confirmPassword} 
                onChange={handleChange} 
                required 
              />
            </div>

            <button type="submit" className="submit-btn" disabled={loading}>
              {loading ? 'Actualizando...' : 'Cambiar Contraseña'}
            </button>
            <button type="button" className="secondary-btn" onClick={() => setStep(1)}>
              Volver atrás
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default ForgotPassword;
