import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      console.log('üîë Token encontrado:', token ? 'S√≠' : 'No'); // ‚úÖ DEBUG

      if (token) {
        try {
          const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
          console.log('üì° Verificando autenticaci√≥n en:', `${API_URL}/auth/me`); // ‚úÖ DEBUG

          const response = await axios.get(`${API_URL}/auth/me`, {
            headers: { Authorization: `Bearer ${token}` }
          });

          console.log('‚úÖ Usuario verificado:', response.data); // ‚úÖ DEBUG
          setUser(response.data);
        } catch (error) {
          console.error('‚ùå Error verificando autenticaci√≥n:', error.response?.data || error.message);
          localStorage.removeItem('token');
          setUser(null);
        }
      }

      setLoading(false);
      console.log('üèÅ Carga completada. Usuario:', user); // ‚úÖ DEBUG
    };

    checkAuth();
  }, []);

  // src/context/AuthContext.js

const login = async (email, password) => {
  try {
    const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
    console.log('üîê Intentando login en:', `${API_URL}/auth/login`);

    const response = await axios.post(`${API_URL}/auth/login`, { email, password });

    console.log('üì• Respuesta del backend:', response.data);

    const { token, user: userData } = response.data;

    if (!token || !userData) {
      console.error('‚ùå Formato incorrecto:', response.data);
      throw new Error('Respuesta del servidor incorrecta');
    }

    localStorage.setItem('token', token);
    setUser(userData);

    console.log('‚úÖ Login exitoso. Usuario guardado:', userData);

    return { success: true, user: userData };
  } catch (error) {
    console.error('‚ùå Error en login:', error.response?.data || error.message);
    return { 
      success: false, 
      message: error.response?.data?.message || 'Error al iniciar sesi√≥n' 
    };
  }
};

// ‚úÖ A√ëADIR ESTA NUEVA FUNCI√ìN
const setUserSession = (user, token) => {
  localStorage.setItem('token', token);
  setUser(user);
  console.log('‚úÖ Sesi√≥n guardada manualmente:', user);
};

return (
  <AuthContext.Provider value={{ 
    user, 
    loading, 
    login, 
    logout,
    isAuthenticated,
    isAdmin,
    setUserSession // ‚úÖ EXPORTAR
  }}>
    {children}
  </AuthContext.Provider>
);

  const logout = () => {
    console.log('üëã Cerrando sesi√≥n...'); // ‚úÖ DEBUG
    localStorage.removeItem('token');
    setUser(null);
    window.location.href = '/';
  };

  const isAuthenticated = () => {
    const authenticated = !!user;
    console.log('üîç isAuthenticated:', authenticated, 'User:', user); // ‚úÖ DEBUG
    return authenticated;
  };

  const isAdmin = () => {
    const admin = user?.role === 'admin';
    console.log('üîç isAdmin:', admin, 'Role:', user?.role); // ‚úÖ DEBUG
    return admin;
  };

  return (
    <AuthContext.Provider value={{ 
      user, 
      loading, 
      login, 
      logout,
      isAuthenticated,
      isAdmin
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe usarse dentro de AuthProvider');
  }
  return context;
};
