import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaSignOutAlt, FaUserCircle, FaTrashAlt, FaExclamationTriangle } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import ProfileTab from '../components/profile/ProfileTab';
import axios from 'axios';
import '../styles/Profile.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Profile = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated, logout, login, loading } = useAuth();
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  useEffect(() => {
    if (!loading && !isAuthenticated()) navigate('/login');
  }, [isAuthenticated, navigate, loading]);

  const confirmDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${API_URL}/auth/profile`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      logout();
    } catch (err) {
      alert("Error al eliminar la cuenta");
    }
  };

  if (loading) return <div className="profile-loader"><span></span></div>;
  if (!user) return null;

  return (
    <div className="profile-luxury-wrapper">
      <div className="profile-hero-bg"></div>

      <div className="container profile-main-container">
        <div className="profile-glass-card">

          {/* HEADER PROFESIONAL */}
          <div className="profile-card-header">
            <div className="avatar-section">
              <FaUserCircle className="main-avatar-icon" />
              <div className="status-indicator"></div>
            </div>
            <div className="header-text-section">
              <h1>{user.name}</h1>
              <p className="user-badge">Cliente Verificado</p>
            </div>
          </div>

          {/* CONTENIDO PRINCIPAL */}
          <div className="profile-card-content">
            <div className="content-section">
              <h2 className="section-title">Información de la Cuenta</h2>
              <ProfileTab user={user} login={login} />
            </div>
          </div>

          {/* ACCIONES DE CUENTA */}
          <div className="profile-card-actions">
            <button className="btn-action-logout" onClick={logout}>
              <FaSignOutAlt /> Cerrar Sesión
            </button>

            <div className="divider-text">Seguridad avanzada</div>

            {!showDeleteConfirm ? (
              <button className="btn-action-delete-trigger" onClick={() => setShowDeleteConfirm(true)}>
                <FaTrashAlt /> Eliminar mi cuenta
              </button>
            ) : (
              <div className="delete-confirmation-box">
                <p><FaExclamationTriangle /> ¿Confirmas la eliminación permanente?</p>
                <div className="confirm-buttons">
                  <button className="btn-confirm-yes" onClick={confirmDelete}>Sí, eliminar</button>
                  <button className="btn-confirm-no" onClick={() => setShowDeleteConfirm(false)}>Cancelar</button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
