import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUser, FaBox, FaBell, FaLock, FaEdit, FaSave, FaTimes, FaTruck, FaExternalLinkAlt } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import '../styles/Profile.css';
import TrackingView from '../components/TrackingView';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Profile = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated, logout, login } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
    } else {
      setLoading(false);
    }
  }, [isAuthenticated, navigate]);

  if (loading) {
    return <div className="loading-screen"><div className="spinner"></div><p>Cargando tu cuenta...</p></div>;
  }

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-header">
          <div className="user-avatar">
            {user?.avatar ? (
              <img src={user.avatar} alt={user.name} />
            ) : (
              <div className="avatar-placeholder">
                {user?.name?.charAt(0).toUpperCase()}
              </div>
            )}
          </div>
          <div className="user-info">
            <h1>{user?.name}</h1>
            <p>{user?.email}</p>
            <span className={`role-badge ${user?.role}`}>{user?.role === 'admin' ? 'Administrador' : 'Cliente'}</span>
          </div>
        </div>

        <div className="profile-tabs">
          <button className={activeTab === 'profile' ? 'active' : ''} onClick={() => setActiveTab('profile')}><FaUser /> Mi Perfil</button>
          <button className={activeTab === 'orders' ? 'active' : ''} onClick={() => setActiveTab('orders')}><FaBox /> Mis Pedidos</button>
          <button className={activeTab === 'notifications' ? 'active' : ''} onClick={() => setActiveTab('notifications')}><FaBell /> Notificaciones</button>
          <button className={activeTab === 'security' ? 'active' : ''} onClick={() => setActiveTab('security')}><FaLock /> Seguridad</button>
        </div>

        <div className="profile-content">
          {activeTab === 'profile' && <ProfileTab user={user} login={login} />}
          {activeTab === 'orders' && <OrdersTab />}
          {activeTab === 'notifications' && <NotificationsTab />}
          {activeTab === 'security' && <SecurityTab />}
        </div>

        <button className="btn-logout-main" onClick={logout}>Cerrar Sesión</button>
      </div>
    </div>
  );
};

// --- TAB: Mis Pedidos (CON TRACKING LINK) ---
const OrdersTab = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/orders/my-orders`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(response.data);
    } catch (error) {
      console.error('Error al cargar pedidos:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-ES', { style: 'currency', currency: 'EUR' }).format(price);
  };

  if (loading) return <div className="tab-loading">Actualizando tus pedidos...</div>;

  if (orders.length === 0) {
    return (
      <div className="empty-state">
        <FaBox size={48} />
        <h3>Aún no has comprado nada</h3>
        <p>Tus pedidos aparecerán aquí en cuanto realices una compra.</p>
      </div>
    );
  }

  return (
    <div className="orders-tab">
      <h2>Historial de Pedidos</h2>
      <div className="orders-list">
        {orders.map(order => (
          <div key={order._id} className="order-card">
            <div className="order-header">
              <div className="order-meta">
                <span className="order-id">#{order._id.slice(-8).toUpperCase()}</span>
                <span className="order-date">{new Date(order.createdAt).toLocaleDateString()}</span>
              </div>
              <span className={`status-badge ${order.orderStatus}`}>{order.orderStatus}</span>
            </div>

            {/* SECCIÓN DE TRACKING REAL-TIME */}
            {order.tracking && (
              <div className="tracking-realtime-card">
                <div className="tracking-header">
                  <FaTruck /> <span>Seguimiento del Envío</span>
                </div>
                <div className="tracking-body">
                  <p><strong>Carrier:</strong> {order.tracking.carrier}</p>
                  <p><strong>Nº Tracking:</strong> <code>{order.tracking.trackingNumber}</code></p>

                  {order.tracking.trackingUrl && (
                    <a 
                      href={order.tracking.trackingUrl} 
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="tracking-external-btn"
                    >
                      LOCALIZAR EN TIEMPO REAL <FaExternalLinkAlt size={12} />
                    </a>
                  )}
                </div>
              </div>
            )}

            {/* Componente de vista de hitos internos (opcional) */}
            {order.orderStatus !== 'pendiente' && order.tracking && (
              <div className="tracking-inline-view">
                <TrackingView orderId={order._id} />
              </div>
            )}

            <div className="order-items">
              {order.items.map((item, index) => (
                <div key={index} className="order-item">
                  <img src={item.product?.images?.[0] || 'https://via.placeholder.com/50'} alt={item.product?.name} />
                  <div className="item-details">
                    <p className="item-name">{item.product?.name}</p>
                    <p className="item-qty">Cant: {item.quantity}</p>
                  </div>
                  <span className="item-price">{formatPrice(item.price * item.quantity)}</span>
                </div>
              ))}
            </div>

            <div className="order-total-footer">
              <span>Pagado con: <strong>{order.paymentMethod?.toUpperCase()}</strong></span>
              <span className="total-amount">Total: {formatPrice(order.totalAmount)}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// --- TAB: Mi Perfil ---
const ProfileTab = ({ user, login }) => {
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name || '',
    phone: user?.phone || '',
    street: user?.address?.street || '',
    city: user?.address?.city || '',
    postalCode: user?.address?.postalCode || '',
    country: user?.address?.country || 'España'
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSave = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.put(`${API_URL}/auth/profile`, {
        name: formData.name,
        phone: formData.phone,
        address: {
          street: formData.street,
          city: formData.city,
          postalCode: formData.postalCode,
          country: formData.country
        }
      }, { headers: { Authorization: `Bearer ${token}` } });

      login({ ...user, ...response.data }, token);
      setEditing(false);
      alert('Perfil actualizado con éxito');
    } catch (error) {
      alert('Hubo un error al guardar los cambios');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-tab">
      <div className="tab-header">
        <h2>Mis Datos</h2>
        {!editing ? (
          <button className="btn-edit" onClick={() => setEditing(true)}><FaEdit /> Editar Perfil</button>
        ) : (
          <div className="edit-actions">
            <button className="btn-save" onClick={handleSave} disabled={loading}>{loading ? 'Guardando...' : 'Guardar'}</button>
            <button className="btn-cancel" onClick={() => setEditing(false)}>Cancelar</button>
          </div>
        )}
      </div>
      <div className="profile-form">
        <div className="form-grid">
          <div className="form-group">
            <label>Nombre</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} disabled={!editing} />
          </div>
          <div className="form-group">
            <label>Teléfono</label>
            <input type="tel" name="phone" value={formData.phone} onChange={handleChange} disabled={!editing} />
          </div>
        </div>
        <h3>Dirección de Envío</h3>
        <div className="form-group">
          <label>Calle y Número</label>
          <input type="text" name="street" value={formData.street} onChange={handleChange} disabled={!editing} />
        </div>
        <div className="form-grid-3">
          <div className="form-group">
            <label>Ciudad</label>
            <input type="text" name="city" value={formData.city} onChange={handleChange} disabled={!editing} />
          </div>
          <div className="form-group">
            <label>C.P.</label>
            <input type="text" name="postalCode" value={formData.postalCode} onChange={handleChange} disabled={!editing} />
          </div>
          <div className="form-group">
            <label>País</label>
            <input type="text" name="country" value={formData.country} onChange={handleChange} disabled={!editing} />
          </div>
        </div>
      </div>
    </div>
  );
};

// --- TAB: Notificaciones ---
const NotificationsTab = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadNotifs = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${API_URL}/notifications`, { headers: { Authorization: `Bearer ${token}` } });
        setNotifications(res.data.notifications || []);
      } catch (err) { console.error(err); }
      finally { setLoading(false); }
    };
    loadNotifs();
  }, []);

  if (loading) return <p>Cargando avisos...</p>;

  return (
    <div className="notifications-tab">
      <h2>Notificaciones</h2>
      {notifications.length === 0 ? <p>No tienes mensajes nuevos.</p> : (
        <div className="notif-list">
          {notifications.map(n => (
            <div key={n._id} className={`notif-card ${n.read ? '' : 'unread'}`}>
              <h4>{n.title}</h4>
              <p>{n.message}</p>
              <small>{new Date(n.createdAt).toLocaleString()}</small>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// --- TAB: Seguridad ---
const SecurityTab = () => {
  const [passData, setPassData] = useState({ currentPassword: '', newPassword: '' });
  const [loading, setLoading] = useState(false);

  const handlePassChange = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_URL}/auth/change-password`, passData, { headers: { Authorization: `Bearer ${token}` } });
      alert('Contraseña cambiada correctamente');
      setPassData({ currentPassword: '', newPassword: '' });
    } catch (err) { alert('Error al cambiar la contraseña'); }
    finally { setLoading(false); }
  };

  return (
    <div className="security-tab">
      <h2>Seguridad de la Cuenta</h2>
      <form onSubmit={handlePassChange} className="password-form">
        <div className="form-group">
          <label>Contraseña Actual</label>
          <input type="password" required value={passData.currentPassword} onChange={e => setPassData({...passData, currentPassword: e.target.value})} />
        </div>
        <div className="form-group">
          <label>Nueva Contraseña</label>
          <input type="password" required value={passData.newPassword} onChange={e => setPassData({...passData, newPassword: e.target.value})} />
        </div>
        <button type="submit" className="btn-save" disabled={loading}>Actualizar Contraseña</button>
      </form>
    </div>
  );
};

export default Profile;
