import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaShoppingCart, FaUser, FaBars, FaTimes, FaBoxOpen, FaBell, FaCog, FaSignOutAlt } from 'react-icons/fa';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import NotificationBell from './NotificationBell';
import { getCategories } from '../services/api';
import '../styles/Header.css';

const Header = () => {
  const navigate = useNavigate();
  const { getCartCount } = useCart();
  const { user, logout, isAuthenticated } = useAuth();
  const [categories, setCategories] = useState([]);
  const [menuOpen, setMenuOpen] = useState(false);
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const userMenuRef = useRef(null);

  useEffect(() => {
    loadCategories();
  }, []);

  // Cerrar dropdown al hacer clic fuera
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (userMenuRef.current && !userMenuRef.current.contains(event.target)) {
        setUserMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const loadCategories = async () => {
    try {
      const response = await getCategories();
      setCategories(response.data);
    } catch (error) {
      console.error('Error al cargar categor√≠as:', error);
    }
  };

  const handleLogout = () => {
    logout();
    setUserMenuOpen(false);
    setMenuOpen(false);
    navigate('/');
  };

  const handleMenuItemClick = (path) => {
    navigate(path);
    setUserMenuOpen(false);
    setMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="header-top">
        <div className="container">
          <Link to="/" className="logo">
            <h1>SimShop</h1>
          </Link>

          <div className="search-bar">
            <input type="text" placeholder="Buscar productos..." />
            <button>Buscar</button>
          </div>

          <div className="header-actions">
            {isAuthenticated() ? (
              <>
                <NotificationBell />

                {/* Dropdown de Usuario */}
                <div className="user-dropdown" ref={userMenuRef}>
                  <button 
                    className="user-button"
                    onClick={() => setUserMenuOpen(!userMenuOpen)}
                  >
                    <FaUser />
                    <span>{user?.name}</span>
                  </button>

                  {userMenuOpen && (
                    <div className="user-dropdown-menu">
                      <div className="dropdown-header">
                        <strong>{user?.name}</strong>
                        <small>{user?.email}</small>
                      </div>

                      <div className="dropdown-items">
                        <button onClick={() => handleMenuItemClick('/perfil')}>
                          <FaUser />
                          <span>Mi Perfil</span>
                        </button>

                        <button onClick={() => handleMenuItemClick('/orders')}>
                          <FaBoxOpen />
                          <span>Mis Pedidos</span>
                        </button>

                        <button onClick={() => handleMenuItemClick('/notificacion')}>
                          <FaBell />
                          <span>Notificaciones</span>
                        </button>

                        <button onClick={() => handleMenuItemClick('/security')}>
                          <FaCog />
                          <span>Configuraci√≥n</span>
                        </button>

                        {user?.role === 'admin' && (
                          <button onClick={() => handleMenuItemClick('/admin')} className="admin-link">
                            üéõÔ∏è Panel Admin
                          </button>
                        )}

                        <div className="dropdown-divider"></div>

                        <button onClick={handleLogout} className="logout-btn-dropdown">
                          <FaSignOutAlt />
                          <span>Cerrar Sesi√≥n</span>
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <Link to="/login" className="login-link">
                <FaUser />
                <span>Iniciar Sesi√≥n</span>
              </Link>
            )}

            <Link to="/carrito" className="cart-link">
              <FaShoppingCart />
              {getCartCount() > 0 && (
                <span className="cart-badge">{getCartCount()}</span>
              )}
            </Link>
          </div>

          <button className="mobile-menu-btn" onClick={() => setMenuOpen(!menuOpen)}>
            {menuOpen ? <FaTimes /> : <FaBars />}
          </button>
        </div>
      </div>

      <nav className={`main-nav ${menuOpen ? 'open' : ''}`}>
        <div className="container">
          <ul>
            <li><Link to="/" onClick={() => setMenuOpen(false)}>Inicio</Link></li>
            <li><Link to="/productos" onClick={() => setMenuOpen(false)}>Todos los Productos</Link></li>
            {categories.slice(0, 6).map(category => (
              <li key={category._id}>
                <Link 
                  to={`/categoria/${category.slug}`}
                  onClick={() => setMenuOpen(false)}
                >
                  {category.name}
                </Link>
              </li>
            ))}
          </ul>
        </div>
      </nav>
    </header>
  );
};

export default Header;
